289‚Üí        let idsToExport = selectedIds
290‚Üí        let filesList = files
291‚Üí
292‚Üí        Task.detached(priority: .userInitiated) {
293‚Üí            let tempDir = URL(fileURLWithPath: NSTemporaryDirectory(), isDirectory: true)
294‚Üí            var urls: [URL] = []
