298‚Üí                guard let result = try? VaultStorage.shared.retrieveFile(id: id, with: key) else { continue }
299‚Üí                let file = filesList.first { $0.id == id }
300‚Üí                let filename = file?.filename ?? "Export_\(id.uuidString)"
301‚Üí                let url = tempDir.appendingPathComponent(filename)
302‚Üí                try? result.content.write(to: url, options: [.atomic])
