345‚Üí    // MARK: - Import: Camera Capture
346‚Üí
347‚Üí    func handleCapturedImage(_ imageData: Data) {
348‚Üí        guard !isSharedVault, let key = appState?.currentVaultKey else { return }
349‚Üí        let currentMasterKey = self.masterKey
350‚Üí        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
351‚Üí
352‚Üí        Task.detached(priority: .userInitiated) {
353‚Üí            do {
354‚Üí                let tempInputURL = FileManager.default.temporaryDirectory
355‚Üí                    .appendingPathComponent(UUID().uuidString)
356‚Üí                    .appendingPathExtension("jpg")
357‚Üí                try imageData.write(to: tempInputURL, options: [.atomic])
358‚Üí
359‚Üí                let (optimizedURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
360‚Üí                    fileURL: tempInputURL, mimeType: "image/jpeg", mode: optimizationMode
361‚Üí                )
362‚Üí                defer {
363‚Üí                    if wasOptimized { try? FileManager.default.removeItem(at: optimizedURL) }
364‚Üí                    try? FileManager.default.removeItem(at: tempInputURL)
365‚Üí                }
366‚Üí
367‚Üí                let ext = mimeType == "image/heic" ? "heic" : "jpg"
368‚Üí                let filename = "IMG_\(Date().timeIntervalSince1970).\(ext)"
369‚Üí                let thumbnail = FileUtilities.generateThumbnail(fromFileURL: optimizedURL)
370‚Üí
371‚Üí                let fileId = try VaultStorage.shared.storeFileFromURL(
372‚Üí                    optimizedURL, filename: filename, mimeType: mimeType,
373‚Üí                    with: key, thumbnailData: thumbnail
374‚Üí                )
375‚Üí                let encThumb = thumbnail.flatMap { try? CryptoEngine.encrypt($0, with: currentMasterKey?.rawBytes ?? key.rawBytes) }
376‚Üí                if let encThumb {
377‚Üí                    await ThumbnailCache.shared.storeEncrypted(id: fileId, data: encThumb)
378‚Üí                }
379‚Üí
380‚Üí                EmbraceManager.shared.trackFeatureUsage("camera_capture", context: [
381‚Üí                    "optimized": String(wasOptimized),
382‚Üí                    "mime_type": mimeType
383‚Üí                ])
384‚Üí
385‚Üí                await MainActor.run {
386‚Üí                    if let milestone = MilestoneTracker.shared.checkFirstFile(totalCount: self.files.count + 1) {
387‚Üí                        self.toastMessage = .milestone(milestone)
388‚Üí                    } else {
389‚Üí                        self.toastMessage = .fileEncrypted()
390‚Üí                    }
391‚Üí                    self.loadFiles()
392‚Üí                }
393‚Üí
394‚Üí                await ShareSyncManager.shared.scheduleSync(vaultKey: key)
395‚Üí            } catch {
396‚Üí                await MainActor.run {
397‚Üí                    self.toastMessage = .importFailed(1, imported: 0, reason: error.localizedDescription)
398‚Üí                }
399‚Üí            }
400‚Üí        }
401‚Üí    }
402‚Üí
403‚Üí    // MARK: - Import: Photo Picker
404‚Üí
405‚Üí    /// Returns limit info (remaining, selected) if the user hit the free plan cap, nil if import proceeds normally.
406‚Üí    func handleSelectedPhotos(_ results: [PHPickerResult]) -> (remaining: Int, selected: Int)? {
407‚Üí        vmLogger.info("üì∏ handleSelectedPhotos called with \(results.count) results")
408‚Üí        guard !isSharedVault, appState?.currentVaultKey != nil else {
409‚Üí            vmLogger.warning("‚ùå handleSelectedPhotos: guard failed - isSharedVault=\(self.isSharedVault), hasKey=\(self.appState?.currentVaultKey != nil)")
410‚Üí            return nil
411‚Üí        }
412‚Üí        guard let subscriptionManager else {
413‚Üí            vmLogger.warning("‚ùå handleSelectedPhotos: no subscriptionManager")
414‚Üí            return nil
415‚Üí        }
416‚Üí
417‚Üí        if !subscriptionManager.isPremium {
418‚Üí            let remaining = max(0, SubscriptionManager.maxFreeFilesPerVault - self.files.count)
419‚Üí            vmLogger.info("üí∞ Free tier check: remaining=\(remaining), files.count=\(self.files.count)")
420‚Üí            if remaining == 0 {
421‚Üí                return (0, results.count)
422‚Üí            }
423‚Üí            if results.count > remaining {
424‚Üí                pendingImport = .photos(results)
425‚Üí                return (remaining, results.count)
426‚Üí            }
427‚Üí        }
428‚Üí
429‚Üí        vmLogger.info("‚úÖ Proceeding with photo import of \(results.count) photos")
430‚Üí        performPhotoImport(results)
431‚Üí        return nil
432‚Üí    }
