434‚Üí    func performPhotoImport(_ results: [PHPickerResult]) {
435‚Üí        vmLogger.info("performPhotoImport START with \(results.count) photos")
436‚Üí        guard let key = appState?.currentVaultKey else {
437‚Üí            vmLogger.error("performPhotoImport: no vault key")
438‚Üí            return
439‚Üí        }
440‚Üí        let encryptionKey = self.masterKey?.rawBytes ?? key.rawBytes
441‚Üí        let count = results.count
442‚Üí        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
443‚Üí
444‚Üí        activeImportTask?.cancel()
445‚Üí        importGeneration += 1
446‚Üí        let myGeneration = importGeneration
447‚Üí        importProgress = (0, count)
448‚Üí        IdleTimerManager.shared.disable()
449‚Üí
450‚Üí        // Separate videos and images for prioritized parallel processing:
451‚Üí        // 1 dedicated video worker + 2 image workers = 3 parallel workers
452‚Üí        var videoWork: [ParallelImporter.PickerWorkItem] = []
453‚Üí        var imageWork: [ParallelImporter.PickerWorkItem] = []
454‚Üí
455‚Üí        for (index, result) in results.enumerated() {
456‚Üí            let isVideo = result.itemProvider.hasItemConformingToTypeIdentifier(UTType.movie.identifier)
457‚Üí            let item = ParallelImporter.PickerWorkItem(originalIndex: index, provider: result.itemProvider)
458‚Üí            if isVideo { videoWork.append(item) } else { imageWork.append(item) }
459‚Üí        }
460‚Üí
461‚Üí        let videoWorkerCount = videoWork.isEmpty ? 0 : 1
462‚Üí        let imageWorkerCount = videoWork.isEmpty ? 3 : 2
463‚Üí        vmLogger.info("Import: \(videoWork.count) videos, \(imageWork.count) images ‚Äî \(videoWorkerCount) video + \(imageWorkerCount) image workers")
464‚Üí
465‚Üí        let (stream, continuation) = AsyncStream<ParallelImporter.ImportEvent>.makeStream()
466‚Üí
467‚Üí        activeImportTask = Task(priority: .userInitiated) { [weak self] in
468‚Üí            guard let self else { return }
469‚Üí
470‚Üí            // Launch parallel workers off MainActor
471‚Üí            let workerTask = Task.detached(priority: .userInitiated) {
472‚Üí                await ParallelImporter.runPhotoImport(
473‚Üí                    videoWork: videoWork,
474‚Üí                    imageWork: imageWork,
475‚Üí                    videoWorkerCount: videoWorkerCount,
476‚Üí                    imageWorkerCount: imageWorkerCount,
477‚Üí                    key: key,
478‚Üí                    encryptionKey: encryptionKey,
479‚Üí                    optimizationMode: optimizationMode,
480‚Üí                    continuation: continuation
481‚Üí                )
482‚Üí                continuation.finish()
483‚Üí            }
484‚Üí
485‚Üí            // Consume events on MainActor for real-time UI updates
486‚Üí            var successCount = 0
487‚Üí            var failedCount = 0
488‚Üí            var lastErrorReason: String?
489‚Üí
490‚Üí            for await event in stream {
491‚Üí                guard !Task.isCancelled else { break }
492‚Üí
493‚Üí                switch event {
494‚Üí                case .imported(let file):
495‚Üí                    successCount += 1
496‚Üí                    self.files.append(file)
497‚Üí                case .failed(let reason):
498‚Üí                    failedCount += 1
499‚Üí                    if let reason { lastErrorReason = reason }
500‚Üí                }
501‚Üí                self.importProgress = (successCount + failedCount, count)
502‚Üí            }
503‚Üí
504‚Üí            // Cancel workers if we were cancelled
505‚Üí            if Task.isCancelled {
506‚Üí                workerTask.cancel()
507‚Üí                vmLogger.warning("Photo import cancelled (imported=\(successCount), failed=\(failedCount))")
508‚Üí            }
509‚Üí
510‚Üí            // Only clean up state if we're still the current import ‚Äî a newer import
511‚Üí            // may have already set its own importProgress and disabled IdleTimerManager
512‚Üí            guard self.importGeneration == myGeneration else { return }
513‚Üí
514‚Üí            self.importProgress = nil
515‚Üí            IdleTimerManager.shared.enable()
516‚Üí
517‚Üí            guard !Task.isCancelled else { return }
518‚Üí
519‚Üí            vmLogger.info("Import complete ‚Äî success: \(successCount), failed: \(failedCount), files.count: \(self.files.count)")
520‚Üí
521‚Üí            if failedCount > 0 && successCount == 0 {
522‚Üí                self.toastMessage = .importFailed(failedCount, imported: 0, reason: lastErrorReason)
523‚Üí            } else if failedCount > 0 {
524‚Üí                self.toastMessage = .importFailed(failedCount, imported: successCount, reason: lastErrorReason)
525‚Üí            } else if let milestone = MilestoneTracker.shared.checkFirstFile(totalCount: self.files.count) {
526‚Üí                self.toastMessage = .milestone(milestone)
527‚Üí            } else {
528‚Üí                self.toastMessage = .filesImported(successCount)
529‚Üí            }
530‚Üí
531‚Üí            if successCount > 0 {
532‚Üí                self.updateFilterAfterImport()
533‚Üí                ShareSyncManager.shared.scheduleSync(vaultKey: key)
534‚Üí            }
535‚Üí        }
536‚Üí    }
537‚Üí
538‚Üí    // MARK: - Import: File Importer
539‚Üí
540‚Üí    /// Returns limit info (remaining, selected) if the user hit the free plan cap, nil if import proceeds normally.
541‚Üí    func handleImportedFiles(_ result: Result<[URL], Error>) -> (remaining: Int, selected: Int)? {
542‚Üí        guard !isSharedVault, appState?.currentVaultKey != nil else { return nil }
543‚Üí        guard case .success(let urls) = result else { return nil }
544‚Üí        guard let subscriptionManager else { return nil }
545‚Üí
546‚Üí        if !subscriptionManager.isPremium {
547‚Üí            let remaining = max(0, SubscriptionManager.maxFreeFilesPerVault - files.count)
548‚Üí            if remaining == 0 {
549‚Üí                return (0, urls.count)
550‚Üí            }
551‚Üí            if urls.count > remaining {
552‚Üí                pendingImport = .files(urls)
553‚Üí                return (remaining, urls.count)
554‚Üí            }
555‚Üí        }
556‚Üí
557‚Üí        performFileImport(urls)
558‚Üí        return nil
559‚Üí    }
560‚Üí
561‚Üí    func performFileImport(_ urls: [URL]) {
562‚Üí        guard let key = appState?.currentVaultKey else { return }
563‚Üí        let encryptionKey = self.masterKey?.rawBytes ?? key.rawBytes
564‚Üí        let count = urls.count
565‚Üí        let showProgress = count > 1
566‚Üí        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
567‚Üí
568‚Üí        activeImportTask?.cancel()
569‚Üí        importGeneration += 1
570‚Üí        let myGeneration = importGeneration
571‚Üí        if showProgress { importProgress = (0, count) }
572‚Üí        IdleTimerManager.shared.disable()
573‚Üí
574‚Üí        // Separate videos and non-videos for prioritized parallel processing
575‚Üí        var videoWork: [ParallelImporter.URLWorkItem] = []
576‚Üí        var otherWork: [ParallelImporter.URLWorkItem] = []
577‚Üí
578‚Üí        for (index, url) in urls.enumerated() {
579‚Üí            let mime = FileUtilities.mimeType(forExtension: url.pathExtension)
580‚Üí            let item = ParallelImporter.URLWorkItem(originalIndex: index, url: url)
581‚Üí            if mime.hasPrefix("video/") { videoWork.append(item) } else { otherWork.append(item) }
582‚Üí        }
583‚Üí
584‚Üí        let videoWorkerCount = videoWork.isEmpty ? 0 : 1
585‚Üí        let otherWorkerCount = videoWork.isEmpty ? 3 : 2
586‚Üí        vmLogger.info("File import: \(videoWork.count) videos, \(otherWork.count) other ‚Äî \(videoWorkerCount + otherWorkerCount) workers")
587‚Üí
588‚Üí        let (stream, continuation) = AsyncStream<ParallelImporter.ImportEvent>.makeStream()
589‚Üí
590‚Üí        activeImportTask = Task(priority: .userInitiated) { [weak self] in
591‚Üí            guard let self else { return }
592‚Üí
593‚Üí            let workerTask = Task.detached(priority: .userInitiated) {
594‚Üí                await ParallelImporter.runFileImport(
595‚Üí                    videoWork: videoWork,
596‚Üí                    otherWork: otherWork,
597‚Üí                    videoWorkerCount: videoWorkerCount,
598‚Üí                    otherWorkerCount: otherWorkerCount,
599‚Üí                    key: key,
600‚Üí                    encryptionKey: encryptionKey,
601‚Üí                    optimizationMode: optimizationMode,
602‚Üí                    continuation: continuation
603‚Üí                )
604‚Üí                continuation.finish()
605‚Üí            }
606‚Üí
607‚Üí            var successCount = 0
608‚Üí            var failedCount = 0
609‚Üí            var lastErrorReason: String?
610‚Üí
611‚Üí            for await event in stream {
612‚Üí                guard !Task.isCancelled else { break }
613‚Üí
614‚Üí                switch event {
615‚Üí                case .imported(let file):
616‚Üí                    successCount += 1
617‚Üí                    self.files.append(file)
618‚Üí                case .failed(let reason):
619‚Üí                    failedCount += 1
620‚Üí                    if let reason { lastErrorReason = reason }
621‚Üí                }
622‚Üí                if showProgress {
623‚Üí                    self.importProgress = (successCount + failedCount, count)
624‚Üí                }
625‚Üí            }
626‚Üí
627‚Üí            if Task.isCancelled {
628‚Üí                workerTask.cancel()
629‚Üí                vmLogger.warning("File import cancelled (imported=\(successCount), failed=\(failedCount))")
630‚Üí            }
631‚Üí
632‚Üí            // Only clean up state if we're still the current import
633‚Üí            guard self.importGeneration == myGeneration else { return }
634‚Üí
635‚Üí            self.importProgress = nil
636‚Üí            IdleTimerManager.shared.enable()
637‚Üí
638‚Üí            guard !Task.isCancelled else { return }
639‚Üí
640‚Üí            if failedCount > 0 && successCount == 0 {
641‚Üí                self.toastMessage = .importFailed(failedCount, imported: 0, reason: lastErrorReason)
642‚Üí            } else if failedCount > 0 {
643‚Üí                self.toastMessage = .importFailed(failedCount, imported: successCount, reason: lastErrorReason)
644‚Üí            } else {
645‚Üí                self.toastMessage = .filesImported(successCount)
646‚Üí            }
647‚Üí
648‚Üí            vmLogger.info("File import complete: imported=\(successCount), failed=\(failedCount)")
649‚Üí            if successCount > 0 {
650‚Üí                self.updateFilterAfterImport()
651‚Üí                ShareSyncManager.shared.scheduleSync(vaultKey: key)
652‚Üí            }
653‚Üí        }
654‚Üí    }
655‚Üí
656‚Üí    // MARK: - Import: Pending Files
657‚Üí
658‚Üí    func importPendingFiles() {
659‚Üí        guard !isImportingPendingFiles else { return }
660‚Üí        guard let vaultKey = appState?.currentVaultKey else { return }
661‚Üí
662‚Üí        isImportingPendingFiles = true
663‚Üí        IdleTimerManager.shared.disable()
664‚Üí
665‚Üí        let fingerprint = KeyDerivation.keyFingerprint(from: vaultKey.rawBytes)
666‚Üí        let initialPendingCount = StagedImportManager.pendingImportableFileCount(for: fingerprint)
667‚Üí        if initialPendingCount > 0 {
668‚Üí            importProgress = (0, initialPendingCount)
669‚Üí        }
670‚Üí
671‚Üí        activeImportTask?.cancel()
672‚Üí        activeImportTask = Task.detached(priority: .userInitiated) {
673‚Üí            let result = await ImportIngestor.processPendingImports(for: vaultKey) { progress in
674‚Üí                await MainActor.run {
675‚Üí                    guard !Task.isCancelled else { return }
676‚Üí                    self.importProgress = (progress.completed, progress.total)
677‚Üí                }
678‚Üí            }
679‚Üí
680‚Üí            await MainActor.run {
681‚Üí                self.isImportingPendingFiles = false
682‚Üí                self.importProgress = nil
683‚Üí                IdleTimerManager.shared.enable()
684‚Üí            }
685‚Üí
686‚Üí            guard !Task.isCancelled else { return }
687‚Üí
688‚Üí            let remainingImportable = StagedImportManager.pendingImportableFileCount(for: fingerprint)
689‚Üí            await MainActor.run {
690‚Üí                guard let appState = self.appState else { return }
691‚Üí                appState.hasPendingImports = remainingImportable > 0
692‚Üí                appState.pendingImportCount = remainingImportable
693‚Üí
694‚Üí                if result.imported > 0 {
695‚Üí                    self.loadFiles()
696‚Üí                    let generator = UINotificationFeedbackGenerator()
697‚Üí                    generator.notificationOccurred(.success)
698‚Üí                    if result.failed > 0 {
699‚Üí                        self.toastMessage = .importFailed(result.failed, imported: result.imported, reason: result.failureReason)
700‚Üí                    } else {
701‚Üí                        self.toastMessage = .filesImported(result.imported)
702‚Üí                    }
703‚Üí                } else if result.failed > 0 {
704‚Üí                    self.toastMessage = .importFailed(result.failed, imported: 0, reason: result.failureReason)
705‚Üí                    let generator = UINotificationFeedbackGenerator()
706‚Üí                    generator.notificationOccurred(.error)
707‚Üí                } else {
708‚Üí                    appState.hasPendingImports = false
709‚Üí                    appState.pendingImportCount = 0
710‚Üí                }
711‚Üí            }
712‚Üí        }
713‚Üí    }
714‚Üí
715‚Üí    func proceedWithLimitedImport(limitAlertRemaining: Int) {
716‚Üí        guard let pending = pendingImport else { return }
717‚Üí        let remaining = limitAlertRemaining
718‚Üí        pendingImport = nil
719‚Üí
720‚Üí        switch pending {
721‚Üí        case .photos(let results):
722‚Üí            performPhotoImport(Array(results.prefix(remaining)))
723‚Üí        case .files(let urls):
724‚Üí            performFileImport(Array(urls.prefix(remaining)))
725‚Üí        }
726‚Üí    }
727‚Üí
728‚Üí    // MARK: - Shared Vault
729‚Üí
730‚Üí    func checkSharedVaultStatus() {
731‚Üí        guard let key = appState?.currentVaultKey else { return }
732‚Üí
733‚Üí        Task {
734‚Üí            do {
735‚Üí                var index = try VaultStorage.shared.loadIndex(with: key)
736‚Üí
737‚Üí                let shared = index.isSharedVault ?? false
738‚Üí                await MainActor.run {
739‚Üí                    isSharedVault = shared
740‚Üí                    sharePolicy = index.sharePolicy
741‚Üí                    sharedVaultId = index.sharedVaultId
742‚Üí                    sharedVaultOpenCount = index.openCount ?? 0
743‚Üí                }
744‚Üí
745‚Üí                guard shared else { return }
746‚Üí
747‚Üí                EmbraceManager.shared.trackFeatureUsage("shared_vault_opened", context: [
748‚Üí                    "vault_id": sharedVaultId ?? "unknown",
749‚Üí                    "has_expiration": String(index.sharePolicy?.expiresAt != nil),
750‚Üí                    "has_max_opens": String(index.sharePolicy?.maxOpens != nil)
751‚Üí                ])
752‚Üí
753‚Üí                if let expires = index.sharePolicy?.expiresAt, Date() > expires {
754‚Üí                    await MainActor.run {
755‚Üí                        selfDestructMessage = "This shared vault has expired. The vault owner set an expiration date of \(expires.formatted(date: .abbreviated, time: .omitted)). All shared files have been removed."
756‚Üí                        showSelfDestructAlert = true
757‚Üí                    }
758‚Üí                    return
759‚Üí                }
760‚Üí
761‚Üí                if !hasCountedOpenThisSession {
762‚Üí                    let currentOpens = (index.openCount ?? 0) + 1
763‚Üí                    if let maxOpens = index.sharePolicy?.maxOpens, currentOpens > maxOpens {
764‚Üí                        await MainActor.run {
765‚Üí                            selfDestructMessage = "This shared vault has reached its maximum number of opens. All shared files have been removed."
766‚Üí                            showSelfDestructAlert = true
767‚Üí                        }
768‚Üí                        return
769‚Üí                    }
770‚Üí
771‚Üí                    index.openCount = currentOpens
772‚Üí                    try VaultStorage.shared.saveIndex(index, with: key)
773‚Üí                    await MainActor.run {
774‚Üí                        hasCountedOpenThisSession = true
775‚Üí                        sharedVaultOpenCount = currentOpens
776‚Üí                    }
777‚Üí                }
778‚Üí
779‚Üí                if let vaultId = index.sharedVaultId {
780‚Üí                    do {
781‚Üí                        let currentVersion = index.sharedVaultVersion ?? 1
782‚Üí                        if let _ = try await CloudKitSharingManager.shared.checkForUpdates(
783‚Üí                            shareVaultId: vaultId, currentVersion: currentVersion
784‚Üí                        ) {
785‚Üí                            await MainActor.run {
786‚Üí                                updateAvailable = true
787‚Üí                            }
788‚Üí                        }
789‚Üí                    } catch CloudKitSharingError.revoked {
790‚Üí                        await MainActor.run {
791‚Üí                            selfDestructMessage = "The vault owner has revoked your access to this shared vault. All shared files have been removed."
792‚Üí                            showSelfDestructAlert = true
793‚Üí                        }
794‚Üí                    } catch {
795‚Üí                        #if DEBUG
796‚Üí                        print("‚ö†Ô∏è [VaultViewModel] Failed to check for updates: \(error)")
797‚Üí                        #endif
798‚Üí                    }
799‚Üí                }
800‚Üí            } catch {
801‚Üí                #if DEBUG
802‚Üí                print("‚ùå [VaultViewModel] Failed to check shared vault status: \(error)")
803‚Üí                #endif
804‚Üí            }
805‚Üí        }
806‚Üí    }
807‚Üí
808‚Üí    func selfDestruct() {
809‚Üí        guard let key = appState?.currentVaultKey else { return }
810‚Üí
811‚Üí        do {
812‚Üí            let index = try VaultStorage.shared.loadIndex(with: key)
813‚Üí
814‚Üí            if let vaultId = index.sharedVaultId {
815‚Üí                Task {
816‚Üí                    do {
817‚Üí                        try await CloudKitSharingManager.shared.markShareConsumed(shareVaultId: vaultId)
818‚Üí                    } catch {
819‚Üí                        vmLogger.error("Failed to mark share consumed during self-destruct: \(error.localizedDescription, privacy: .public)")
820‚Üí                        EmbraceManager.shared.captureError(error, context: ["action": "markShareConsumed", "vaultId": vaultId])
821‚Üí                    }
822‚Üí                }
823‚Üí            }
824‚Üí
825‚Üí            for file in index.files where !file.isDeleted {
826‚Üí                do {
827‚Üí                    try VaultStorage.shared.deleteFile(id: file.fileId, with: key)
828‚Üí                } catch {
829‚Üí                    vmLogger.error("Failed to delete file during self-destruct \(file.fileId, privacy: .public): \(error.localizedDescription, privacy: .public)")
830‚Üí                    EmbraceManager.shared.captureError(error, context: ["action": "deleteFile", "fileId": file.fileId])
831‚Üí                }
832‚Üí            }
833‚Üí            try VaultStorage.shared.deleteVaultIndex(for: key)
834‚Üí        } catch {
835‚Üí            vmLogger.error("Self-destruct error: \(error.localizedDescription, privacy: .public)")
836‚Üí            EmbraceManager.shared.captureError(error, context: ["action": "selfDestruct"])
837‚Üí        }
838‚Üí
839‚Üí        appState?.lockVault()
840‚Üí    }
841‚Üí
842‚Üí    // MARK: - Vault Key Change
843‚Üí
844‚Üí    func handleVaultKeyChange(oldKey: VaultKey?, newKey: VaultKey?) {
845‚Üí        if activeImportTask != nil {
846‚Üí            vmLogger.warning("Vault key changed during active import ‚Äî cancelling import (files.count=\(self.files.count))")
847‚Üí        }
848‚Üí        activeImportTask?.cancel()
849‚Üí        activeImportTask = nil
850‚Üí        importProgress = nil
851‚Üí        isDeleteInProgress = false
852‚Üí        IdleTimerManager.shared.enable()
853‚Üí
854‚Üí        if oldKey != newKey {
855‚Üí            vmLogger.info("Vault key changed: clearing files (had \(self.files.count) files)")
856‚Üí            files = []
857‚Üí            masterKey = nil
858‚Üí            Task { await ThumbnailCache.shared.clear() }
859‚Üí            isLoading = false
860‚Üí            isSharedVault = false
861‚Üí        }
862‚Üí
863‚Üí        if newKey != nil {
864‚Üí            ShareUploadManager.shared.resumePendingUploadsIfNeeded(trigger: "vault_key_changed")
865‚Üí        }
866‚Üí    }
867‚Üí
868‚Üí    // MARK: - Filter Helpers
869‚Üí
870‚Üí    func updateFilterAfterImport() {
871‚Üí        let hasNonMedia = files.contains { !$0.isMedia }
872‚Üí        if hasNonMedia && fileFilter == .media {
873‚Üí            setFileFilter(.all)
874‚Üí        } else if !hasNonMedia && fileFilter != .media {
875‚Üí            setFileFilter(.media)
876‚Üí        }
877‚Üí    }
878‚Üí}
