                }
            }

            _ = try VaultStorage.shared.storeFile(
                data: decrypted,
                filename: file.filename,
                mimeType: file.mimeType,
                with: vaultKey,
                thumbnailData: thumbnailData,
                duration: file.duration,
                fileId: file.id  // <- Preserve original file ID from shared vault
            )
        }
    }
}

