  5‚Üí// MARK: - Shared Vault
  6‚Üí
  7‚Üíextension VaultView {
  8‚Üí
  9‚Üí    // MARK: - Shared Vault Banner
 10‚Üí
 11‚Üí    var sharedVaultBannerView: some View {
 12‚Üí        VStack(spacing: 0) {
 13‚Üí            HStack(spacing: 8) {
 14‚Üí                Image(systemName: "person.2.fill")
 15‚Üí                    .font(.caption)
 16‚Üí                    .accessibilityHidden(true)
 17‚Üí
 18‚Üí                Text("Shared Vault")
 19‚Üí                    .font(.caption).fontWeight(.medium)
 20‚Üí
 21‚Üí                Spacer()
 22‚Üí            }
 23‚Üí            .padding(.horizontal)
