 25‚Üí
 26‚Üí            if sharedVaultHasPolicyDetails {
 27‚Üí                sharedVaultPolicyDetailsView
 28‚Üí            }
 29‚Üí
 30‚Üí            if viewModel.updateAvailable {
 31‚Üí                HStack {
 32‚Üí                    Image(systemName: "arrow.down.circle.fill")
 33‚Üí                        .foregroundStyle(.tint)
 34‚Üí                    Text("New files available")
 35‚Üí                        .font(.caption)
 36‚Üí                    Spacer()
 37‚Üí                    Button(action: { Task { await downloadUpdate() } }) {
 38‚Üí                        if viewModel.isUpdating {
 39‚Üí                            ProgressView().scaleEffect(0.7)
 40‚Üí                        } else {
 41‚Üí                            Text("Update Now")
 42‚Üí                                .font(.caption).fontWeight(.medium)
 43‚Üí                        }
 44‚Üí                    }
 45‚Üí                    .vaultProminentButtonStyle()
 46‚Üí                    .controlSize(.mini)
 47‚Üí                    .disabled(viewModel.isUpdating)
 48‚Üí                    .accessibilityLabel(viewModel.isUpdating ? "Updating shared vault" : "Update shared vault")
 49‚Üí                }
 50‚Üí                .padding(.horizontal)
 51‚Üí                .padding(.vertical, 4)
 52‚Üí                .background(Color.accentColor.opacity(0.1))
 53‚Üí            }
 54‚Üí        }
 55‚Üí        .vaultBannerBackground()
 56‚Üí        .accessibilityElement(children: .combine)
 57‚Üí    }
 58‚Üí
 59‚Üí    private var sharedVaultHasPolicyDetails: Bool {
 60‚Üí        let policy = viewModel.sharePolicy
 61‚Üí        return policy?.expiresAt != nil
 62‚Üí            || policy?.maxOpens != nil
 63‚Üí            || policy?.allowDownloads == false
 64‚Üí    }
 65‚Üí
 66‚Üí    private var sharedVaultPolicyDetailsView: some View {
 67‚Üí        HStack(spacing: 12) {
 68‚Üí            if let maxOpens = viewModel.sharePolicy?.maxOpens {
 69‚Üí                let remaining = max(maxOpens - viewModel.sharedVaultOpenCount, 0)
 70‚Üí                Label("\(remaining) of \(maxOpens) opens left", systemImage: "lock.open.display")
 71‚Üí                    .accessibilityIdentifier("shared_vault_opens_left")
 72‚Üí            }
 73‚Üí
 74‚Üí            if viewModel.sharePolicy?.allowDownloads == false {
 75‚Üí                Label("Exports disabled", systemImage: "square.and.arrow.up.trianglebadge.exclamationmark")
 76‚Üí                    .accessibilityIdentifier("shared_vault_exports_disabled")
 77‚Üí            }
 78‚Üí
 79‚Üí            if let expires = viewModel.sharePolicy?.expiresAt {
 80‚Üí                Label {
 81‚Üí                    Text("Expires \(expires, style: .date)")
 82‚Üí                } icon: {
 83‚Üí                    Image(systemName: "calendar")
 84‚Üí                }
 85‚Üí                .accessibilityIdentifier("shared_vault_expires")
 86‚Üí            }
 87‚Üí        }
 88‚Üí        .font(.caption2)
 89‚Üí        .foregroundStyle(.vaultSecondaryText)
 90‚Üí        .frame(maxWidth: .infinity, alignment: .leading)
 91‚Üí        .padding(.horizontal)
 92‚Üí        .padding(.bottom, 6)
 93‚Üí    }
 94‚Üí
 95‚Üí    // MARK: - Download Update
 96‚Üí
 97‚Üí    func downloadUpdate() async {
 98‚Üí        guard let key = appState.currentVaultKey,
 99‚Üí              let vaultId = viewModel.sharedVaultId else { return }
100‚Üí
101‚Üí        viewModel.isUpdating = true
102‚Üí        viewModel.sharedVaultUpdateProgress = (completed: 0, total: 100, message: "Downloading update...")
103‚Üí        IdleTimerManager.shared.disable()
104‚Üí
105‚Üí        var bgTaskCleaned = false
106‚Üí        var bgTaskId: UIBackgroundTaskIdentifier = .invalid
107‚Üí        bgTaskId = UIApplication.shared.beginBackgroundTask(withName: "SharedVaultUpdate") {
108‚Üí            // Expiration handler ‚Äî clean up progress state so the overlay disappears
109‚Üí            bgTaskCleaned = true
110‚Üí            Task { @MainActor [weak viewModel] in
111‚Üí                viewModel?.sharedVaultUpdateProgress = nil
112‚Üí                viewModel?.isUpdating = false
113‚Üí                IdleTimerManager.shared.enable()
114‚Üí            }
115‚Üí            UIApplication.shared.endBackgroundTask(bgTaskId)
116‚Üí            bgTaskId = .invalid
117‚Üí        }
118‚Üí
119‚Üí        defer {
120‚Üí            viewModel.isUpdating = false
121‚Üí            viewModel.sharedVaultUpdateProgress = nil
122‚Üí            IdleTimerManager.shared.enable()
123‚Üí            if !bgTaskCleaned && bgTaskId != .invalid {
124‚Üí                UIApplication.shared.endBackgroundTask(bgTaskId)
125‚Üí            }
126‚Üí        }
127‚Üí
128‚Üí        do {
129‚Üí            let index = try VaultStorage.shared.loadIndex(with: key)
130‚Üí
131‚Üí            // Use the stored phrase-derived share key
132‚Üí            guard let shareKeyData = index.shareKeyData else {
133‚Üí                #if DEBUG
134‚Üí                print("‚ùå [VaultView] No share key stored in vault index")
135‚Üí                #endif
136‚Üí                return
137‚Üí            }
138‚Üí
139‚Üí            let shareKey = ShareKey(shareKeyData)
140‚Üí            let data = try await CloudKitSharingManager.shared.downloadUpdatedVault(
141‚Üí                shareVaultId: vaultId,
142‚Üí                shareKey: shareKey,
143‚Üí                onProgress: { [weak viewModel] completed, total in
144‚Üí                    Task { @MainActor in
145‚Üí                        // Map download progress to 0‚Üí70% range
146‚Üí                        let pct = total > 0 ? Int(Double(completed) / Double(total) * 70) : 0
147‚Üí                        viewModel?.sharedVaultUpdateProgress = (completed: pct, total: 100, message: "Downloading update...")
148‚Üí                    }
149‚Üí                }
150‚Üí            )
151‚Üí
152‚Üí            if SVDFSerializer.isSVDF(data) {
153‚Üí                try await importSVDFDelta(data: data, shareKey: shareKeyData, vaultKey: key, index: index)
154‚Üí            } else {
155‚Üí                try await importLegacyFull(data: data, shareKey: shareKeyData, vaultKey: key, index: index)
156‚Üí            }
157‚Üí
158‚Üí            // Store the new version to avoid false "new files available"
159‚Üí            if let newVersion = try? await CloudKitSharingManager.shared.checkForUpdates(
