import SwiftUI

enum OnboardingStep: Int, CaseIterable {
    case welcome
    case permissions
    case analytics
    case paywall
    case thankYou
    case rating

    var progressFraction: CGFloat {
        CGFloat(rawValue + 1) / CGFloat(Self.allCases.count)
    }

    func next() -> OnboardingStep? {
        OnboardingStep(rawValue: rawValue + 1)
    }

    func previous() -> OnboardingStep? {
        OnboardingStep(rawValue: rawValue - 1)
    }
}

struct OnboardingView: View {
    /// When set, the view is shown in replay mode: pattern setup is skipped and
    /// tapping "Continue" on the final step calls this closure instead.
    var onReplayDismiss: (() -> Void)? = nil

    @Environment(AppState.self) private var appState
    @Environment(\.accessibilityReduceMotion) private var reduceMotion
    @State private var currentStep: OnboardingStep = .welcome
    @State private var showPatternSetup = false

    private var isReplay: Bool { onReplayDismiss != nil }

    var body: some View {
        if showPatternSetup {
            PatternSetupView(onComplete: { completeOnboarding() })
        } else {
            VStack(spacing: 0) {
                // Progress bar + back arrow (+ close button in replay mode)
                HStack(spacing: 8) {
                    Button {
                        if let prev = currentStep.previous() {
                            withAnimation(animation) { currentStep = prev }
                        }
                    } label: {
                        Image(systemName: "chevron.left")
                            .font(.body.weight(.semibold))
                            .foregroundStyle(.vaultText)
                    }
                    .opacity(currentStep != .welcome ? 1 : 0)
                    .disabled(currentStep == .welcome)
                    .accessibilityIdentifier("onboarding_back")

                    Capsule()
                        .fill(Color.vaultSecondaryText.opacity(0.2))
                        .frame(height: 4)
                        .overlay(alignment: .leading) {
                            Capsule()
                                .fill(Color.accentColor)
                                .frame(
                                    width: nil,
                                    height: 4
                                )
                                .frame(
                                    maxWidth: .infinity,
                                    alignment: .leading
                                )
                                .scaleEffect(x: currentStep.progressFraction, y: 1, anchor: .leading)
                        }

                    if currentStep == .paywall {
                        Button("Skip") { advance() }
                            .font(.subheadline.weight(.medium))
                            .foregroundStyle(.vaultSecondaryText)
                            .accessibilityIdentifier("paywall_skip")
                    } else if isReplay {
                        Button {
                            onReplayDismiss?()
                        } label: {
                            Image(systemName: "xmark")
                                .font(.body.weight(.semibold))
                                .foregroundStyle(.vaultSecondaryText)
                        }
                        .accessibilityLabel("Close")
                        .accessibilityIdentifier("onboarding_replay_close")
                    }
                }
                .padding(.horizontal, 20)
                .padding(.top, 12)
                .padding(.bottom, 8)

                // Step content
                switch currentStep {
                case .welcome:
                    WelcomeView(onContinue: { advance() })
                case .permissions:
                    PermissionsView(onContinue: { advance() })
                case .analytics:
                    AnalyticsConsentView(onContinue: { advance() })
                case .paywall:
                    PaywallStepView(onContinue: { advance() })
                case .thankYou:
                    ThankYouView(onContinue: { advance() })
                case .rating:
                    RatingView(onContinue: {
                        if let dismiss = onReplayDismiss {
                            dismiss()
                        } else {
                            withAnimation(animation) { showPatternSetup = true }
                        }
                    })
                }
            }
            .background(Color.vaultBackground.ignoresSafeArea())
        }
    }

    private var animation: Animation? {
        reduceMotion ? nil : .easeInOut
    }

    private func advance() {
        if let next = currentStep.next() {
            withAnimation(animation) { currentStep = next }
        }
    }

    private func completeOnboarding() {
        Task {
            await appState.completeOnboarding()
        }
    }
}

struct FeatureRow: View {
    let icon: String
    let title: String
    let description: String

    @Environment(\.dynamicTypeSize) private var dynamicTypeSize

    var body: some View {
        let useVertical = dynamicTypeSize.isAccessibilitySize

        Group {
            if useVertical {
                VStack(alignment: .leading, spacing: 8) {
                    featureIcon
                    featureText
                }
            } else {
                HStack(alignment: .top, spacing: 16) {
                    featureIcon
                    featureText
                }
            }
        }
        .accessibilityElement(children: .combine)
    }

    private var featureIcon: some View {
        Image(systemName: icon)
            .font(.title2)
            .foregroundStyle(.tint)
            .frame(width: 32)
            .accessibilityHidden(true)
    }

    private var featureText: some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(title)
                .font(.headline)
                .lineLimit(nil)
                .fixedSize(horizontal: false, vertical: true)

            Text(description)
                .font(.subheadline)
                .foregroundStyle(.vaultSecondaryText)
                .lineLimit(nil)
                .fixedSize(horizontal: false, vertical: true)
        }
    }
}

#Preview {
    OnboardingView()
        .environment(AppState())
        .environment(SubscriptionManager.shared)
}

import SwiftUI

struct WelcomeView: View {
    let onContinue: () -> Void

    @State private var animateIcon = false
    @Environment(\.accessibilityReduceMotion) private var reduceMotion
    @Environment(\.dynamicTypeSize) private var dynamicTypeSize

    var body: some View {
        GeometryReader { proxy in
            let h = proxy.size.height
            let isCompact = h < 700 // iPhone SE, mini

            VStack(spacing: 0) {
                ScrollView {
                    VStack(spacing: 0) {
                        Spacer().frame(height: h * 0.08)

                        // App Logo
                        Image("VaultLogo")
                            .resizable()
                            .aspectRatio(contentMode: .fit)
                            .frame(width: min(h * 0.15, 140), height: min(h * 0.15, 140))
                            .clipShape(RoundedRectangle(cornerRadius: 24, style: .continuous))
                            .shadow(color: .black.opacity(0.15), radius: 8, y: 4)
                            .scaleEffect(animateIcon ? 1.05 : 1.0)
                            .accessibilityHidden(true)
                            .onAppear {
                                guard !reduceMotion else { return }
                                withAnimation(.easeInOut(duration: 2).repeatForever(autoreverses: true)) {
                                    animateIcon = true
                                }
                            }

                        Spacer().frame(height: h * 0.03)

                        // Welcome Text
                        VStack(spacing: 8) {
                            Text("Welcome to Vaultaire")
                                .font(.title2)
                                .fontWeight(.bold)

                            Text("Secure storage with plausible deniability")
                                .font(isCompact ? .subheadline : .body)
                                .foregroundStyle(.vaultSecondaryText)
                        }
                        .multilineTextAlignment(.center)

                        Spacer().frame(height: h * 0.04)

                        // Feature Rows
                        VStack(alignment: .leading, spacing: h * 0.02) {
                            FeatureRow(
                                icon: "hand.draw",
                                title: "Pattern-Based Encryption",
                                description: isCompact ? "Different patterns = different vaults" : "Each pattern creates a unique encrypted vault"
                            )
                            FeatureRow(
                                icon: "eye.slash",
                                title: "True Plausible Deniability",
                                description: isCompact ? "No one can tell how many vaults you have" : "Nobody can tell how many vaults you have"
                            )
                            FeatureRow(
                                icon: "lock.shield",
                                title: "Hardware Security",
                                description: isCompact ? "Keys protected by Secure Enclave" : "Your encryption keys are protected by your device's Secure Enclave"
                            )
                            FeatureRow(
                                icon: "icloud",
                                title: "Encrypted Backup",
                                description: isCompact ? "Back up to iCloud, only you can decrypt" : "Back up to your iCloud. Only you can decrypt it with your pattern"
                            )
                        }
                        .padding(.horizontal)

                        Spacer().frame(height: h * 0.04)
                    }
                    .frame(maxWidth: .infinity)
                }
                .scrollBounceBehavior(.basedOnSize)

                // Continue Button ‚Äî pinned at bottom
                Button(action: onContinue) {
                    Text("Protect Your First Vault")
                        .font(.headline)
                        .frame(maxWidth: .infinity)
                        .padding()
                }
                .vaultProminentButtonStyle()
                .padding(.horizontal, 32)
                .padding(.bottom, 24)
                .accessibilityIdentifier("welcome_continue")
            }
        }
        .background(Color.vaultBackground.ignoresSafeArea())
    }
}

