import Foundation

struct PatternSetupCoordinator {
    var deriveKey: ([Int], Int) async throws -> Data = { pattern, gridSize in
        try await KeyDerivation.deriveKey(from: pattern, gridSize: gridSize)
    }
    var vaultExists: (Data) -> Bool = { key in
        VaultStorage.shared.vaultHasFiles(for: VaultKey(key))
    }
    var saveIndex: (VaultStorage.VaultIndex, Data) throws -> Void = { index, key in
        try VaultStorage.shared.saveIndex(index, with: VaultKey(key))
    }
    var saveRecoveryPhrase: (String, [Int], Int, Data) async throws -> Void = { phrase, pattern, gridSize, key in
        try await RecoveryPhraseManager.shared.saveRecoveryPhrase(
            phrase: phrase, pattern: pattern, gridSize: gridSize, patternKey: key
        )
    }

    enum SetupResult {
        case success(key: Data)
        case duplicatePattern
        case error(String)
    }

    /// Save a new pattern: derive key, check uniqueness, create vault index, save recovery phrase.
    func savePattern(_ pattern: [Int], gridSize: Int, phrase: String) async -> SetupResult {
        do {
            let key = try await deriveKey(pattern, gridSize)

            if vaultExists(key) {
                return .duplicatePattern
            }

            // Create empty vault index. loadIndex auto-creates a proper v3 index with master key when none exists.
            let emptyIndex = try VaultStorage.shared.loadIndex(with: VaultKey(key))
            try saveIndex(emptyIndex, key)

            try await saveRecoveryPhrase(phrase, pattern, gridSize, key)

            return .success(key: key)
        } catch {
            return .error(error.localizedDescription)
        }
    }

    /// Save a custom recovery phrase for an already-created vault.
    func saveCustomPhrase(_ phrase: String, pattern: [Int], gridSize: Int, key: Data) async -> SetupResult {
        do {
            try await saveRecoveryPhrase(phrase, pattern, gridSize, key)
            return .success(key: key)
        } catch {
            return .error(error.localizedDescription)
        }
    }
}

import SwiftUI
import AVFoundation
import UserNotifications
import Photos
import os.log

private let permissionsLogger = Logger(subsystem: "app.vaultaire.ios", category: "Permissions")

struct PermissionsView: View {
    let onContinue: () -> Void

    @State private var notificationStatus: PermissionStatus = .notDetermined
    @State private var cameraStatus: PermissionStatus = .notDetermined
    @State private var photoStatus: PermissionStatus = .notDetermined

    enum PermissionStatus {
        case notDetermined, granted, denied
    }

    var body: some View {
        VStack(spacing: 32) {
            Spacer()

            Image(systemName: "checkmark.shield.fill")
                .font(.system(size: 56))
                .foregroundStyle(.tint)

            Text("Enable Permissions")
                .font(.title)
                .fontWeight(.bold)

            Text("Vaultaire works best with these permissions. You can change them anytime in Settings.")
                .font(.subheadline)
                .foregroundStyle(.vaultSecondaryText)
                .multilineTextAlignment(.center)
                .padding(.horizontal, 24)

            VStack(spacing: 16) {
                permissionRow(
                    icon: "bell.fill",
                    title: "Notifications",
                    description: "Know when backups and imports finish",
                    status: notificationStatus,
                    action: requestNotifications
                )

                permissionRow(
                    icon: "camera.fill",
                    title: "Camera",
                    description: "Capture photos directly into your vault",
                    status: cameraStatus,
                    action: requestCamera
                )

                permissionRow(
                    icon: "photo.fill",
                    title: "Photos",
                    description: "Import existing photos and videos",
                    status: photoStatus,
                    action: requestPhotos
                )
            }
            .padding(.horizontal, 24)

            Spacer()

            Button(action: onContinue) {
                Text("Continue")
                    .font(.headline)
                    .frame(maxWidth: .infinity)
                    .padding()
            }
            .vaultProminentButtonStyle()
            .padding(.horizontal, 40)
            .padding(.bottom, 40)
            .accessibilityIdentifier("permissions_continue")
        }
        .background(Color.vaultBackground.ignoresSafeArea())
        .task {
            await checkCurrentStatuses()
        }
    }

    // MARK: - Views

    private func permissionRow(
        icon: String,
        title: String,
        description: String,
        status: PermissionStatus,
        action: @escaping () async -> Void
    ) -> some View {
        HStack(spacing: 16) {
            Image(systemName: icon)
                .font(.title2)
                .foregroundStyle(.tint)
                .frame(width: 32)

            VStack(alignment: .leading, spacing: 2) {
                Text(title)
                    .font(.headline)
                Text(description)
                    .font(.caption)
                    .foregroundStyle(.vaultSecondaryText)
                    .fixedSize(horizontal: false, vertical: true)
            }

            Spacer()

            switch status {
            case .notDetermined:
                Button("Allow") {
                    Task { await action() }
                }
                .buttonStyle(.borderedProminent)
                .controlSize(.small)
            case .granted:
                Image(systemName: "checkmark.circle.fill")
                    .foregroundStyle(.green)
                    .font(.title3)
            case .denied:
                Image(systemName: "xmark.circle.fill")
                    .foregroundStyle(.vaultSecondaryText)
                    .font(.title3)
            }
        }
        .padding()
        .vaultGlassBackground(cornerRadius: 12)
    }

    // MARK: - Permission Requests

    private func checkCurrentStatuses() async {
        async let notifSettings = UNUserNotificationCenter.current().notificationSettings()
        let cameraAuth = AVCaptureDevice.authorizationStatus(for: .video)
        let photoAuth = PHPhotoLibrary.authorizationStatus()

        let settings = await notifSettings
        await MainActor.run {
            switch settings.authorizationStatus {
            case .authorized, .provisional: notificationStatus = .granted
            case .denied: notificationStatus = .denied
            default: notificationStatus = .notDetermined
            }

            switch cameraAuth {
            case .authorized: cameraStatus = .granted
            case .denied, .restricted: cameraStatus = .denied
            default: cameraStatus = .notDetermined
            }

            switch photoAuth {
            case .authorized, .limited: photoStatus = .granted
            case .denied, .restricted: photoStatus = .denied
            default: photoStatus = .notDetermined
            }
        }
    }

    private func requestNotifications() async {
        do {
            let granted = try await UNUserNotificationCenter.current()
                .requestAuthorization(options: [.alert, .sound])
            permissionsLogger.info("Notification permission: \(granted)")
            await MainActor.run {
                notificationStatus = granted ? .granted : .denied
            }
        } catch {
            permissionsLogger.error("Notification request failed: \(error.localizedDescription)")
            await MainActor.run { notificationStatus = .denied }
        }
    }

    private func requestCamera() async {
        let granted = await AVCaptureDevice.requestAccess(for: .video)
        permissionsLogger.info("Camera permission: \(granted)")
        await MainActor.run {
            cameraStatus = granted ? .granted : .denied
        }
    }

    private func requestPhotos() async {
        let status = PHPhotoLibrary.authorizationStatus(for: .readWrite)
        if status == .notDetermined {
            PHPhotoLibrary.requestAuthorization(for: .readWrite) { newStatus in
                DispatchQueue.main.async {
                    switch newStatus {
                    case .authorized, .limited:
                        self.photoStatus = .granted
                    case .denied, .restricted:
                        self.photoStatus = .denied
                    default:
                        self.photoStatus = .notDetermined
                    }
                }
            }
        } else {
            await MainActor.run {
                switch status {
                case .authorized, .limited:
                    photoStatus = .granted
                case .denied, .restricted:
                    photoStatus = .denied
                default:
                    photoStatus = .notDetermined
                }
            }
        }
    }
}

