file.content:
/* Compare section specific styles */

.page-compare {
  line-height: 1.65;
}

.page-compare ul {
  list-style: none;
  margin: 0;
  padding: 0;
}

/* ‚îÄ‚îÄ Breadcrumbs ‚îÄ‚îÄ */
.breadcrumbs {
  padding: 100px 0 0;
  font-size: 0.92rem;
  color: var(--text-secondary);
}

.breadcrumbs a {
  color: var(--accent);
}

.breadcrumbs a:hover {
  color: var(--highlight);
}

.breadcrumbs span {
  margin: 0 8px;
  opacity: 0.5;
}

/* ‚îÄ‚îÄ Compare Hero ‚îÄ‚îÄ */
.compare-hero {
  padding: 32px 0 48px;
  text-align: center;
}

.compare-hero-inner {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 24px;
  margin-bottom: 24px;
}

.compare-hero .competitor-icon,
.compare-hero .vaultaire-icon {
  width: 72px;
  height: 72px;
  border-radius: 16px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
}

.compare-hero .vs-badge {
  font-family: var(--font-display);
  font-weight: 700;
  font-size: 1.5rem;
  color: var(--highlight);
  text-transform: uppercase;
}

.compare-hero h1 {
  font-size: clamp(2rem, 4vw, 3rem);
  line-height: 1.1;
  margin-bottom: 16px;
}

.compare-hero .positioning {
  color: var(--text-secondary);
  font-size: 1.1rem;
  max-width: 700px;
  margin: 0 auto;
}

/* ‚îÄ‚îÄ Compare Section ‚îÄ‚îÄ */
.compare-section {
  padding: 48px 0;
}

.compare-text-center {
  text-align: center;
}

.compare-pb-0 {
  padding-bottom: 0;
}

.compare-link-accent {
  color: var(--accent);
}

.compare-rating-note {
  color: var(--text-secondary);
  font-size: 0.9rem;
  margin-top: 12px;
}

.compare-summary-note {
  margin-top: 16px;
  font-size: 0.88rem;
  color: var(--text-secondary);
}

.compare-review-link {
  margin-top: 16px;
}

.compare-back-wrap {
  text-align: center;
  margin-top: 32px;
}

.compare-section h2 {
  font-size: clamp(1.3rem, 2.5vw, 1.8rem);
  margin-bottom: 24px;
  color: var(--text);
}

.compare-section p {
  color: var(--text-secondary);
  font-size: 1rem;
  line-height: 1.65;
  margin-bottom: 16px;
  max-width: 800px;
}

/* ‚îÄ‚îÄ Feature Comparison Table ‚îÄ‚îÄ */
.compare-table-wrap {
  overflow-x: auto;
  margin: 0 -24px;
  padding: 0 24px;
  -webkit-overflow-scrolling: touch;
}

.compare-table {
  width: 100%;
  border-collapse: collapse;
  font-size: 0.95rem;
}

.compare-table thead th {
  padding: 16px 20px;
  text-align: left;
  font-family: var(--font-display);
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  font-size: 0.88rem;
  border-bottom: 2px solid var(--accent);
  color: var(--text);
}

.compare-table thead th:first-child {
  width: 40%;
}

.compare-table thead th:nth-child(2),
.compare-table thead th:nth-child(3) {
  width: 30%;
  text-align: center;
}

.compare-table tbody td {
  padding: 14px 20px;
  border-bottom: 1px solid color-mix(in srgb, var(--text) 10%, transparent 90%);
  vertical-align: middle;
}

.compare-table tbody td:nth-child(2),
.compare-table tbody td:nth-child(3) {
  text-align: center;
}

.compare-table tbody tr:hover {
  background: color-mix(in srgb, var(--surface) 50%, transparent 50%);
}

.compare-table .feature-name {
  color: var(--text);
  font-weight: 500;
}

.compare-table .feature-note {
  display: block;
  font-size: 0.9rem;
  line-height: 1.45;
  color: var(--text-secondary);
  margin-top: 2px;
}

.check-yes {
  color: #57E37A;
  font-weight: 700;
  font-size: 1.2rem;
}

.check-no {
  color: #FF6F6F;
  font-weight: 700;
  font-size: 1.2rem;
}

:root[data-theme='light'] .check-yes {
  color: #2E9E4D;
}

:root[data-theme='light'] .check-no {
  color: #E45858;
}

.check-partial {
  color: var(--text-secondary);
  font-size: 0.85rem;
}

/* ‚îÄ‚îÄ Pricing Comparison ‚îÄ‚îÄ */
.pricing-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 24px;
  margin-top: 24px;
}

.pricing-card {
  background: color-mix(in srgb, var(--surface) 80%, transparent 20%);
  border: 1px solid color-mix(in srgb, var(--text) 12%, transparent 88%);
  border-radius: 16px;
  padding: 32px;
}

.pricing-card.pricing-vaultaire {
  border-color: var(--accent);
}

.pricing-card h3 {
  font-size: 1.2rem;
  margin-bottom: 20px;
  text-transform: uppercase;
}

.pricing-tier {
  margin-bottom: 16px;
  padding-bottom: 16px;
  border-bottom: 1px solid color-mix(in srgb, var(--text) 8%, transparent 92%);
}

.pricing-tier:last-child {
  border-bottom: none;
  margin-bottom: 0;
  padding-bottom: 0;
}

.pricing-tier .tier-name {
  font-weight: 600;
  color: var(--text);
  font-size: 0.95rem;
}

.pricing-tier .tier-price {
  font-family: var(--font-display);
  font-weight: 700;
  font-size: 1.4rem;
  color: var(--accent);
  margin: 4px 0;
}

.pricing-tier .tier-details {
  font-size: 0.94rem;
  line-height: 1.55;
  color: var(--text-secondary);
}

.pricing-tier .tier-details li {
  padding: 2px 0;
  padding-left: 16px;
  position: relative;
}

.pricing-tier .tier-details li::before {
  content: '/';
  position: absolute;
  left: 0;
  color: var(--highlight);
  font-weight: bold;
}

/* ‚îÄ‚îÄ Reviews Section ‚îÄ‚îÄ */
.compare-reviews {
  padding: 48px 0;
}

.review-card {
  background: color-mix(in srgb, var(--surface) 60%, transparent 40%);
  border: 1px solid color-mix(in srgb, var(--text) 10%, transparent 90%);
  border-radius: 12px;
  padding: 24px;
  margin-bottom: 20px;
}

.review-card .review-complaint {
  font-weight: 600;
  color: var(--highlight);
  font-size: 1rem;
  margin-bottom: 8px;
}

.review-card .review-quote {
  font-style: italic;
  color: var(--text-secondary);
  font-size: 0.98rem;
  line-height: 1.65;
  margin-bottom: 12px;
  padding-left: 16px;
  border-left: 2px solid color-mix(in srgb, var(--text) 20%, transparent 80%);
}

.review-card .review-response {
  color: var(--text);
  font-size: 0.98rem;
  line-height: 1.65;
}

.review-card .review-response strong {
  color: var(--accent);
}

/* ‚îÄ‚îÄ CTA Section ‚îÄ‚îÄ */
.compare-cta {
  padding: 80px 0;
  text-align: center;
  background: radial-gradient(ellipse at center, color-mix(in srgb, var(--accent) 8%, transparent 92%) 0%, transparent 70%);
}

.compare-cta h2 {
  font-size: clamp(1.8rem, 3.5vw, 2.8rem);
  margin-bottom: 16px;
}

.compare-cta p {
  color: var(--text-secondary);
  max-width: 600px;
  margin: 0 auto 32px;
}

.compare-cta .btn {
  font-size: 1rem;
}

/* ‚îÄ‚îÄ More Comparisons Nav ‚îÄ‚îÄ */
.more-comparisons {
  padding: 48px 0;
  border-top: 1px solid color-mix(in srgb, var(--text) 10%, transparent 90%);
}

.more-comparisons h3 {
  font-size: 1.1rem;
  margin-bottom: 20px;
  text-align: center;
}

.more-comparisons-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 12px;
}

.more-comparisons-grid a {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px 16px;
  background: color-mix(in srgb, var(--surface) 50%, transparent 50%);
  border: 1px solid color-mix(in srgb, var(--text) 8%, transparent 92%);
  border-radius: 10px;
  font-size: 0.88rem;
  color: var(--text);
  transition: border-color 0.2s, background 0.2s;
}

.more-comparisons-grid a:hover {
  border-color: var(--accent);
  background: color-mix(in srgb, var(--surface) 80%, transparent 20%);
}

.more-comparisons-grid a img {
  width: 28px;
  height: 28px;
  border-radius: 6px;
}

/* ‚îÄ‚îÄ Compare Landing Page (Grid of Cards) ‚îÄ‚îÄ */
.compare-landing-hero {
  padding: 104px 0 36px;
  text-align: center;
}

.compare-landing-hero h1 {
  font-size: clamp(2rem, 4.5vw, 3.2rem);
  margin-bottom: 16px;
}

.compare-landing-hero p {
  color: var(--text-secondary);
  font-size: 1.1rem;
  max-width: 700px;
  margin: 0 auto;
}

.compare-card-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
  gap: 24px;
  padding: 24px 0 80px;
}

.compare-card {
  background: color-mix(in srgb, var(--surface) 80%, transparent 20%);
  border: 1px solid color-mix(in srgb, var(--text) 12%, transparent 88%);
  border-radius: 16px;
  padding: 28px;
  display: flex;
  gap: 20px;
  align-items: flex-start;
  transition: transform 0.2s, border-color 0.2s, box-shadow 0.2s;
}

.compare-card:hover {
  transform: translateY(-4px);
  border-color: var(--accent);
  box-shadow: 0 12px 32px rgba(0, 0, 0, 0.2);
}

.compare-card img {
  width: 56px;
  height: 56px;
  border-radius: 12px;
  flex-shrink: 0;
}

.compare-card-content h3 {
  font-size: 1rem;
  margin-bottom: 4px;
  text-transform: none;
  letter-spacing: 0;
}

.compare-card-content .card-ratings {
  font-size: 0.82rem;
  color: var(--text-secondary);
  margin-bottom: 8px;
}

.compare-card-content .card-diff {
  font-size: 0.96rem;
  color: var(--text-secondary);
  line-height: 1.55;
}

/* ‚îÄ‚îÄ Review Article ‚îÄ‚îÄ */
.review-article {
  max-width: 800px;
  margin: 0 auto;
  padding: 32px 0 80px;
}

.review-article h1 {
  font-size: clamp(1.8rem, 3.5vw, 2.6rem);
  line-height: 1.15;
  margin-bottom: 16px;
  text-transform: none;
  letter-spacing: -0.02em;
}

.review-article .article-meta {
  color: var(--text-secondary);
  font-size: 0.88rem;
  margin-bottom: 32px;
}

.review-article h2 {
  font-size: 1.4rem;
  margin: 40px 0 16px;
  text-transform: none;
  letter-spacing: -0.01em;
}

.review-article h3 {
  font-size: 1.1rem;
  margin: 28px 0 12px;
  text-transform: none;
  letter-spacing: 0;
}

.review-article p {
  color: var(--text-secondary);
  font-size: 1rem;
  margin-bottom: 16px;
  line-height: 1.7;
}

.review-article blockquote {
  border-left: 3px solid var(--highlight);
  padding: 12px 20px;
  margin: 20px 0;
  background: color-mix(in srgb, var(--surface) 50%, transparent 50%);
  border-radius: 0 8px 8px 0;
  font-style: italic;
  color: var(--text-secondary);
}

.review-article .verdict-box {
  background: color-mix(in srgb, var(--accent) 10%, transparent 90%);
  border: 1px solid var(--accent);
  border-radius: 16px;
  padding: 32px;
  margin: 40px 0;
  text-align: center;
}

.review-article .verdict-box h3 {
  color: var(--accent);
  margin-bottom: 12px;
}

.review-article .verdict-box p {
  color: var(--text);
  margin-bottom: 16px;
}

/* ‚îÄ‚îÄ Responsive ‚îÄ‚îÄ */
@media (max-width: 900px) {
  .pricing-grid {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 768px) {
  .compare-hero {
    padding-top: 24px;
  }

  .compare-hero .competitor-icon,
  .compare-hero .vaultaire-icon {
    width: 56px;
    height: 56px;
    border-radius: 12px;
  }

  .compare-hero .vs-badge {
    font-size: 1.1rem;
  }

  .compare-table {
    font-size: 0.9rem;
  }

  .compare-table thead th,
  .compare-table tbody td {
    padding: 10px 12px;
  }

  .compare-card-grid {
    grid-template-columns: 1fr;
    gap: 16px;
    padding-bottom: 56px;
  }

  .compare-card {
    padding: 18px;
    gap: 14px;
  }

  .compare-landing-hero {
    padding-top: 96px;
    padding-bottom: 28px;
  }

  .compare-landing-hero p {
    font-size: 1rem;
  }

  .more-comparisons-grid {
    grid-template-columns: 1fr;
  }

  .review-article {
    padding: 24px 0 60px;
  }

  .review-article .verdict-box {
    padding: 22px;
  }
}

