.page-feature main {
  padding: 0 0 0;
}

/* ‚îÄ‚îÄ Breadcrumbs (reuse compare pattern) ‚îÄ‚îÄ */
.page-feature .breadcrumbs {
  padding: 100px 0 0;
  font-size: 0.92rem;
  color: var(--text-secondary);
}

.page-feature .breadcrumbs a {
  color: var(--accent);
}

.page-feature .breadcrumbs a:hover {
  color: var(--highlight);
}

.page-feature .breadcrumbs span {
  margin: 0 8px;
  opacity: 0.5;
}

/* ‚îÄ‚îÄ Feature Article ‚îÄ‚îÄ */
.feature-article {
  max-width: 800px;
  margin: 0 auto;
  padding: 32px 0 80px;
}

.feature-article h1 {
  font-size: clamp(2rem, 4vw, 3rem);
  line-height: 1.1;
  margin-bottom: 16px;
  text-transform: none;
  letter-spacing: -0.02em;
}

.feature-article .article-meta {
  color: var(--text-secondary);
  font-size: 0.88rem;
  margin-bottom: 40px;
  display: flex;
  align-items: center;
  gap: 12px;
  flex-wrap: wrap;
}

.feature-article .article-meta .meta-tag {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  padding: 4px 12px;
  background: color-mix(in srgb, var(--accent) 10%, transparent 90%);
  border-radius: 4px;
  font-size: 0.82rem;
  font-family: var(--font-display);
  font-weight: 500;
  letter-spacing: 0.02em;
  text-transform: uppercase;
  clip-path: polygon(4px 0, 100% 0, 100% calc(100% - 4px), calc(100% - 4px) 100%, 0 100%, 0 4px);
}

.feature-article h2 {
  font-size: 1.5rem;
  margin: 48px 0 20px;
  text-transform: none;
  letter-spacing: -0.01em;
  color: var(--text);
}

.feature-article h3 {
  font-size: 1.15rem;
  margin: 32px 0 12px;
  text-transform: none;
  letter-spacing: 0;
  color: var(--text);
}

.feature-article p {
  color: var(--text-secondary);
  font-size: 1rem;
  margin-bottom: 16px;
  line-height: 1.75;
}

.feature-article strong {
  color: var(--text);
  font-weight: 600;
}

.feature-article ul,
.feature-article ol {
  color: var(--text-secondary);
  margin: 0 0 20px 0;
  padding-left: 0;
  list-style: none;
}

.feature-article li {
  padding: 6px 0 6px 20px;
  position: relative;
  line-height: 1.65;
}

.feature-article li::before {
  content: '/';
  position: absolute;
  left: 0;
  color: var(--highlight);
  font-weight: 700;
  font-family: var(--font-display);
}

.feature-article ol {
  counter-reset: feature-step;
}

.feature-article ol li {
  counter-increment: feature-step;
}

.feature-article ol li::before {
  content: counter(feature-step) '.';
  color: var(--accent);
}

/* ‚îÄ‚îÄ Hero Section ‚îÄ‚îÄ */
.feature-hero {
  padding: 24px 0 0;
  text-align: center;
}

.feature-hero-icon {
  font-size: 3.5rem;
  margin-bottom: 20px;
  display: block;
}

.feature-hero .feature-article h1 {
  text-align: center;
}

.feature-hero p.hero-lead {
  font-size: 1.15rem;
  color: var(--text-secondary);
  max-width: 640px;
  margin: 0 auto 32px;
  line-height: 1.7;
}

/* ‚îÄ‚îÄ Info Boxes ‚îÄ‚îÄ */
.info-box {
  background: color-mix(in srgb, var(--surface) 60%, transparent 40%);
  border: 1px solid color-mix(in srgb, var(--text) 10%, transparent 90%);
  padding: 24px 28px;
  margin: 24px 0;
  clip-path: polygon(12px 0, 100% 0, 100% calc(100% - 12px), calc(100% - 12px) 100%, 0 100%, 0 12px);
  position: relative;
}

.info-box::after {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  width: 24px;
  height: 24px;
  background: linear-gradient(to bottom left, var(--accent) 50%, transparent 50%);
  opacity: 0.3;
}

.info-box-label {
  font-family: var(--font-display);
  font-weight: 700;
  font-size: 0.78rem;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--accent);
  margin-bottom: 8px;
}

.info-box p {
  margin-bottom: 8px;
}

.info-box p:last-child {
  margin-bottom: 0;
}

/* ‚îÄ‚îÄ Stat Callouts ‚îÄ‚îÄ */
.stat-row {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
  gap: 16px;
  margin: 32px 0;
}

.stat-card {
  background: color-mix(in srgb, var(--surface) 50%, transparent 50%);
  border: 1px solid color-mix(in srgb, var(--text) 8%, transparent 92%);
  padding: 24px;
  text-align: center;
  clip-path: polygon(8px 0, 100% 0, 100% calc(100% - 8px), calc(100% - 8px) 100%, 0 100%, 0 8px);
}

.stat-value {
