  font-size: 0.78rem;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--text-secondary);
  margin-bottom: 20px;
  text-align: center;
}

/* Pattern Grid Diagram */
.pattern-grid-diagram {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 12px;
  max-width: 280px;
  margin: 0 auto;
  padding: 20px;
}

.grid-node {
  aspect-ratio: 1;
  border-radius: 50%;
  border: 2px solid color-mix(in srgb, var(--text) 25%, transparent 75%);
  background: transparent;
  transition: all 0.3s ease;
  position: relative;
}

.grid-node.active {
  border-color: var(--accent);
  background: color-mix(in srgb, var(--accent) 20%, transparent 80%);
  box-shadow: 0 0 12px color-mix(in srgb, var(--accent) 30%, transparent 70%);
}

.grid-node.active::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 40%;
  height: 40%;
  background: var(--accent);
  border-radius: 50%;
}

/* Encryption Flow Diagram */
.flow-diagram {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  flex-wrap: wrap;
  padding: 16px 0;
}

.flow-step {
  display: flex;
