  clip-path: polygon(6px 0, 100% 0, 100% calc(100% - 6px), calc(100% - 6px) 100%, 0 100%, 0 6px);
  min-width: 100px;
}

.flow-step-label {
  font-size: 0.72rem;
  color: var(--text-secondary);
  text-align: center;
}

.flow-arrow {
  color: var(--highlight);
  font-size: 1.2rem;
