}

/* ‚îÄ‚îÄ Breadcrumbs (reuse compare pattern) ‚îÄ‚îÄ */
.page-feature .breadcrumbs {
  padding: 100px 0 0;
  font-size: 0.92rem;
  color: var(--text-secondary);
}

.page-feature .breadcrumbs a {
  color: var(--accent);
}

.page-feature .breadcrumbs a:hover {
  color: var(--highlight);
}

.page-feature .breadcrumbs span {
  margin: 0 8px;
  opacity: 0.5;
}

