}

.feature-article ul,
.feature-article ol {
  color: var(--text-secondary);
  margin: 0 0 20px 0;
  padding-left: 0;
  list-style: none;
}

.feature-article li {
  padding: 6px 0 6px 20px;
  position: relative;
  line-height: 1.65;
}

.feature-article li::before {
  content: '/';
  position: absolute;
  left: 0;
  color: var(--highlight);
  font-weight: 700;
  font-family: var(--font-display);
}

.feature-article ol {
  counter-reset: feature-step;
