    padding: 32px 24px;
  }

  .related-grid {
    grid-template-columns: 1fr;
  }

  .feature-hub-grid {
    grid-template-columns: 1fr;
    gap: 16px;
    padding-bottom: 56px;
  }

  .feature-hub-hero {
    padding-top: 96px;
  }

  .feature-hub-card {
    padding: 22px;
    gap: 14px;
  }

  .diagram-container {
    padding: 20px;
    margin: 24px -8px;
  }

  .pattern-grid-diagram {
    gap: 8px;
    max-width: 220px;
  }

  .info-box {
    padding: 20px;
  }
}

