352‚Üí        const mockupTimeEl = document.getElementById("mockup-time");
353‚Üí
354‚Üí        if (!canvas || !pathEl || !cursorEl || !svgEl || !lockZone || !phoneScreen || dots.length === 0 || !mockupTimeEl) {
355‚Üí          return;
356‚Üí        }
357‚Üí
358‚Üí        const ctx = canvas.getContext("2d");
359‚Üí        if (!ctx) return;
360‚Üí
361‚Üí        const runtime = {
362‚Üí          disposed: false,
363‚Üí          heroVisible: true,
364‚Üí          width: 0,
365‚Üí          height: 0,
366‚Üí          bgDots: [],
367‚Üí          accentColor: "#CCC3F8",
368‚Üí          accentTrailColor: "rgba(204, 195, 248, 0.4)",
369‚Üí          drawingPath: false,
370‚Üí          drawingResetTimerId: 0,
371‚Üí          drawRafId: 0,
372‚Üí          minuteTimerId: 0,
373‚Üí          sequenceIndex: 0,
374‚Üí          nodeCenters: [],
375‚Üí          patternLoopTicket: 0,
376‚Üí          patternLoopRunning: false,
377‚Üí          timeoutIds: new Set(),
378‚Üí          sequences: [[0, 5, 6, 7, 12, 17, 16, 15, 20, 21, 22, 23, 24]]
379‚Üí        };
380‚Üí
381‚Üí        let resizeRafId = 0;
382‚Üí
383‚Üí        const scheduleTimeout = (fn, ms) => {
384‚Üí          const id = window.setTimeout(() => {
385‚Üí            runtime.timeoutIds.delete(id);
386‚Üí            if (!runtime.disposed) fn();
387‚Üí          }, ms);
388‚Üí          runtime.timeoutIds.add(id);
389‚Üí          return id;
390‚Üí        };
391‚Üí
392‚Üí        const clearScheduledTimeout = (id) => {
393‚Üí          if (!id) return;
394‚Üí          window.clearTimeout(id);
395‚Üí          runtime.timeoutIds.delete(id);
396‚Üí        };
397‚Üí
398‚Üí        const clearAllTimeouts = () => {
399‚Üí          runtime.timeoutIds.forEach((id) => window.clearTimeout(id));
400‚Üí          runtime.timeoutIds.clear();
401‚Üí          runtime.drawingResetTimerId = 0;
402‚Üí          runtime.minuteTimerId = 0;
403‚Üí        };
404‚Üí
405‚Üí        const updateCanvasTheme = () => {
406‚Üí          const styles = getComputedStyle(document.documentElement);
407‚Üí          runtime.accentColor = styles.getPropertyValue("--accent-dark").trim() || "#CCC3F8";
408‚Üí          runtime.accentTrailColor = toRgba(runtime.accentColor, 0.35);
409‚Üí        };
410‚Üí
411‚Üí        const initDots = () => {
412‚Üí          runtime.bgDots = [];
413‚Üí          const spacing = 110;
414‚Üí          const rows = Math.ceil(runtime.height / spacing);
415‚Üí          const cols = Math.ceil(runtime.width / spacing);
416‚Üí          for (let i = 0; i < cols; i += 1) {
417‚Üí            for (let j = 0; j < rows; j += 1) {
418‚Üí              runtime.bgDots.push({
419‚Üí                x: i * spacing + (Math.random() * 24),
420‚Üí                y: j * spacing + (Math.random() * 24)
421‚Üí              });
422‚Üí            }
423‚Üí          }
424‚Üí        };
425‚Üí
426‚Üí        const resize = () => {
427‚Üí          if (runtime.disposed) return;
428‚Üí          runtime.width = canvas.width = canvas.parentElement.offsetWidth;
429‚Üí          runtime.height = canvas.height = canvas.parentElement.offsetHeight;
430‚Üí          initDots();
431‚Üí        };
432‚Üí
433‚Üí        const scheduleResize = () => {
434‚Üí          if (runtime.disposed || resizeRafId) return;
435‚Üí          resizeRafId = window.requestAnimationFrame(() => {
436‚Üí            resizeRafId = 0;
437‚Üí            if (!runtime.disposed) {
438‚Üí              resize();
439‚Üí            }
440‚Üí          });
441‚Üí        };
442‚Üí
443‚Üí        const drawRandomPattern = () => {
444‚Üí          if (runtime.drawingPath || runtime.bgDots.length === 0 || runtime.disposed || document.hidden || !runtime.heroVisible) return;
445‚Üí          runtime.drawingPath = true;
446‚Üí
447‚Üí          const startIndex = Math.floor(Math.random() * runtime.bgDots.length);
448‚Üí          const pathLength = 4 + Math.floor(Math.random() * 3);
449‚Üí          const start = runtime.bgDots[startIndex];
450‚Üí          let current = start;
451‚Üí
452‚Üí          ctx.strokeStyle = runtime.accentTrailColor;
453‚Üí          ctx.lineWidth = 2;
454‚Üí          ctx.beginPath();
455‚Üí          ctx.moveTo(start.x, start.y);
456‚Üí
457‚Üí          for (let i = 0; i < pathLength; i += 1) {
458‚Üí            const next = runtime.bgDots[Math.floor(Math.random() * runtime.bgDots.length)];
459‚Üí            const dist = Math.hypot(next.x - current.x, next.y - current.y);
460‚Üí            if (dist < 200 && dist > 36) {
461‚Üí              ctx.lineTo(next.x, next.y);
462‚Üí              current = next;
463‚Üí            }
464‚Üí          }
465‚Üí          ctx.stroke();
466‚Üí
467‚Üí          clearScheduledTimeout(runtime.drawingResetTimerId);
468‚Üí          runtime.drawingResetTimerId = scheduleTimeout(() => {
469‚Üí            runtime.drawingPath = false;
470‚Üí            runtime.drawingResetTimerId = 0;
471‚Üí          }, 540);
472‚Üí        };
473‚Üí
474‚Üí        const draw = () => {
475‚Üí          if (runtime.disposed || document.hidden || !runtime.heroVisible) {
476‚Üí            runtime.drawRafId = 0;
477‚Üí            return;
478‚Üí          }
479‚Üí
480‚Üí          ctx.clearRect(0, 0, runtime.width, runtime.height);
481‚Üí          ctx.fillStyle = runtime.accentColor;
482‚Üí          runtime.bgDots.forEach((dot) => {
483‚Üí            ctx.beginPath();
484‚Üí            ctx.arc(dot.x, dot.y, 1.4, 0, Math.PI * 2);
485‚Üí            ctx.fill();
486‚Üí          });
487‚Üí
488‚Üí          if (Math.random() > 0.95) drawRandomPattern();
489‚Üí          runtime.drawRafId = window.requestAnimationFrame(draw);
490‚Üí        };
491‚Üí
492‚Üí        const startDrawLoop = () => {
493‚Üí          if (runtime.disposed || document.hidden || !runtime.heroVisible || runtime.drawRafId) return;
494‚Üí          runtime.drawRafId = window.requestAnimationFrame(draw);
495‚Üí        };
496‚Üí
497‚Üí        const stopDrawLoop = () => {
498‚Üí          if (!runtime.drawRafId) return;
499‚Üí          window.cancelAnimationFrame(runtime.drawRafId);
500‚Üí          runtime.drawRafId = 0;
501‚Üí        };
502‚Üí
503‚Üí        const updateMockupTime = () => {
504‚Üí          const now = new Date();
505‚Üí          const hour = ((now.getHours() + 11) % 12) + 1;
506‚Üí          const minute = String(now.getMinutes()).padStart(2, "0");
507‚Üí          mockupTimeEl.textContent = `${hour}:${minute}`;
508‚Üí        };
509‚Üí
510‚Üí        const scheduleMinuteTick = () => {
511‚Üí          if (runtime.disposed || document.hidden || !runtime.heroVisible) return;
512‚Üí          updateMockupTime();
513‚Üí          const now = new Date();
514‚Üí          const msUntilNextMinute = ((60 - now.getSeconds()) * 1000) - now.getMilliseconds();
515‚Üí          clearScheduledTimeout(runtime.minuteTimerId);
516‚Üí          runtime.minuteTimerId = scheduleTimeout(() => {
517‚Üí            runtime.minuteTimerId = 0;
518‚Üí            scheduleMinuteTick();
519‚Üí          }, Math.max(200, msUntilNextMinute));
520‚Üí        };
521‚Üí
522‚Üí        const computeNodeCenters = () => {
523‚Üí          const baseRect = lockZone.getBoundingClientRect();
524‚Üí          svgEl.setAttribute("viewBox", `0 0 ${baseRect.width} ${baseRect.height}`);
525‚Üí          svgEl.setAttribute("width", `${baseRect.width}`);
526‚Üí          svgEl.setAttribute("height", `${baseRect.height}`);
527‚Üí          runtime.nodeCenters = dots.map((dot) => {
528‚Üí            const rect = dot.getBoundingClientRect();
529‚Üí            return {
530‚Üí              x: rect.left - baseRect.left + (rect.width / 2),
531‚Üí              y: rect.top - baseRect.top + (rect.height / 2)
532‚Üí            };
533‚Üí          });
534‚Üí        };
535‚Üí
536‚Üí        const setPath = (points) => {
537‚Üí          if (points.length === 0) {
538‚Üí            pathEl.setAttribute("d", "");
539‚Üí            cursorEl.style.opacity = "0";
540‚Üí            return;
541‚Üí          }
542‚Üí          const commands = points.map((point, idx) => `${idx === 0 ? "M" : "L"} ${point.x.toFixed(2)} ${point.y.toFixed(2)}`);
543‚Üí          pathEl.setAttribute("d", commands.join(" "));
544‚Üí          const tail = points[points.length - 1];
545‚Üí          cursorEl.setAttribute("cx", tail.x.toFixed(2));
546‚Üí          cursorEl.setAttribute("cy", tail.y.toFixed(2));
547‚Üí          cursorEl.style.opacity = "1";
548‚Üí        };
549‚Üí
550‚Üí        const sleep = (ms, ticket) => new Promise((resolve) => {
551‚Üí          scheduleTimeout(() => resolve(!runtime.disposed && !document.hidden && runtime.heroVisible && ticket === runtime.patternLoopTicket), ms);
552‚Üí        });
553‚Üí
554‚Üí        const animateSegment = (stablePoints, fromPoint, toPoint, durationMs, ticket) => new Promise((resolve) => {
555‚Üí          const startedAt = performance.now();
556‚Üí          const tick = (now) => {
557‚Üí            if (runtime.disposed || document.hidden || !runtime.heroVisible || ticket !== runtime.patternLoopTicket) {
558‚Üí              resolve(false);
559‚Üí              return;
560‚Üí            }
561‚Üí            const progress = Math.min(1, (now - startedAt) / durationMs);
562‚Üí            const x = fromPoint.x + ((toPoint.x - fromPoint.x) * progress);
563‚Üí            const y = fromPoint.y + ((toPoint.y - fromPoint.y) * progress);
564‚Üí            setPath([...stablePoints, { x, y }]);
565‚Üí            if (progress < 1) {
566‚Üí              window.requestAnimationFrame(tick);
567‚Üí            } else {
568‚Üí              resolve(true);
569‚Üí            }
570‚Üí          };
571‚Üí          window.requestAnimationFrame(tick);
572‚Üí        });
573‚Üí
574‚Üí        const runSequence = async (sequence, ticket) => {
575‚Üí          dots.forEach((dot) => dot.classList.remove("active"));
576‚Üí          setPath([]);
577‚Üí          const points = [];
578‚Üí          computeNodeCenters();
579‚Üí          if (!(await sleep(250, ticket))) return false;
580‚Üí          if (sequence.length === 0) return true;
581‚Üí
582‚Üí          const firstNode = sequence[0];
583‚Üí          dots[firstNode].classList.add("active");
584‚Üí          points.push(runtime.nodeCenters[firstNode]);
585‚Üí          setPath(points);
586‚Üí
587‚Üí          for (let i = 1; i < sequence.length; i += 1) {
588‚Üí            const prevPoint = runtime.nodeCenters[sequence[i - 1]];
589‚Üí            const nextPoint = runtime.nodeCenters[sequence[i]];
590‚Üí            const stablePoints = points.slice(0);
591‚Üí            const segmentOk = await animateSegment(stablePoints, prevPoint, nextPoint, 170, ticket);
592‚Üí            if (!segmentOk) return false;
593‚Üí            dots[sequence[i]].classList.add("active");
594‚Üí            points.push(nextPoint);
595‚Üí            setPath(points);
596‚Üí          }
597‚Üí
598‚Üí          if (!(await sleep(220, ticket))) return false;
599‚Üí          phoneScreen.classList.add("mockup-unlocking");
600‚Üí          if (!(await sleep(980, ticket))) return false;
601‚Üí          phoneScreen.classList.remove("mockup-unlocking");
