:root {
    --app-bg-light: #D1D1E9;
    --app-bg-dark: #20233C;
    --app-surface-light: #FFFFFE;
    --app-surface-dark: #323444;
    --app-text-light: #2B2C34;
    --app-text-dark: #E8E8F0;
    --app-accent-light: #1F0D77;
    --app-accent-dark: #CCC3F8;
    --app-highlight-light: #E45858;
    --app-highlight-dark: #FF6F6F;

    --bg-dark: #20233C;
    --bg-light: #2A2D49;
    --surface-dark: #323444;
    --surface-light: #3A3D55;
    --text-dark: #E8E8F0;
    --text-light: #D6D8EC;
    --accent-dark: #CCC3F8;
    --accent-light: #CCC3F8;
    --highlight-dark: #FF6F6F;
    --highlight-light: #FF6F6F;
    --text-secondary: #D1D2DC;
    --header-bg: rgba(32, 35, 60, 0.8);
    --header-border: rgba(204, 195, 248, 0.22);
    --theme-button-bg: rgba(255, 255, 255, 0.08);
    --theme-button-text: #E8E8F0;
    --pattern-link: #57E37A;
    --phone-frame-bg: #06070E;
    --phone-screen-bg: #20233C;
    --phone-screen-border: rgba(204, 195, 248, 0.18);
    --phone-text-primary: #E8E8F0;
    --phone-text-secondary: rgba(232, 232, 240, 0.78);
    --phone-dot-inactive: rgba(232, 232, 240, 0.39);
    --phone-dot-border: rgba(232, 232, 240, 0.23);
    --phone-dot-active: #CCC3F8;
    --phone-line-color: rgba(204, 195, 248, 0.70);
    --phone-shield-fill: rgba(232, 232, 240, 0.76);
    --phone-shield-cut: #20233C;
    --phone-action-primary: rgba(232, 232, 240, 0.78);
    --phone-action-secondary: rgba(232, 232, 240, 0.55);
    --phone-unlock-pixel: rgba(66, 108, 195, 0.82);
    --phone-unlock-pixel-dim: rgba(66, 108, 195, 0.30);
    --accent-contrast: #20233C;

    --angle-sharp: 0px;
    --border-width: 2px;
    --x-angle: 15deg;
    --font-display: 'Space Grotesk', sans-serif;
    --font-body: 'Plus Jakarta Sans', sans-serif;
    --ease-cinematic: cubic-bezier(0.19, 1, 0.22, 1);
}

:root[data-theme='light'] {
    --bg-dark: #D1D1E9;
    --bg-light: #E3E4F4;
    --surface-dark: #FFFFFE;
    --surface-light: #FFFFFF;
    --text-dark: #2B2C34;
    --text-light: #4D4F67;
    --accent-dark: #1F0D77;
    --accent-light: #1F0D77;
    --highlight-dark: #E45858;
    --highlight-light: #E45858;
    --text-secondary: #6C6F88;
    --header-bg: rgba(209, 209, 233, 0.86);
    --header-border: rgba(31, 13, 119, 0.18);
    --theme-button-bg: rgba(31, 13, 119, 0.09);
    --theme-button-text: #1F0D77;
    --pattern-link: #2E9E4D;
    --phone-frame-bg: #B8BCD2;
    --phone-screen-bg: #D1D1E9;
    --phone-screen-border: rgba(31, 13, 119, 0.22);
    --phone-text-primary: #2B2C34;
    --phone-text-secondary: rgba(43, 44, 52, 0.70);
    --phone-dot-inactive: rgba(43, 44, 52, 0.35);
    --phone-dot-border: rgba(43, 44, 52, 0.21);
    --phone-dot-active: #1F0D77;
    --phone-line-color: rgba(31, 13, 119, 0.70);
    --phone-shield-fill: rgba(43, 44, 52, 0.3);
    --phone-shield-cut: #D1D1E9;
    --phone-action-primary: rgba(43, 44, 52, 0.70);
    --phone-action-secondary: rgba(43, 44, 52, 0.50);
    --phone-unlock-pixel: rgba(31, 13, 119, 0.82);
    --phone-unlock-pixel-dim: rgba(31, 13, 119, 0.30);
    --accent-contrast: #F8F8FF;
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

html {
    scroll-behavior: smooth;
}

body {
    background-color: var(--bg-dark);
    color: var(--text-dark);
    font-family: var(--font-body);
    overflow-x: hidden;
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    transition: background-color 240ms ease, color 240ms ease;
}

h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-display);
    text-transform: uppercase;
    letter-spacing: 0.03em;
}

a { text-decoration: none; color: inherit; transition: 0.3s ease; }
ul { list-style: none; }

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 24px;
    position: relative;
    z-index: 2;
}

.text-gradient {
    background: linear-gradient(135deg, var(--text-dark) 0%, var(--accent-dark) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

.highlight-text {
    color: var(--highlight-dark);
}

/* ‚îÄ‚îÄ Buttons ‚îÄ‚îÄ */
.btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    padding: 18px 36px;
    font-family: var(--font-display);
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    position: relative;
    clip-path: polygon(10px 0, 100% 0, 100% calc(100% - 10px), calc(100% - 10px) 100%, 0 100%, 0 10px);
    transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    border: none;
    cursor: pointer;
}

.btn-primary {
    background-color: var(--accent-dark);
    color: var(--accent-contrast);
}

.btn-nav {
    padding: 12px 24px;
    font-size: 0.9rem;
}

.btn-primary::before {
    content: '';
    position: absolute;
    top: 0; left: 0; width: 100%; height: 100%;
    background: rgba(255,255,255,0.2);
    transform: translateX(-100%);
    transition: transform 0.4s;
    z-index: 1;
}

.btn-primary:hover::before {
    transform: translateX(0);
}

.btn-primary:hover {
    background-color: var(--accent-dark);
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(31, 13, 119, 0.3);
}

.btn-secondary {
    background: transparent;
    border: 2px solid var(--text-dark);
    color: var(--text-dark);
}

.btn-secondary:hover {
    background: var(--surface-dark);
    border-color: var(--accent-dark);
}

/* ‚îÄ‚îÄ Header / Nav ‚îÄ‚îÄ */
header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    z-index: 1000;
    background: var(--header-bg);
    backdrop-filter: blur(12px);
    border-bottom: 1px solid var(--header-border);
    transition: background-color 240ms ease, border-color 240ms ease;
}

.nav-inner {
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 80px;
    gap: 16px;
}

.logo {
    font-family: var(--font-display);
    font-weight: 700;
    font-size: 1.25rem;
    letter-spacing: 0.01em;
    color: var(--text-dark);
    display: flex;
    align-items: center;
    gap: 10px;
}

.logo .logo-mark {
    width: 28px;
    height: 28px;
    border-radius: 8px;
    display: block;
    box-shadow: 0 6px 14px color-mix(in srgb, var(--bg-dark) 68%, transparent 32%);
}

.logo .logo-mark--light {
    display: none;
}

:root[data-theme='light'] .logo .logo-mark--dark {
    display: none;
}

:root[data-theme='light'] .logo .logo-mark--light {
    display: block;
}

.logo span {
    color: var(--text-dark);
}

.nav-links {
    display: flex;
    gap: 32px;
    align-items: center;
    justify-content: center;
    flex-wrap: nowrap;
    flex: 1 1 auto;
    min-width: 0;
}

.nav-links a {
    font-size: 0.9rem;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    opacity: 0.8;
    position: relative;
    white-space: nowrap;
}

.nav-links a::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--accent-dark);
    transition: width 0.3s;
}

.nav-links a:hover::after {
    width: 100%;
}

.nav-cta {
    display: flex;
    align-items: center;
    gap: 12px;
    flex-shrink: 0;
}

.theme-toggle {
    border: 1px solid var(--header-border);
    background: var(--theme-button-bg);
    color: var(--theme-button-text);
    height: 44px;
    min-width: 44px;
    padding: 0 14px;
    border-radius: 999px;
    font: 600 0.82rem var(--font-body);
    display: inline-flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
    transition: transform 180ms ease, background-color 180ms ease, border-color 180ms ease;
}

.theme-toggle:hover {
    transform: translateY(-1px);
}

.theme-toggle:focus-visible {
    outline: 2px solid var(--accent-dark);
    outline-offset: 2px;
}

.theme-icon {
    width: 16px;
    height: 16px;
    flex: 0 0 16px;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    line-height: 0;
}

.theme-icon svg {
    width: 16px;
    height: 16px;
    display: block;
}

/* ‚îÄ‚îÄ Hero ‚îÄ‚îÄ */
.hero {
    position: relative;
    min-height: 100vh;
    display: flex;
    align-items: center;
    padding-top: max(108px, calc(80px + env(safe-area-inset-top)));
    overflow: hidden;
}

.hero-bg-x {
    position: absolute;
    top: 0; left: 0; width: 100%; height: 100%;
    z-index: 0;
    pointer-events: none;
}

.slash-1 {
    position: absolute;
    width: 200%;
    height: 2px;
    top: 0;
    left: -50%;
    transform: rotate(35deg);
    transform-origin: center;
    background: linear-gradient(90deg, transparent, var(--highlight-dark), transparent);
    opacity: 0.8;
}
.slash-2 {
    position: absolute;
    width: 200%;
    height: 2px;
    top: 0;
    left: -50%;
    transform: rotate(-35deg);
    transform-origin: center;
    background: linear-gradient(90deg, transparent, var(--highlight-dark), transparent);
    opacity: 0.8;
}

#pattern-canvas {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 0;
    opacity: 0.3;
}

.hero-content {
    position: relative;
    z-index: 2;
    width: 100%;
}

/* Hero: Two-column grid ‚Äî stacked title (724b) + phone mockup (9bff) */
.hero-layout {
    display: grid;
    grid-template-columns: 1.2fr 1fr;
    align-items: center;
    gap: 40px;
    width: 100%;
}

/* Stacked title from design-724b */
.hero-title-stack {
    font-family: var(--font-display);
    font-size: clamp(3.5rem, 7vw, 7rem);
    line-height: 0.85;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: -0.03em;
    display: flex;
    flex-direction: column;
}

.stack-layer {
    display: block;
    transition: transform 0.5s var(--ease-cinematic);
}

.stack-1 {
    color: transparent;
    -webkit-text-stroke: 1px var(--text-secondary);
    opacity: 0.5;
    transform: translateX(-20px);
}

.stack-2 {
    color: var(--accent-dark);
    opacity: 0.8;
}

.stack-3 {
    color: var(--text-dark);
    position: relative;
    z-index: 10;
}

.hero-sub {
    margin-top: 2rem;
    font-size: 1.25rem;
    color: var(--text-dark);
    opacity: 0.8;
    max-width: 600px;
    border-left: 2px solid var(--highlight-dark);
    padding-left: 20px;
}

.hero-actions {
    display: flex;
    gap: 20px;
    flex-wrap: wrap;
    margin-top: 48px;
}

/* ‚îÄ‚îÄ Phone Mockup ‚îÄ‚îÄ */
.mockup-container {
    position: relative;
    --mockup-h: min(700px, calc(100vh - 190px));
    width: min(336px, calc(var(--mockup-h) * 0.48), 100%);
    aspect-ratio: 336 / 700;
    height: var(--mockup-h);
    max-height: 700px;
    max-width: 100%;
    margin-left: auto;
}

.phone-frame {
    width: 100%;
    height: 100%;
    background: var(--phone-frame-bg);
    border-radius: 46px;
    padding: 10px;
    box-shadow: 0 38px 90px rgba(0, 0, 0, 0.46), 0 0 0 1px rgba(255, 255, 255, 0.06);
    position: relative;
    transition: transform 220ms ease;
}

.phone-frame:hover {
    transform: translateY(-2px);
}

.phone-screen {
    width: 100%;
    height: 100%;
    background: var(--phone-screen-bg);
    border-radius: 36px;
    overflow: hidden;
    position: relative;
    border: 1px solid var(--phone-screen-border);
    padding: 12px 12px 18px;
}

.phone-status {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.88rem;
    font-weight: 600;
    margin: 2px 8px 8px;
    color: var(--phone-text-primary);
    opacity: 0.9;
    position: relative;
    min-height: 28px;
    z-index: 5;
}

.phone-dynamic-island {
    position: absolute;
    left: 50%;
    top: 1px;
    transform: translateX(-50%);
    width: min(92px, 32%);
    height: 24px;
    border-radius: 999px;
    background: #05060A;
}

.status-right {
    display: inline-flex;
    align-items: center;
    gap: 5px;
    flex-shrink: 0;
}

.status-icon-svg {
    width: 14px;
    height: 14px;
    flex-shrink: 0;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    color: currentColor;
    opacity: 0.95;
}

.status-icon-svg svg {
    width: 100%;
    height: 100%;
    display: block;
}

.status-battery {
    width: 20px;
    height: 10px;
    flex-shrink: 0;
    border-radius: 2px;
    border: 1px solid color-mix(in srgb, var(--phone-text-primary) 62%, transparent 38%);
    position: relative;
}

.status-battery::before {
    content: '';
    position: absolute;
    right: -3px;
    top: 2px;
    width: 2px;
    height: 4px;
    border-radius: 1px;
    background: color-mix(in srgb, var(--phone-text-primary) 62%, transparent 38%);
}

.status-battery::after {
    content: '';
    position: absolute;
    top: 1px;
    left: 1px;
    width: 12px;
    height: 6px;
    border-radius: 1px;
    background: color-mix(in srgb, var(--phone-dot-active) 65%, var(--phone-screen-bg) 35%);
}

.mockup-stage {
    position: absolute;
    inset: 46px 12px 18px;
    border-radius: 28px;
    transition: opacity 220ms ease;
}

.mockup-lock-state {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    opacity: 1;
    pointer-events: auto;
}

.mockup-unlock-state {
    opacity: 0;
    pointer-events: none;
    display: grid;
    place-items: center;
}

.mockup-vault-state {
    opacity: 0;
    pointer-events: none;
    display: flex;
    flex-direction: column;
    gap: 10px;
    padding: 2px 2px 0;
    min-height: 0;
}

.phone-screen.mockup-unlocking .mockup-lock-state {
    opacity: 0;
    pointer-events: none;
}

.phone-screen.mockup-unlocking .mockup-unlock-state {
    opacity: 1;
}

.phone-screen.mockup-vault .mockup-lock-state,
.phone-screen.mockup-vault .mockup-unlock-state {
    opacity: 0;
    pointer-events: none;
}

.phone-screen.mockup-vault .mockup-vault-state {
    opacity: 1;
    pointer-events: auto;
}

.lock-header {
    margin-top: 70px;
    display: grid;
    gap: 10px;
    place-items: center;
}

.lock-shield {
    width: 44px;
    height: 44px;
    border-radius: 12px;
    background: color-mix(in srgb, var(--phone-text-primary) 10%, transparent 90%);
    display: grid;
    place-items: center;
    color: color-mix(in srgb, var(--phone-text-primary) 78%, transparent 22%);
    font-size: 1rem;
}

.lock-title {
    font-family: var(--font-body);
    font-size: 1.25rem;
    font-weight: 600;
    text-transform: none;
    letter-spacing: -0.01em;
    color: var(--phone-text-primary);
    margin: 0;
}

.pattern-lock-zone {
    width: 226px;
    height: 226px;
    position: relative;
    margin-top: 50px;
    margin-bottom: 8px;
}

.pattern-grid {
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    gap: 34px;
    position: relative;
    z-index: 5;
}

.grid-dot {
    width: 18px;
    height: 18px;
    border-radius: 50%;
    background: var(--phone-dot-inactive);
    border: 1px solid var(--phone-dot-border);
    transition: background-color 180ms ease, box-shadow 180ms ease;
}

.grid-dot.active {
    background: var(--phone-dot-active);
    border-color: transparent;
    box-shadow: 0 0 0 2px color-mix(in srgb, var(--phone-dot-active) 30%, transparent 70%);
}

.pattern-svg {
    position: absolute;
    inset: 0;
    pointer-events: none;
    z-index: 4;
}

.pattern-line {
    stroke: var(--phone-line-color);
    stroke-width: 4;
    stroke-linecap: round;
    stroke-linejoin: round;
    fill: none;
    filter: drop-shadow(0 0 6px color-mix(in srgb, var(--phone-dot-active) 52%, transparent 48%));
}

.pattern-cursor {
    fill: var(--phone-dot-active);
    filter: drop-shadow(0 0 3px color-mix(in srgb, var(--phone-dot-active) 62%, transparent 38%));
    opacity: 0;
    transition: opacity 120ms ease;
}

.lock-actions {
    margin-top: auto;
    margin-bottom: 20px;
    display: grid;
    gap: 12px;
    place-items: center;
}

.lock-action {
    color: var(--phone-text-secondary);
    font-size: 0.95rem;
    font-weight: 500;
    opacity: 0.85;
}

.lock-action-primary {
    opacity: 1;
    color: var(--phone-action-primary);
}

.lock-action-secondary {
    opacity: 1;
    color: var(--phone-action-secondary);
}

.unlock-wrap {
    display: grid;
    gap: 14px;
    place-items: center;
    transform: translateY(-8px);
}

.unlock-pixel {
    width: 14px;
    height: 14px;
    border-radius: 2px;
    background: var(--phone-unlock-pixel-dim);
    animation: unlockPulse 1s infinite ease-in-out;
}

.unlock-text {
    color: var(--phone-text-secondary);
    font-size: 1.02rem;
    font-weight: 500;
}

.vault-app-header {
    width: 100%;
    display: grid;
    grid-template-columns: 34px 1fr 34px;
    align-items: center;
    gap: 8px;
    margin-top: 2px;
}

.vault-app-btn {
    width: 32px;
    height: 32px;
    border-radius: 50%;
    border: 1px solid color-mix(in srgb, var(--phone-text-primary) 22%, transparent 78%);
    background: color-mix(in srgb, var(--phone-text-primary) 8%, transparent 92%);
    color: var(--phone-text-primary);
    display: grid;
    place-items: center;
    font-size: 0.84rem;
}

.vault-app-title {
    margin: 0;
    color: var(--phone-text-primary);
    font-size: 0.92rem;
    font-weight: 600;
    text-align: center;
}

.vault-search-row {
    width: 100%;
    display: grid;
    grid-template-columns: 1fr 30px 30px;
    gap: 6px;
    align-items: center;
    margin-top: 2px;
}

.vault-search {
    height: 32px;
    border-radius: 9px;
    border: 1px solid color-mix(in srgb, var(--phone-text-primary) 14%, transparent 86%);
    background: color-mix(in srgb, var(--phone-text-primary) 6%, transparent 94%);
    color: var(--phone-text-secondary);
    font-size: 0.82rem;
    display: flex;
    align-items: center;
    padding: 0 10px;
}

.vault-search-icon {
    margin-right: 6px;
    opacity: 0.7;
    font-size: 0.72rem;
}

.vault-chip {
    height: 30px;
    border-radius: 8px;
    border: 1px solid color-mix(in srgb, var(--phone-text-primary) 18%, transparent 82%);
    background: color-mix(in srgb, var(--phone-text-primary) 8%, transparent 92%);
    color: var(--phone-text-secondary);
    display: grid;
    place-items: center;
    font-size: 0.78rem;
}

.vault-section-label {
    color: var(--phone-text-secondary);
    font-size: 0.82rem;
    font-weight: 600;
    margin-top: 1px;
    padding-left: 2px;
    margin-bottom: 2px;
}

.vault-grid {
    width: 100%;
    flex: 1;
    min-height: 0;
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: repeat(6, minmax(0, 1fr));
    gap: 2px;
    border-radius: 6px;
    overflow: hidden;
    align-content: stretch;
}

.vault-cell {
    background: color-mix(in srgb, var(--phone-text-primary) 6%, transparent 94%);
    position: relative;
    border: 1px solid color-mix(in srgb, var(--phone-text-primary) 8%, transparent 92%);
    overflow: hidden;
}

.vault-thumb {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
    filter: saturate(1.08) contrast(1.02);
}

.vault-cell::after {
    content: '';
    position: absolute;
    inset: 0;
    background: linear-gradient(180deg, transparent 58%, rgba(8, 10, 20, 0.35) 100%);
    pointer-events: none;
}

.vault-cell-time {
    position: absolute;
    left: 3px;
    bottom: 3px;
    font-size: 0.54rem;
    color: rgba(255, 255, 255, 0.9);
    background: rgba(20, 22, 36, 0.75);
    border-radius: 3px;
    padding: 0 3px;
    z-index: 2;
}

.vault-fab {
    position: absolute;
    right: 10px;
    bottom: 12px;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    border: none;
    background: color-mix(in srgb, var(--phone-dot-active) 84%, white 16%);
    color: color-mix(in srgb, var(--phone-screen-bg) 82%, black 18%);
    padding: 0;
    display: grid;
    place-items: center;
    box-shadow: 0 6px 14px color-mix(in srgb, var(--phone-dot-active) 32%, transparent 68%);
}

.vault-fab-plus {
    position: relative;
    width: 16px;
    height: 16px;
    display: block;
}

.vault-fab-plus::before,
.vault-fab-plus::after {
    content: '';
    position: absolute;
    left: 50%;
    top: 50%;
    width: 16px;
    height: 3px;
    border-radius: 3px;
    background: currentColor;
    transform: translate(-50%, -50%);
}

.vault-fab-plus::after {
    transform: translate(-50%, -50%) rotate(90deg);
}

/* ‚îÄ‚îÄ Trust Strip ‚îÄ‚îÄ */
.trust-strip {
    background: var(--highlight-light);
    padding: 24px 0;
    position: relative;
    z-index: 3;
    transform: skewY(-2deg);
    margin-top: 0;
    border-top: 1px solid var(--surface-light);
    border-bottom: 1px solid var(--surface-light);
}

.trust-content {
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
    transform: skewY(2deg);
    gap: 20px;
}

.trust-item {
    font-family: var(--font-display);
    font-weight: 700;
    text-transform: uppercase;
    color: var(--surface-light);
    font-size: 1.1rem;
    display: flex;
    align-items: center;
    gap: 10px;
}

.trust-item::before {
    content: '\00d7';
    color: var(--bg-dark);
    font-size: 1.5rem;
    font-weight: 900;
}

/* ‚îÄ‚îÄ Features ‚îÄ‚îÄ */
.features {
    padding: 170px 0 120px;
    background: var(--bg-light);
    color: var(--text-light);
    position: relative;
    margin-top: -50px;
    clip-path: polygon(0 50px, 100% 0, 100% calc(100% - 50px), 0 100%);
}

.section-header {
    margin-bottom: 80px;
    text-align: center;
}

.section-header h2 {
    font-size: 3rem;
    color: var(--text-dark);
    margin-bottom: 16px;
}

.section-header-subtitle {
    color: var(--text-light);
    max-width: 600px;
    margin: 0 auto;
}

.feature-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 30px;
}

.feature-card {
    background: var(--surface-light);
    padding: 40px;
    position: relative;
    clip-path: polygon(
        20px 0, 100% 0,
        100% calc(100% - 20px),
        calc(100% - 20px) 100%,
        0 100%, 0 20px
    );
    transition: transform 0.3s ease;
    box-shadow: 0 20px 40px rgba(32, 35, 60, 0.1);
}

.feature-card:hover {
    transform: translateY(-10px);
}

.feature-card::after {
    content: '';
    position: absolute;
    top: 0; right: 0;
    width: 40px;
    height: 40px;
    background: linear-gradient(to bottom left, var(--highlight-light) 50%, transparent 50%);
}

.feature-icon {
    font-size: 2rem;
    margin-bottom: 24px;
    color: var(--accent-light);
}

.feature-card h3 {
    font-size: 1.5rem;
    margin-bottom: 16px;
    color: var(--text-dark);
}

.feature-card p {
    color: var(--text-light);
    font-size: 1rem;
    line-height: 1.7;
}

/* ‚îÄ‚îÄ Timeline ‚îÄ‚îÄ */
.timeline-section {
    padding: 120px 0;
    background: var(--bg-dark);
}

.timeline {
    position: relative;
    max-width: 800px;
    margin: 0 auto;
}

.timeline::before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    bottom: 0;
    width: 2px;
    background: rgba(255,255,255,0.1);
    transform: translateX(-50%);
}

.step {
    position: relative;
    margin-bottom: 80px;
    width: 50%;
}

.step:nth-child(odd) {
    left: 0;
    padding-right: 60px;
    text-align: right;
}

.step:nth-child(even) {
    left: 50%;
    padding-left: 60px;
}

.step-marker {
    position: absolute;
    top: 0;
    width: 20px;
    height: 20px;
    background: var(--bg-dark);
    border: 2px solid var(--highlight-dark);
    transform: rotate(45deg);
    z-index: 2;
}

.step:nth-child(odd) .step-marker {
    right: -11px;
}

.step:nth-child(even) .step-marker {
    left: -11px;
}

.step-number {
    font-family: var(--font-display);
    font-size: 4rem;
    color: rgba(255,255,255,0.05);
    font-weight: 800;
    position: absolute;
    top: -20px;
    line-height: 1;
}

.step:nth-child(odd) .step-number { right: 40px; }
.step:nth-child(even) .step-number { left: 40px; }

.step h3 {
    font-size: 1.8rem;
    margin-bottom: 12px;
    color: var(--text-dark);
    position: relative;
}

.step p {
    color: var(--accent-dark);
}

/* ‚îÄ‚îÄ Security ‚îÄ‚îÄ */
.security-section {
    background: var(--surface-dark);
    padding: 100px 0;
    border-top: 1px solid rgba(255,255,255,0.05);
}

.tech-specs {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    align-items: center;
}

.tech-diagram {
    background: var(--bg-dark);
    border: 1px solid var(--accent-light);
    padding: 40px;
    position: relative;
}

.tech-diagram::before, .tech-diagram::after {
    content: '';
    position: absolute;
    width: 20px;
    height: 20px;
    border-color: var(--highlight-light);
    border-style: solid;
    transition: all 0.5s;
}
.tech-diagram::before { top: -1px; left: -1px; border-width: 2px 0 0 2px; }
.tech-diagram::after { bottom: -1px; right: -1px; border-width: 0 2px 2px 0; }

.code-block {
    font-family: monospace;
    color: var(--accent-dark);
    line-height: 1.8;
    font-size: 0.9rem;
}

.code-key { color: var(--highlight-dark); }
.code-val { color: var(--text-dark); }
.code-comment { color: var(--text-secondary); }

.tech-content h3 {
    font-size: 2.5rem;
    margin-bottom: 24px;
}

.tech-lead {
    color: var(--accent-dark);
    font-size: 1.1rem;
}

.tech-content ul {
    margin-top: 30px;
}

.tech-content li {
    margin-bottom: 16px;
    padding-left: 24px;
    position: relative;
    color: var(--text-dark);
}

.tech-content li::before {
    content: '/';
    color: var(--highlight-light);
    position: absolute;
    left: 0;
    font-weight: bold;
}

/* ‚îÄ‚îÄ FAQ ‚îÄ‚îÄ */
.faq-section {
    padding: 120px 0;
    background: var(--bg-dark);
}

.faq-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 24px;
    width: min(100%, 800px);
    max-width: 800px;
    margin: 0 auto;
}

.faq-item {
    border: 1px solid color-mix(in srgb, var(--text-dark) 12%, transparent 88%);
    background: color-mix(in srgb, var(--surface-dark) 58%, transparent 42%);
    transition: 0.3s;
}

.faq-item[open] {
    border-color: var(--highlight-light);
    background: var(--surface-dark);
}

.faq-summary {
    padding: 24px;
    cursor: pointer;
    font-family: var(--font-display);
    font-size: 1.2rem;
    font-weight: 500;
    display: flex;
    justify-content: space-between;
    align-items: center;
    list-style: none;
}

.faq-summary::-webkit-details-marker { display: none; }

.faq-summary::after {
    content: '+';
    font-size: 1.5rem;
    color: var(--highlight-dark);
    transition: transform 0.3s;
}

.faq-item[open] .faq-summary::after {
    transform: rotate(45deg);
}

.faq-content {
    padding: 0 24px 24px 24px;
    color: var(--text-dark);
    opacity: 0.9;
}

/* ‚îÄ‚îÄ Final CTA ‚îÄ‚îÄ */
.final-cta {
    padding: 140px 0;
    text-align: center;
    background: radial-gradient(circle at center, var(--accent-light) 0%, var(--bg-dark) 70%);
    position: relative;
    overflow: hidden;
}

.final-cta h2 {
    font-size: 4rem;
    margin-bottom: 30px;
    line-height: 1;
}

/* ‚îÄ‚îÄ Footer ‚îÄ‚îÄ */
footer {
    background: color-mix(in srgb, var(--bg-dark) 82%, #07080D 18%);
    padding: 60px 0;
    font-size: 0.9rem;
    border-top: 1px solid color-mix(in srgb, var(--text-dark) 14%, transparent 86%);
}

.footer-content {
    display: flex;
    justify-content: flex-start;
    align-items: flex-start;
    flex-wrap: wrap;
    gap: 18px 24px;
}

.footer-brand {
    width: 100%;
}

.footer-links {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    gap: 8px 24px;
}

.footer-links a {
    margin: 0;
    color: var(--accent-dark);
}
.footer-links a:hover { color: var(--highlight-dark); }

.footer-compare {
    width: 100%;
    padding-top: 20px;
    margin-top: 6px;
    border-top: 1px solid color-mix(in srgb, var(--text-dark) 8%, transparent 92%);
    display: grid;
    grid-template-columns: repeat(5, minmax(0, 1fr));
    gap: 8px 14px;
    font-size: 0.82rem;
}

.footer-compare a {
    color: var(--text-secondary);
    transition: color .2s;
    text-align: left;
}

.footer-compare a:hover {
    color: var(--accent-dark);
}

/* ‚îÄ‚îÄ Grid Overlay ‚îÄ‚îÄ */
.grid-overlay {
    position: absolute;
    width: 100%; height: 100%;
    background-image: radial-gradient(var(--text-dark) 1px, transparent 1px);
    background-size: 40px 40px;
    opacity: 0.05;
    z-index: 1;
    pointer-events: none;
}

/* ‚îÄ‚îÄ Mobile Expanding Navigation ‚îÄ‚îÄ */

.mobile-menu-btn {
    display: none;
    width: 44px;
    height: 44px;
    background: var(--theme-button-bg);
    border: 1px solid var(--header-border);
    border-radius: 999px;
    cursor: pointer;
    align-items: center;
    justify-content: center;
    padding: 0;
    transition: background-color 180ms ease, border-color 180ms ease;
    flex-shrink: 0;
}

.mobile-menu-btn:hover {
    background: color-mix(in srgb, var(--accent-dark) 12%, transparent 88%);
}

.mobile-menu-btn svg {
    width: 18px;
    height: 18px;
    stroke: var(--text-dark);
    stroke-width: 1.8;
    fill: none;
    stroke-linecap: round;
}

.mobile-menu-btn .icon-close { display: none; }
.mobile-menu-btn.open .icon-menu { display: none; }
.mobile-menu-btn.open .icon-close { display: block; }

.mobile-nav-expand {
    display: none;
    overflow: hidden;
    max-height: 0;
    border-top: 1px solid transparent;
    transition:
        max-height 520ms cubic-bezier(0.16, 1, 0.3, 1),
        border-color 300ms ease;
}

.mobile-nav-expand.open {
    max-height: 520px;
    border-top-color: var(--header-border);
}

.mobile-nav-items {
    padding: 8px 0 20px;
    display: flex;
    flex-direction: column;
    gap: 1px;
}

.mobile-nav-item {
    display: flex;
    align-items: center;
    gap: 14px;
    padding: 13px 8px;
    border-radius: 10px;
    text-decoration: none;
    color: var(--text-dark);
    opacity: 0;
    transform: translateY(10px);
    transition: background-color 200ms ease;
}

.mobile-nav-item:hover,
.mobile-nav-item:active {
    background: color-mix(in srgb, var(--accent-dark) 8%, transparent 92%);
}

.mobile-nav-expand.open .mobile-nav-item {
    opacity: 1;
    transform: translateY(0);
    transition:
        opacity 380ms cubic-bezier(0.16, 1, 0.3, 1),
        transform 460ms cubic-bezier(0.34, 1.56, 0.64, 1),
        background-color 200ms ease;
}

.mobile-nav-expand.open .mobile-nav-item:nth-child(1) { transition-delay: 50ms, 50ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(2) { transition-delay: 100ms, 100ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(3) { transition-delay: 150ms, 150ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(4) { transition-delay: 200ms, 200ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(5) { transition-delay: 250ms, 250ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(6) { transition-delay: 300ms, 300ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(7) { transition-delay: 350ms, 350ms, 0ms; }

.mobile-nav-expand:not(.open) .mobile-nav-item {
    transition:
        opacity 200ms ease,
        transform 200ms ease,
        background-color 200ms ease;
    transition-delay: 0ms;
}

.mobile-nav-icon {
    width: 38px;
    height: 38px;
    border-radius: 9px;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
    background: color-mix(in srgb, var(--accent-dark) 10%, transparent 90%);
}

.mobile-nav-icon svg {
    width: 18px;
    height: 18px;
    stroke-width: 1.8;
    stroke-linecap: round;
    stroke-linejoin: round;
    fill: none;
}

.mobile-nav-label {
    display: flex;
    flex-direction: column;
    gap: 0px;
    min-width: 0;
}

.mobile-nav-label-title {
    font-family: var(--font-display);
    font-size: 0.92rem;
    font-weight: 600;
    letter-spacing: 0.02em;
    text-transform: uppercase;
}

.mobile-nav-label-desc {
    font-size: 0.78rem;
    color: var(--text-secondary);
    line-height: 1.3;
}

.mobile-nav-separator {
    height: 1px;
    background: var(--header-border);
    margin: 6px 8px;
}

.mobile-nav-cta {
    padding: 4px 8px 0;
}

.mobile-nav-cta .btn {
    width: 100%;
    text-align: center;
    justify-content: center;
    padding: 14px 24px;
    font-size: 0.9rem;
}

main {
    transition: transform 520ms cubic-bezier(0.16, 1, 0.3, 1);
}

body.mobile-nav-open main {
    transform: translateY(var(--mobile-nav-h, 0px));
}

body.mobile-nav-open footer {
    transform: translateY(var(--mobile-nav-h, 0px));
}

footer {
    transition: transform 520ms cubic-bezier(0.16, 1, 0.3, 1);
}

/* ‚îÄ‚îÄ Responsive ‚îÄ‚îÄ */
@media (max-width: 900px) {
    .hero-layout {
        grid-template-columns: 1fr;
        text-align: center;
    }
    .hero-sub { border-left: none; padding-left: 0; margin-inline: auto; }
    .hero-actions { justify-content: center; }
    .mockup-container {
        --mockup-h: clamp(320px, calc(100vh - 260px), 700px);
        margin: 24px auto 28px;
        height: var(--mockup-h);
        width: min(100%, calc(var(--mockup-h) * 0.48));
        max-width: 336px;
    }
    .hero-title-stack { align-items: center; }
    .stack-1 { transform: translateX(0); }
}

@media (max-width: 1360px) {
    .nav-links {
        gap: 24px;
    }

    .nav-links a {
        font-size: 0.84rem;
        letter-spacing: 0.08em;
    }

    .nav-cta {
        gap: 10px;
    }
}

@media (max-width: 1160px) {
    .nav-links {
        display: none;
    }
    .mobile-menu-btn {
        display: flex;
    }
    .mobile-nav-expand {
        display: block;
    }
    .nav-cta .btn-nav {
        display: none;
    }
    .theme-toggle #theme-label {
        display: none;
    }
    .theme-toggle {
        width: 44px;
        min-width: 44px;
        padding: 0;
        justify-content: center;
        gap: 0;
    }
}

@media (max-width: 768px) {
    .nav-inner {
        height: 74px;
    }
    .hero {
        min-height: auto;
        padding-top: max(112px, calc(74px + env(safe-area-inset-top) + 28px));
        padding-bottom: 40px;
    }
    .hero-layout {
        gap: 24px;
    }
    .hero-title-stack { font-size: clamp(2.5rem, 8vw, 4rem); }
    .hero-sub {
        margin-top: 1.35rem;
        font-size: 1.05rem;
        line-height: 1.5;
        max-width: 36ch;
    }
    .hero-actions {
        margin-top: 28px;
        width: 100%;
        gap: 12px;
    }
    .hero-actions .btn {
        width: 100%;
    }
    .timeline::before { left: 20px; }
    .step { width: 100%; padding-left: 60px !important; text-align: left !important; left: 0 !important; }
    .step:nth-child(odd) .step-marker, .step:nth-child(even) .step-marker { left: 9px; }
    .step-number { left: 60px !important; }
    .tech-specs { grid-template-columns: 1fr; }
    .nav-links { display: none; }
    .mobile-menu-btn { display: flex; }
    .mobile-nav-expand { display: block; }
    .nav-cta .btn-nav { display: none; }
    .nav-cta {
        gap: 10px;
    }
    .nav-cta .btn {
        padding: 10px 16px !important;
        font-size: 0.82rem !important;
        letter-spacing: 0.03em;
    }
    .logo {
        font-size: 1.1rem;
    }
    .logo .logo-mark {
        width: 24px;
        height: 24px;
        border-radius: 7px;
    }
    .trust-content { flex-direction: column; }
    .trust-strip {
        margin-top: 0;
    }
    .theme-toggle #theme-label {
        display: none;
    }
    .theme-toggle {
        width: 44px;
        min-width: 44px;
        padding: 0;
        justify-content: center;
        gap: 0;
    }
    .features {
        margin-top: -36px;
        padding-top: 136px;
        clip-path: polygon(0 36px, 100% 0, 100% calc(100% - 40px), 0 100%);
    }
    .mockup-container {
        width: min(100%, 340px);
        height: auto;
        margin: 20px auto 32px;
    }
    .pattern-lock-zone {
        width: 218px;
        height: 218px;
        margin-top: 42px;
    }
    .lock-title {
        font-size: 1.15rem;
    }

    .footer-links {
        display: grid;
        grid-template-columns: 1fr;
        gap: 8px;
    }

    .footer-compare {
        grid-template-columns: 1fr;
        gap: 6px;
    }
}

@media (max-height: 820px) and (min-width: 901px) {
    .hero {
        padding-bottom: 56px;
    }

    .trust-strip {
        margin-top: 0;
    }
}

@media (max-height: 700px) and (max-width: 900px) {
    .hero {
        padding-bottom: 56px;
    }

    .trust-strip {
        margin-top: 0;
    }
}

@keyframes loaderPulse {
    0% {
        opacity: 0.45;
        transform: scale(0.86);
    }
    50% {
        opacity: 1;
        transform: scale(1);
        background: var(--app-accent-dark);
    }
    100% {
        opacity: 0.45;
        transform: scale(0.86);
    }
}

@keyframes unlockPulse {
    0% {
        opacity: 0.42;
        transform: scale(0.86);
        background: var(--phone-unlock-pixel-dim);
    }
    50% {
        opacity: 1;
        transform: scale(1);
        background: var(--phone-unlock-pixel);
    }
    100% {
        opacity: 0.42;
        transform: scale(0.86);
        background: var(--phone-unlock-pixel-dim);
    }
}

@keyframes pulse {
    0% { opacity: 0.3; }
    50% { opacity: 0.6; }
    100% { opacity: 0.3; }
}

