@import "tailwindcss";
@import "./site-shared.css";
@import "../compare/styles.css";
@import "./pages/home.css";
@import "./pages/manifesto.css";
@import "./pages/privacy.css";
@import "./pages/terms.css";
@import "./pages/invite.css";
@import "./pages/features.css";

@theme {
  /* Vaultaire design tokens (matching iOS app) */
  --color-vault-bg: #D1D1E9;
  --color-vault-bg-dark: #1A1B2E;
  --color-vault-surface: #FFFFFE;
  --color-vault-surface-dark: #2D2E3A;
  --color-vault-text: #2B2C34;
  --color-vault-text-dark: #E8E8F0;
  --color-vault-secondary: #2B2C34B3;
  --color-vault-secondary-dark: #E8E8F0B3;
  --color-vault-accent: #6246EA;
  --color-vault-highlight: #E45858;
  --color-vault-highlight-dark: #FF6F6F;
}

@layer base {
  html {
    scroll-behavior: smooth;
    -webkit-font-smoothing: antialiased;
  }
}

