.page-privacy main {
  padding: 118px 0 64px;
}

.page-privacy .legal-card {
  background: color-mix(in srgb, var(--surface) 94%, transparent 6%);
  border: 1px solid color-mix(in srgb, var(--text) 10%, transparent 90%);
  border-radius: 24px;
  padding: 36px;
  max-width: 980px;
  margin: 0 auto;
}

.page-privacy h1 {
  margin: 0;
  font-family: var(--font-display);
  font-size: clamp(2rem, 4.2vw, 2.8rem);
  line-height: 1.1;
  letter-spacing: 0.02em;
}

.page-privacy .updated {
  color: var(--text-secondary);
  margin: 10px 0 28px;
  font-weight: 500;
}

.page-privacy section {
  margin-bottom: 28px;
}

.page-privacy h2 {
  margin: 0 0 10px;
  font-size: 1.12rem;
  font-weight: 700;
  color: var(--text);
  letter-spacing: 0.02em;
}

.page-privacy p,
.page-privacy li {
  color: var(--text-secondary);
  margin: 0;
  font-size: 1rem;
  line-height: 1.62;
}

.page-privacy ul {
  margin: 0;
  padding-left: 20px;
  display: grid;
  gap: 8px;
}

.page-privacy .legal-mt-10 {
  margin-top: 10px;
}

.page-privacy .legal-section-last {
  margin-bottom: 0;
}

.page-privacy a {
  color: var(--accent);
  text-underline-offset: 3px;
}

@media (max-width: 768px) {
  .page-privacy .legal-card {
    padding: 24px 18px;
    border-radius: 18px;
  }
}

