file.content:
@import "tailwindcss";
@import "./site-shared.css";
@import "../compare/styles.css";
@import "./pages/home.css";
@import "./pages/manifesto.css";
@import "./pages/privacy.css";
@import "./pages/terms.css";
@import "./pages/invite.css";
@import "./pages/features.css";

@theme {
  /* Vaultaire design tokens (matching iOS app) */
  --color-vault-bg: #D1D1E9;
  --color-vault-bg-dark: #1A1B2E;
  --color-vault-surface: #FFFFFE;
  --color-vault-surface-dark: #2D2E3A;
  --color-vault-text: #2B2C34;
  --color-vault-text-dark: #E8E8F0;
  --color-vault-secondary: #2B2C34B3;
  --color-vault-secondary-dark: #E8E8F0B3;
  --color-vault-accent: #6246EA;
  --color-vault-highlight: #E45858;
  --color-vault-highlight-dark: #FF6F6F;
}

@layer base {
  html {
    scroll-behavior: smooth;
    -webkit-font-smoothing: antialiased;
  }
}

content.content:
  1→:root {
  2→  --bg: #20233C;
  3→  --surface: #323444;
  4→  --text: #E8E8F0;
  5→  --text-secondary: #D1D2DC;
  6→  --accent: #CCC3F8;
  7→  --highlight: #FF6F6F;
  8→  --accent-contrast: #20233C;
  9→  --header-bg: rgba(32, 35, 60, 0.8);
 10→  --header-border: rgba(204, 195, 248, 0.22);
 11→  --theme-button-bg: rgba(255, 255, 255, 0.08);
 12→  --theme-button-text: #E8E8F0;
 13→  --font-display: 'Space Grotesk', sans-serif;
 14→  --font-body: 'Plus Jakarta Sans', sans-serif;
 15→}
 16→
 17→:root[data-theme='light'] {
 18→  --bg: #D1D1E9;
 19→  --surface: #FFFFFE;
 20→  --text: #2B2C34;
 21→  --text-secondary: #6C6F88;
 22→  --accent: #1F0D77;
 23→  --highlight: #E45858;
 24→  --accent-contrast: #F8F8FF;
 25→  --header-bg: rgba(209, 209, 233, 0.86);
 26→  --header-border: rgba(31, 13, 119, 0.18);
 27→  --theme-button-bg: rgba(31, 13, 119, 0.09);
 28→  --theme-button-text: #1F0D77;
 29→}
 30→
 31→* {
 32→  box-sizing: border-box;
 33→}
 34→
 35→/* Skip link for accessibility - completely hidden until focused */
 36→.skip-link {
 37→  position: absolute;
 38→  top: -9999px;
 39→  left: -9999px;
 40→  width: 1px;
 41→  height: 1px;
 42→  overflow: hidden;
 43→  clip: rect(0, 0, 0, 0);
 44→  white-space: nowrap;
 45→  border: 0;
 46→  padding: 0;
 47→  margin: 0;
 48→  opacity: 0;
 49→  background: transparent;
 50→  color: transparent;
 51→  pointer-events: none;
 52→}
 53→
 54→.skip-link:focus {
 55→  position: fixed;
 56→  top: 0;
 57→  left: 0;
 58→  width: auto;
 59→  height: auto;
 60→  overflow: visible;
 61→  clip: auto;
 62→  white-space: normal;
 63→  opacity: 1;
 64→  background: var(--accent);
 65→  color: var(--accent-contrast);
 66→  padding: 8px 16px;
 67→  text-decoration: none;
 68→  font-weight: 600;
 69→  z-index: 10000;
 70→  pointer-events: auto;
 71→}
 72→
 73→body {
 74→  margin: 0;
 75→  background: var(--bg);
 76→  color: var(--text);
 77→  font-family: var(--font-body);
 78→  line-height: 1.65;
 79→  -webkit-font-smoothing: antialiased;
 80→  transition: background-color 240ms ease, color 240ms ease;
 81→}
 82→
 83→h1,
 84→h2,
 85→h3,
 86→h4,
 87→h5,
 88→h6 {
 89→  font-family: var(--font-display);
 90→  text-transform: uppercase;
 91→  letter-spacing: 0.03em;
 92→}
 93→
 94→a {
 95→  color: inherit;
 96→  text-decoration: none;
 97→}
 98→
 99→.container {
100→  max-width: 1200px;
101→  margin: 0 auto;
102→  padding: 0 24px;
103→}
104→
105→header {
106→  position: fixed;
107→  top: 0;
108→  left: 0;
109→  right: 0;
110→  z-index: 1000;
111→  background: var(--header-bg);
112→  border-bottom: 1px solid var(--header-border);
113→  backdrop-filter: blur(12px);
114→  transition: background-color 240ms ease, border-color 240ms ease;
115→}
116→
117→.nav-inner {
118→  height: 80px;
119→  display: flex;
120→  align-items: center;
121→  justify-content: space-between;
122→  gap: 16px;
123→}
124→
125→.logo {
126→  display: flex;
127→  align-items: center;
128→  gap: 10px;
129→  color: var(--text);
130→  text-decoration: none;
131→  font-family: var(--font-display);
132→  font-weight: 700;
133→  font-size: 1.25rem;
134→}
135→
136→.logo .logo-mark {
137→  width: 28px;
138→  height: 28px;
139→  border-radius: 8px;
140→  box-shadow: 0 6px 14px color-mix(in srgb, var(--bg) 68%, transparent 32%);
141→}
142→
143→.logo .logo-mark--light {
144→  display: none;
145→}
146→
147→:root[data-theme='light'] .logo .logo-mark--dark {
148→  display: none;
149→}
150→
151→:root[data-theme='light'] .logo .logo-mark--light {
152→  display: block;
153→}
154→
155→.nav-links {
156→  display: flex;
157→  gap: 32px;
158→  align-items: center;
159→  justify-content: center;
160→  flex-wrap: nowrap;
161→  flex: 1 1 auto;
162→  min-width: 0;
163→}
164→
165→.nav-links a {
166→  color: var(--text);
167→  text-decoration: none;
168→  font-size: 0.9rem;
169→  text-transform: uppercase;
170→  letter-spacing: 0.1em;
171→  opacity: 0.8;
172→  position: relative;
173→  white-space: nowrap;
174→}
175→
176→.nav-links a::after {
177→  content: '';
178→  position: absolute;
179→  bottom: -5px;
180→  left: 0;
181→  width: 0;
182→  height: 2px;
183→  background: var(--accent);
184→  transition: width 0.3s;
185→}
186→
187→.nav-links a:hover::after {
188→  width: 100%;
189→}
190→
191→.nav-cta {
192→  display: flex;
193→  align-items: center;
194→  gap: 12px;
195→  flex-shrink: 0;
196→}
197→
198→.btn {
199→  display: inline-flex;
200→  align-items: center;
201→  justify-content: center;
202→  padding: 18px 36px;
203→  text-decoration: none;
204→  font-family: var(--font-display);
205→  font-weight: 700;
206→  text-transform: uppercase;
207→  letter-spacing: 0.05em;
208→  position: relative;
209→  clip-path: polygon(10px 0, 100% 0, 100% calc(100% - 10px), calc(100% - 10px) 100%, 0 100%, 0 10px);
210→  transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
211→  border: none;
212→  cursor: pointer;
213→}
214→
215→.btn-primary {
216→  background: var(--accent);
217→  color: var(--accent-contrast);
218→}
219→
220→.btn-nav {
221→  padding: 12px 24px;
222→  font-size: 0.9rem;
223→}
224→
225→.btn-primary::before {
226→  content: '';
227→  position: absolute;
228→  top: 0;
229→  left: 0;
230→  width: 100%;
231→  height: 100%;
232→  background: rgba(255, 255, 255, 0.2);
233→  transform: translateX(-100%);
234→  transition: transform 0.4s;
235→  z-index: 1;
236→}
237→
238→.btn-primary:hover::before {
239→  transform: translateX(0);
240→}
241→
242→.btn-primary:hover {
243→  transform: translateY(-2px);
244→  box-shadow: 0 10px 30px rgba(31, 13, 119, 0.3);
245→}
246→
247→.theme-toggle {
248→  border: 1px solid var(--header-border);
249→  background: var(--theme-button-bg);
250→  color: var(--theme-button-text);
251→  height: 44px;
252→  min-width: 44px;
253→  padding: 0 14px;
254→  border-radius: 999px;
255→  font: 600 0.82rem var(--font-body);
256→  display: inline-flex;
257→  align-items: center;
258→  gap: 8px;
259→  cursor: pointer;
260→  transition: transform 180ms ease, background-color 180ms ease, border-color 180ms ease;
261→}
262→
263→.theme-toggle:hover {
264→  transform: translateY(-1px);
265→}
266→
267→.theme-toggle:focus-visible {
268→  outline: 2px solid var(--accent);
269→  outline-offset: 2px;
270→}
271→
272→.theme-icon {
273→  width: 16px;
274→  height: 16px;
275→  flex: 0 0 16px;
276→  display: inline-flex;
277→  align-items: center;
278→  justify-content: center;
279→  line-height: 0;
280→}
281→
282→.theme-icon svg {
283→  width: 16px;
284→  height: 16px;
285→  display: block;
286→}
287→
288→footer {
289→  background: color-mix(in srgb, var(--bg) 82%, #07080D 18%);
290→  padding: 60px 0;
291→  font-size: 0.9rem;
292→  border-top: 1px solid color-mix(in srgb, var(--text) 14%, transparent 86%);
293→}
294→
295→.footer-content {
296→  display: flex;
297→  justify-content: flex-start;
298→  align-items: flex-start;
299→  flex-wrap: wrap;
300→  gap: 18px 24px;
301→}
302→
303→.footer-brand {
304→  width: 100%;
305→}
306→
307→.footer-brand h4 {
308→  margin: 0;
309→  color: var(--text);
310→}
311→
312→.footer-brand p {
313→  color: var(--text-secondary);
314→  font-size: 0.8rem;
315→  margin-top: 8px;
316→}
317→
318→.footer-links {
319→  width: 100%;
320→  display: flex;
321→  flex-wrap: wrap;
322→  gap: 8px 24px;
323→}
324→
325→.footer-links a {
326→  margin: 0;
327→  color: var(--accent);
328→  text-decoration: none;
329→}
330→
331→.footer-links a:hover {
332→  color: var(--highlight);
333→}
334→
335→.footer-compare {
336→  width: 100%;
337→  padding-top: 20px;
338→  margin-top: 6px;
339→  border-top: 1px solid color-mix(in srgb, var(--text) 8%, transparent 92%);
340→  display: grid;
341→  grid-template-columns: repeat(5, minmax(0, 1fr));
342→  gap: 8px 14px;
343→  font-size: 0.82rem;
344→}
345→
346→.footer-compare a {
347→  color: var(--text-secondary);
348→  transition: color 0.2s;
349→  text-align: left;
350→}
351→
352→.footer-compare a:hover {
353→  color: var(--accent);
354→}
355→
356→/* ── Mobile Expanding Navigation ── */
357→
358→.mobile-menu-btn {
359→  display: none;
360→  width: 44px;
361→  height: 44px;
362→  background: var(--theme-button-bg);
363→  border: 1px solid var(--header-border);
364→  border-radius: 999px;
365→  cursor: pointer;
366→  align-items: center;
367→  justify-content: center;
368→  padding: 0;
369→  transition: background-color 180ms ease, border-color 180ms ease;
370→  flex-shrink: 0;
371→}
372→
373→.mobile-menu-btn:hover {
374→  background: color-mix(in srgb, var(--accent) 12%, transparent 88%);
375→}
376→
377→.mobile-menu-btn svg {
378→  width: 18px;
379→  height: 18px;
380→  stroke: var(--text);
381→  stroke-width: 1.8;
382→  fill: none;
383→  stroke-linecap: round;
384→}
385→
386→.mobile-menu-btn .icon-close { display: none; }
387→.mobile-menu-btn.open .icon-menu { display: none; }
388→.mobile-menu-btn.open .icon-close { display: block; }
389→
390→.mobile-nav-expand {
391→  display: none;
392→  overflow: hidden;
393→  max-height: 0;
394→  border-top: 1px solid transparent;
395→  transition:
396→    max-height 520ms cubic-bezier(0.16, 1, 0.3, 1),
397→    border-color 300ms ease;
398→}
399→
400→.mobile-nav-expand.open {
401→  max-height: 520px;
402→  border-top-color: var(--header-border);
403→}
404→
405→.mobile-nav-items {
406→  padding: 8px 0 20px;
407→  display: flex;
408→  flex-direction: column;
409→  gap: 1px;
410→}
411→
412→.mobile-nav-item {
413→  display: flex;
414→  align-items: center;
415→  gap: 14px;
416→  padding: 13px 8px;
417→  border-radius: 10px;
418→  text-decoration: none;
419→  color: var(--text);
420→  opacity: 0;
421→  transform: translateY(10px);
422→  transition:
423→    background-color 200ms ease;
424→}
425→
426→.mobile-nav-item:hover,
427→.mobile-nav-item:active {
428→  background: color-mix(in srgb, var(--accent) 8%, transparent 92%);
429→}
430→
431→.mobile-nav-expand.open .mobile-nav-item {
432→  opacity: 1;
433→  transform: translateY(0);
434→  transition:
435→    opacity 380ms cubic-bezier(0.16, 1, 0.3, 1),
436→    transform 460ms cubic-bezier(0.34, 1.56, 0.64, 1),
437→    background-color 200ms ease;
438→}
439→
440→.mobile-nav-expand.open .mobile-nav-item:nth-child(1) { transition-delay: 50ms, 50ms, 0ms; }
441→.mobile-nav-expand.open .mobile-nav-item:nth-child(2) { transition-delay: 100ms, 100ms, 0ms; }
442→.mobile-nav-expand.open .mobile-nav-item:nth-child(3) { transition-delay: 150ms, 150ms, 0ms; }
443→.mobile-nav-expand.open .mobile-nav-item:nth-child(4) { transition-delay: 200ms, 200ms, 0ms; }
444→.mobile-nav-expand.open .mobile-nav-item:nth-child(5) { transition-delay: 250ms, 250ms, 0ms; }
445→.mobile-nav-expand.open .mobile-nav-item:nth-child(6) { transition-delay: 300ms, 300ms, 0ms; }
446→.mobile-nav-expand.open .mobile-nav-item:nth-child(7) { transition-delay: 350ms, 350ms, 0ms; }
447→
448→.mobile-nav-expand:not(.open) .mobile-nav-item {
449→  transition:
450→    opacity 200ms ease,
451→    transform 200ms ease,
452→    background-color 200ms ease;
453→  transition-delay: 0ms;
454→}
455→
456→.mobile-nav-icon {
457→  width: 38px;
458→  height: 38px;
459→  border-radius: 9px;
460→  display: flex;
461→  align-items: center;
462→  justify-content: center;
463→  flex-shrink: 0;
464→  background: color-mix(in srgb, var(--accent) 10%, transparent 90%);
465→}
466→
467→:root[data-theme='light'] .mobile-nav-icon {
468→  background: color-mix(in srgb, var(--accent) 16%, transparent 84%);
469→}
470→
471→.mobile-nav-icon svg {
472→  width: 18px;
473→  height: 18px;
474→  stroke-width: 2.1;
475→  stroke-linecap: round;
476→  stroke-linejoin: round;
477→  fill: none;
478→}
479→
480→.mobile-nav-label {
481→  display: flex;
482→  flex-direction: column;
483→  gap: 0px;
484→  min-width: 0;
485→}
486→
487→.mobile-nav-label-title {
488→  font-family: var(--font-display);
489→  font-size: 0.92rem;
490→  font-weight: 600;
491→  letter-spacing: 0.02em;
492→  text-transform: uppercase;
493→}
494→
495→.mobile-nav-label-desc {
496→  font-size: 0.78rem;
497→  color: var(--text-secondary);
498→  line-height: 1.3;
499→}
500→
501→.mobile-nav-separator {
502→  height: 1px;
503→  background: var(--header-border);
504→  margin: 6px 8px;
505→}
506→
507→.mobile-nav-cta {
508→  padding: 4px 8px 0;
509→}
510→
511→.mobile-nav-cta .btn {
512→  width: 100%;
513→  text-align: center;
514→  justify-content: center;
515→  padding: 14px 24px;
516→  font-size: 0.9rem;
517→}
518→
519→/* Push main content when mobile nav is open */
520→main {
521→  transition: transform 520ms cubic-bezier(0.16, 1, 0.3, 1);
522→}
523→
524→body.mobile-nav-open main {
525→  transform: translateY(var(--mobile-nav-h, 0px));
526→}
527→
528→body.mobile-nav-open footer {
529→  transform: translateY(var(--mobile-nav-h, 0px));
530→}
531→
532→footer {
533→  transition: transform 520ms cubic-bezier(0.16, 1, 0.3, 1);
534→}
535→
536→@media (max-width: 768px) {
537→  .container {
538→    padding: 0 16px;
539→  }
540→
541→  .nav-inner {
542→    height: 74px;
543→  }
544→
545→  .nav-links {
546→    display: none;
547→  }
548→
549→  .mobile-menu-btn {
550→    display: flex;
551→  }
552→
553→  .mobile-nav-expand {
554→    display: block;
555→  }
556→
557→  /* Hide "Get App" in the top bar on mobile — it's in the expanding nav instead */
558→  .nav-cta .btn-nav {
559→    display: none;
560→  }
561→
562→  .theme-toggle #theme-label {
563→    display: none;
564→  }
565→
566→  .theme-toggle {
567→    width: 44px;
568→    min-width: 44px;
569→    padding: 0;
570→    justify-content: center;
571→    gap: 0;
572→  }
573→
574→  .logo {
575→    font-size: 1.1rem;
576→  }
577→
578→  .logo .logo-mark {
579→    width: 24px;
580→    height: 24px;
581→    border-radius: 7px;
582→  }
583→
584→  .nav-cta {
585→    gap: 10px;
586→  }
587→
588→  .nav-cta .btn {
589→    padding: 10px 16px !important;
590→    font-size: 0.82rem !important;
591→    letter-spacing: 0.03em;
592→  }
593→
594→  .footer-links a {
595→    margin: 0;
596→  }
597→
598→  .footer-links {
599→    display: grid;
600→    grid-template-columns: 1fr;
601→    gap: 8px;
602→  }
603→
604→  .footer-compare {
605→    grid-template-columns: 1fr;
606→    gap: 6px;
607→  }
608→}
609→
610→@media (max-width: 1360px) {
611→  .nav-links {
612→    gap: 24px;
613→  }
614→
615→  .nav-links a {
616→    font-size: 0.84rem;
617→    letter-spacing: 0.08em;
618→  }
619→
620→  .nav-cta {
621→    gap: 10px;
622→  }
623→}
624→
625→@media (max-width: 1160px) {
626→  .nav-links {
627→    display: none;
628→  }
629→
630→  .mobile-menu-btn {
631→    display: flex;
632→  }
633→
634→  .mobile-nav-expand {
635→    display: block;
636→  }
637→
638→  .nav-cta .btn-nav {
639→    display: none;
640→  }
641→
642→  .theme-toggle #theme-label {
643→    display: none;
644→  }
645→
646→  .theme-toggle {
647→    width: 44px;
648→    min-width: 44px;
649→    padding: 0;
650→    justify-content: center;
651→    gap: 0;
652→  }
653→}
654→

file.content:
:root {
  --bg: #20233C;
  --surface: #323444;
  --text: #E8E8F0;
  --text-secondary: #D1D2DC;
  --accent: #CCC3F8;
  --highlight: #FF6F6F;
  --accent-contrast: #20233C;
  --header-bg: rgba(32, 35, 60, 0.8);
  --header-border: rgba(204, 195, 248, 0.22);
  --theme-button-bg: rgba(255, 255, 255, 0.08);
  --theme-button-text: #E8E8F0;
  --font-display: 'Space Grotesk', sans-serif;
  --font-body: 'Plus Jakarta Sans', sans-serif;
}

:root[data-theme='light'] {
  --bg: #D1D1E9;
  --surface: #FFFFFE;
  --text: #2B2C34;
  --text-secondary: #6C6F88;
  --accent: #1F0D77;
  --highlight: #E45858;
  --accent-contrast: #F8F8FF;
  --header-bg: rgba(209, 209, 233, 0.86);
  --header-border: rgba(31, 13, 119, 0.18);
  --theme-button-bg: rgba(31, 13, 119, 0.09);
  --theme-button-text: #1F0D77;
}

* {
  box-sizing: border-box;
}

/* Skip link for accessibility - completely hidden until focused */
.skip-link {
  position: absolute;
  top: -9999px;
  left: -9999px;
  width: 1px;
  height: 1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
  padding: 0;
  margin: 0;
  opacity: 0;
  background: transparent;
  color: transparent;
  pointer-events: none;
}

.skip-link:focus {
  position: fixed;
  top: 0;
  left: 0;
  width: auto;
  height: auto;
  overflow: visible;
  clip: auto;
  white-space: normal;
  opacity: 1;
  background: var(--accent);
  color: var(--accent-contrast);
  padding: 8px 16px;
  text-decoration: none;
  font-weight: 600;
  z-index: 10000;
  pointer-events: auto;
}

body {
  margin: 0;
  background: var(--bg);
  color: var(--text);
  font-family: var(--font-body);
  line-height: 1.65;
  -webkit-font-smoothing: antialiased;
  transition: background-color 240ms ease, color 240ms ease;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: var(--font-display);
  text-transform: uppercase;
  letter-spacing: 0.03em;
}

a {
  color: inherit;
  text-decoration: none;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 24px;
}

header {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  background: var(--header-bg);
  border-bottom: 1px solid var(--header-border);
  backdrop-filter: blur(12px);
  transition: background-color 240ms ease, border-color 240ms ease;
}

.nav-inner {
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 16px;
}

.logo {
  display: flex;
  align-items: center;
  gap: 10px;
  color: var(--text);
  text-decoration: none;
  font-family: var(--font-display);
  font-weight: 700;
  font-size: 1.25rem;
}

.logo .logo-mark {
  width: 28px;
  height: 28px;
  border-radius: 8px;
  box-shadow: 0 6px 14px color-mix(in srgb, var(--bg) 68%, transparent 32%);
}

.logo .logo-mark--light {
  display: none;
}

:root[data-theme='light'] .logo .logo-mark--dark {
  display: none;
}

:root[data-theme='light'] .logo .logo-mark--light {
  display: block;
}

.nav-links {
  display: flex;
  gap: 32px;
  align-items: center;
  justify-content: center;
  flex-wrap: nowrap;
  flex: 1 1 auto;
  min-width: 0;
}

.nav-links a {
  color: var(--text);
  text-decoration: none;
  font-size: 0.9rem;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  opacity: 0.8;
  position: relative;
  white-space: nowrap;
}

.nav-links a::after {
  content: '';
  position: absolute;
  bottom: -5px;
  left: 0;
  width: 0;
  height: 2px;
  background: var(--accent);
  transition: width 0.3s;
}

.nav-links a:hover::after {
  width: 100%;
}

.nav-cta {
  display: flex;
  align-items: center;
  gap: 12px;
  flex-shrink: 0;
}

.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 18px 36px;
  text-decoration: none;
  font-family: var(--font-display);
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  position: relative;
  clip-path: polygon(10px 0, 100% 0, 100% calc(100% - 10px), calc(100% - 10px) 100%, 0 100%, 0 10px);
  transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  border: none;
  cursor: pointer;
}

.btn-primary {
  background: var(--accent);
  color: var(--accent-contrast);
}

.btn-nav {
  padding: 12px 24px;
  font-size: 0.9rem;
}

.btn-primary::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(255, 255, 255, 0.2);
  transform: translateX(-100%);
  transition: transform 0.4s;
  z-index: 1;
}

.btn-primary:hover::before {
  transform: translateX(0);
}

.btn-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 30px rgba(31, 13, 119, 0.3);
}

.theme-toggle {
  border: 1px solid var(--header-border);
  background: var(--theme-button-bg);
  color: var(--theme-button-text);
  height: 44px;
  min-width: 44px;
  padding: 0 14px;
  border-radius: 999px;
  font: 600 0.82rem var(--font-body);
  display: inline-flex;
  align-items: center;
  gap: 8px;
  cursor: pointer;
  transition: transform 180ms ease, background-color 180ms ease, border-color 180ms ease;
}

.theme-toggle:hover {
  transform: translateY(-1px);
}

.theme-toggle:focus-visible {
  outline: 2px solid var(--accent);
  outline-offset: 2px;
}

.theme-icon {
  width: 16px;
  height: 16px;
  flex: 0 0 16px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  line-height: 0;
}

.theme-icon svg {
  width: 16px;
  height: 16px;
  display: block;
}

footer {
  background: color-mix(in srgb, var(--bg) 82%, #07080D 18%);
  padding: 60px 0;
  font-size: 0.9rem;
  border-top: 1px solid color-mix(in srgb, var(--text) 14%, transparent 86%);
}

.footer-content {
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  flex-wrap: wrap;
  gap: 18px 24px;
}

.footer-brand {
  width: 100%;
}

.footer-brand h4 {
  margin: 0;
  color: var(--text);
}

.footer-brand p {
  color: var(--text-secondary);
  font-size: 0.8rem;
  margin-top: 8px;
}

.footer-links {
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  gap: 8px 24px;
}

.footer-links a {
  margin: 0;
  color: var(--accent);
  text-decoration: none;
}

.footer-links a:hover {
  color: var(--highlight);
}

.footer-compare {
  width: 100%;
  padding-top: 20px;
  margin-top: 6px;
  border-top: 1px solid color-mix(in srgb, var(--text) 8%, transparent 92%);
  display: grid;
  grid-template-columns: repeat(5, minmax(0, 1fr));
  gap: 8px 14px;
  font-size: 0.82rem;
}

.footer-compare a {
  color: var(--text-secondary);
  transition: color 0.2s;
  text-align: left;
}

.footer-compare a:hover {
  color: var(--accent);
}

/* ── Mobile Expanding Navigation ── */

.mobile-menu-btn {
  display: none;
  width: 44px;
  height: 44px;
  background: var(--theme-button-bg);
  border: 1px solid var(--header-border);
  border-radius: 999px;
  cursor: pointer;
  align-items: center;
  justify-content: center;
  padding: 0;
  transition: background-color 180ms ease, border-color 180ms ease;
  flex-shrink: 0;
}

.mobile-menu-btn:hover {
  background: color-mix(in srgb, var(--accent) 12%, transparent 88%);
}

.mobile-menu-btn svg {
  width: 18px;
  height: 18px;
  stroke: var(--text);
  stroke-width: 1.8;
  fill: none;
  stroke-linecap: round;
}

.mobile-menu-btn .icon-close { display: none; }
.mobile-menu-btn.open .icon-menu { display: none; }
.mobile-menu-btn.open .icon-close { display: block; }

.mobile-nav-expand {
  display: none;
  overflow: hidden;
  max-height: 0;
  border-top: 1px solid transparent;
  transition:
    max-height 520ms cubic-bezier(0.16, 1, 0.3, 1),
    border-color 300ms ease;
}

.mobile-nav-expand.open {
  max-height: 520px;
  border-top-color: var(--header-border);
}

.mobile-nav-items {
  padding: 8px 0 20px;
  display: flex;
  flex-direction: column;
  gap: 1px;
}

.mobile-nav-item {
  display: flex;
  align-items: center;
  gap: 14px;
  padding: 13px 8px;
  border-radius: 10px;
  text-decoration: none;
  color: var(--text);
  opacity: 0;
  transform: translateY(10px);
  transition:
    background-color 200ms ease;
}

.mobile-nav-item:hover,
.mobile-nav-item:active {
  background: color-mix(in srgb, var(--accent) 8%, transparent 92%);
}

.mobile-nav-expand.open .mobile-nav-item {
  opacity: 1;
  transform: translateY(0);
  transition:
    opacity 380ms cubic-bezier(0.16, 1, 0.3, 1),
    transform 460ms cubic-bezier(0.34, 1.56, 0.64, 1),
    background-color 200ms ease;
}

.mobile-nav-expand.open .mobile-nav-item:nth-child(1) { transition-delay: 50ms, 50ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(2) { transition-delay: 100ms, 100ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(3) { transition-delay: 150ms, 150ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(4) { transition-delay: 200ms, 200ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(5) { transition-delay: 250ms, 250ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(6) { transition-delay: 300ms, 300ms, 0ms; }
.mobile-nav-expand.open .mobile-nav-item:nth-child(7) { transition-delay: 350ms, 350ms, 0ms; }

.mobile-nav-expand:not(.open) .mobile-nav-item {
  transition:
    opacity 200ms ease,
    transform 200ms ease,
    background-color 200ms ease;
  transition-delay: 0ms;
}

.mobile-nav-icon {
  width: 38px;
  height: 38px;
  border-radius: 9px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  background: color-mix(in srgb, var(--accent) 10%, transparent 90%);
}

:root[data-theme='light'] .mobile-nav-icon {
  background: color-mix(in srgb, var(--accent) 16%, transparent 84%);
}

.mobile-nav-icon svg {
  width: 18px;
  height: 18px;
  stroke-width: 2.1;
  stroke-linecap: round;
  stroke-linejoin: round;
  fill: none;
}

.mobile-nav-label {
  display: flex;
  flex-direction: column;
  gap: 0px;
  min-width: 0;
}

.mobile-nav-label-title {
  font-family: var(--font-display);
  font-size: 0.92rem;
  font-weight: 600;
  letter-spacing: 0.02em;
  text-transform: uppercase;
}

.mobile-nav-label-desc {
  font-size: 0.78rem;
  color: var(--text-secondary);
  line-height: 1.3;
}

.mobile-nav-separator {
  height: 1px;
  background: var(--header-border);
  margin: 6px 8px;
}

.mobile-nav-cta {
  padding: 4px 8px 0;
}

.mobile-nav-cta .btn {
  width: 100%;
  text-align: center;
  justify-content: center;
  padding: 14px 24px;
  font-size: 0.9rem;
}

/* Push main content when mobile nav is open */
main {
  transition: transform 520ms cubic-bezier(0.16, 1, 0.3, 1);
}

body.mobile-nav-open main {
  transform: translateY(var(--mobile-nav-h, 0px));
}

body.mobile-nav-open footer {
  transform: translateY(var(--mobile-nav-h, 0px));
}

footer {
  transition: transform 520ms cubic-bezier(0.16, 1, 0.3, 1);
}

@media (max-width: 768px) {
  .container {
    padding: 0 16px;
  }

  .nav-inner {
    height: 74px;
  }

  .nav-links {
    display: none;
  }

  .mobile-menu-btn {
    display: flex;
  }

  .mobile-nav-expand {
    display: block;
  }

  /* Hide "Get App" in the top bar on mobile — it's in the expanding nav instead */
  .nav-cta .btn-nav {
    display: none;
  }

  .theme-toggle #theme-label {
    display: none;
  }

  .theme-toggle {
    width: 44px;
    min-width: 44px;
    padding: 0;
    justify-content: center;
    gap: 0;
  }

  .logo {
    font-size: 1.1rem;
  }

  .logo .logo-mark {
    width: 24px;
    height: 24px;
    border-radius: 7px;
  }

  .nav-cta {
    gap: 10px;
  }

  .nav-cta .btn {
    padding: 10px 16px !important;
    font-size: 0.82rem !important;
    letter-spacing: 0.03em;
  }

  .footer-links a {
    margin: 0;
  }

  .footer-links {
    display: grid;
    grid-template-columns: 1fr;
    gap: 8px;
  }

  .footer-compare {
    grid-template-columns: 1fr;
    gap: 6px;
  }
}

@media (max-width: 1360px) {
  .nav-links {
    gap: 24px;
  }

  .nav-links a {
    font-size: 0.84rem;
    letter-spacing: 0.08em;
  }

  .nav-cta {
    gap: 10px;
  }
}

@media (max-width: 1160px) {
  .nav-links {
    display: none;
  }

  .mobile-menu-btn {
    display: flex;
  }

  .mobile-nav-expand {
    display: block;
  }

  .nav-cta .btn-nav {
    display: none;
  }

  .theme-toggle #theme-label {
    display: none;
  }

  .theme-toggle {
    width: 44px;
    min-width: 44px;
    padding: 0;
    justify-content: center;
    gap: 0;
  }
}

