content.content:
  1→/* ── Feature Subpage Styles ── */
  2→
  3→.page-feature main {
  4→  padding: 0 0 0;
  5→}
  6→
  7→/* ── Breadcrumbs (reuse compare pattern) ── */
  8→.page-feature .breadcrumbs {
  9→  padding: 100px 0 0;
 10→  font-size: 0.92rem;
 11→  color: var(--text-secondary);
 12→}
 13→
 14→.page-feature .breadcrumbs a {
 15→  color: var(--accent);
 16→}
 17→
 18→.page-feature .breadcrumbs a:hover {
 19→  color: var(--highlight);
 20→}
 21→
 22→.page-feature .breadcrumbs span {
 23→  margin: 0 8px;
 24→  opacity: 0.5;
 25→}
 26→
 27→/* ── Feature Article ── */
 28→.feature-article {
 29→  max-width: 800px;
 30→  margin: 0 auto;
 31→  padding: 32px 0 80px;
 32→}
 33→
 34→.feature-article h1 {
 35→  font-size: clamp(2rem, 4vw, 3rem);
 36→  line-height: 1.1;
 37→  margin-bottom: 16px;
 38→  text-transform: none;
 39→  letter-spacing: -0.02em;
 40→}
 41→
 42→.feature-article h2 {
 43→  font-size: 1.5rem;
 44→  margin: 48px 0 20px;
 45→  text-transform: none;
 46→  letter-spacing: -0.01em;
 47→  color: var(--text);
 48→  padding-bottom: 12px;
 49→  border-bottom: 2px solid var(--accent);
 50→}
 51→
 52→.feature-article h3 {
 53→  font-size: 1.15rem;
 54→  margin: 32px 0 12px;
 55→  text-transform: none;
 56→  letter-spacing: 0;
 57→  color: var(--text);
 58→}
 59→
 60→.feature-article p {
 61→  color: var(--text-secondary);
 62→  font-size: 1rem;
 63→  margin-bottom: 16px;
 64→  line-height: 1.75;
 65→}
 66→
 67→.feature-article strong {
 68→  color: var(--text);
 69→  font-weight: 600;
 70→}
 71→
 72→.feature-article em {
 73→  color: var(--highlight);
 74→  font-style: italic;
 75→}
 76→
 77→.feature-article ul,
 78→.feature-article ol {
 79→  color: var(--text-secondary);
 80→  margin: 0 0 20px 0;
 81→  padding-left: 0;
 82→  list-style: none;
 83→}
 84→
 85→.feature-article li {
 86→  padding: 6px 0 6px 20px;
 87→  position: relative;
 88→  line-height: 1.65;
 89→}
 90→
 91→.feature-article li::before {
 92→  content: '/';
 93→  position: absolute;
 94→  left: 0;
 95→  color: var(--highlight);
 96→  font-weight: 700;
 97→  font-family: var(--font-display);
 98→}
 99→
100→.feature-article ol {
101→  counter-reset: feature-step;
102→}
103→
104→.feature-article ol li {
105→  counter-increment: feature-step;
106→}
107→
108→.feature-article ol li::before {
109→  content: counter(feature-step) '.';
110→  color: var(--accent);
111→}
112→
113→/* ── Hero Section ── */
114→.feature-hero {
115→  padding: 24px 0 0;
116→  text-align: center;
117→}
118→
119→.feature-hero-icon {
120→  font-size: 3.5rem;
121→  margin-bottom: 20px;
122→  display: block;
123→}
124→
125→.feature-hero .feature-article h1 {
126→  text-align: center;
127→}
128→
129→.feature-hero p.hero-lead {
130→  font-size: 1.15rem;
131→  color: var(--text-secondary);
132→  max-width: 640px;
133→  margin: 0 auto 32px;
134→  line-height: 1.7;
135→}
136→
137→/* ── Info Boxes ── */
138→.info-box {
139→  background: color-mix(in srgb, var(--accent) 6%, transparent 94%);
140→  border: 1px solid color-mix(in srgb, var(--accent) 25%, transparent 75%);
141→  padding: 24px 28px;
142→  margin: 24px 0;
143→  clip-path: polygon(12px 0, 100% 0, 100% calc(100% - 12px), calc(100% - 12px) 100%, 0 100%, 0 12px);
144→  position: relative;
145→}
146→
147→.info-box::after {
148→  content: '';
149→  position: absolute;
150→  top: 0;
151→  right: 0;
152→  width: 24px;
153→  height: 24px;
154→  background: linear-gradient(to bottom left, var(--highlight) 50%, transparent 50%);
155→  opacity: 0.4;
156→}
157→
158→.info-box-label {
159→  font-family: var(--font-display);
160→  font-weight: 700;
161→  font-size: 0.78rem;
162→  text-transform: uppercase;
163→  letter-spacing: 0.1em;
164→  color: var(--highlight);
165→  margin-bottom: 8px;
166→}
167→
168→.info-box p {
169→  margin-bottom: 8px;
170→}
171→
172→.info-box p:last-child {
173→  margin-bottom: 0;
174→}
175→
176→/* ── Stat Callouts ── */
177→.stat-row {
178→  display: grid;
179→  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
180→  gap: 16px;
181→  margin: 32px 0;
182→}
183→
184→.stat-card {
185→  background: color-mix(in srgb, var(--accent) 8%, transparent 92%);
186→  border: 1px solid color-mix(in srgb, var(--accent) 20%, transparent 80%);
187→  padding: 24px;
188→  text-align: center;
189→  clip-path: polygon(8px 0, 100% 0, 100% calc(100% - 8px), calc(100% - 8px) 100%, 0 100%, 0 8px);
190→}
191→
192→.stat-value {
193→  font-family: var(--font-display);
194→  font-weight: 700;
195→  font-size: 1.8rem;
196→  color: var(--accent);
197→  line-height: 1.2;
198→}
199→
200→.stat-label {
201→  font-size: 0.82rem;
202→  color: var(--text-secondary);
203→  margin-top: 4px;
204→  text-transform: uppercase;
205→  letter-spacing: 0.05em;
206→  font-family: var(--font-display);
207→}
208→
209→/* ── CSS/SVG Diagram Containers ── */
210→.diagram-container {
211→  margin: 32px 0;
212→  padding: 32px;
213→  background: color-mix(in srgb, var(--surface) 40%, transparent 60%);
214→  border: 1px solid color-mix(in srgb, var(--accent) 15%, transparent 85%);
215→  clip-path: polygon(16px 0, 100% 0, 100% calc(100% - 16px), calc(100% - 16px) 100%, 0 100%, 0 16px);
216→  position: relative;
217→  overflow: hidden;
218→}
219→
220→.diagram-container::before {
221→  content: '';
222→  position: absolute;
223→  top: 0;
224→  left: 0;
225→  width: 32px;
226→  height: 32px;
227→  background: linear-gradient(to bottom right, var(--highlight) 50%, transparent 50%);
228→  opacity: 0.2;
229→}
230→
231→.diagram-label {
232→  font-family: var(--font-display);
233→  font-weight: 700;
234→  font-size: 0.78rem;
235→  text-transform: uppercase;
236→  letter-spacing: 0.1em;
237→  color: var(--accent);
238→  margin-bottom: 20px;
239→  text-align: center;
240→}
241→
242→/* Pattern Grid Diagram */
243→.pattern-grid-diagram {
244→  display: grid;
245→  grid-template-columns: repeat(5, 1fr);
246→  gap: 12px;
247→  max-width: 280px;
248→  margin: 0 auto;
249→  padding: 20px;
250→}
251→
252→.grid-node {
253→  aspect-ratio: 1;
254→  border-radius: 50%;
255→  border: 2px solid color-mix(in srgb, var(--text) 25%, transparent 75%);
256→  background: transparent;
257→  transition: all 0.3s ease;
258→  position: relative;
259→}
260→
261→.grid-node.active {
262→  border-color: var(--accent);
263→  background: color-mix(in srgb, var(--accent) 20%, transparent 80%);
264→  box-shadow: 0 0 12px color-mix(in srgb, var(--accent) 30%, transparent 70%);
265→}
266→
267→.grid-node.active::after {
268→  content: '';
269→  position: absolute;
270→  top: 50%;
271→  left: 50%;
272→  transform: translate(-50%, -50%);
273→  width: 40%;
274→  height: 40%;
275→  background: var(--accent);
276→  border-radius: 50%;
277→}
278→
279→/* Encryption Flow Diagram */
280→.flow-diagram {
281→  display: flex;
282→  align-items: flex-start;
283→  justify-content: center;
284→  gap: 8px;
285→  flex-wrap: wrap;
286→  padding: 16px 0;
287→}
288→
289→.flow-step {
290→  display: flex;
291→  flex-direction: column;
292→  align-items: center;
293→  gap: 8px;
294→  min-width: 100px;
295→}
296→
297→.flow-step-box {
298→  background: color-mix(in srgb, var(--accent) 12%, transparent 88%);
299→  border: 1px solid color-mix(in srgb, var(--accent) 30%, transparent 70%);
300→  padding: 12px 16px;
301→  font-family: var(--font-display);
302→  font-size: 0.82rem;
303→  font-weight: 600;
304→  text-transform: uppercase;
305→  letter-spacing: 0.05em;
306→  color: var(--text);
307→  text-align: center;
308→  clip-path: polygon(6px 0, 100% 0, 100% calc(100% - 6px), calc(100% - 6px) 100%, 0 100%, 0 6px);
309→  min-width: 100px;
310→  height: 44px;
311→  display: flex;
312→  align-items: center;
313→  justify-content: center;
314→}
315→
316→.flow-step-label {
317→  font-size: 0.72rem;
318→  color: var(--text-secondary);
319→  text-align: center;
320→}
321→
322→.flow-arrow {
323→  color: var(--highlight);
324→  font-size: 1.2rem;
325→  font-weight: 700;
326→  flex-shrink: 0;
327→  height: 44px;
328→  display: flex;
329→  align-items: center;
330→}
331→
332→/* ── Blockquotes ── */
333→.feature-article blockquote {
334→  border-left: 3px solid var(--highlight);
335→  padding: 12px 20px;
336→  margin: 24px 0;
337→  background: color-mix(in srgb, var(--surface) 50%, transparent 50%);
338→  border-radius: 0 8px 8px 0;
339→  font-style: italic;
340→  color: var(--text-secondary);
341→}
342→
343→/* ── FAQ Accordion ── */
344→.feature-faq {
345→  margin: 48px 0 0;
346→}
347→
348→.feature-faq h2 {
349→  margin-bottom: 24px;
350→}
351→
352→.feature-faq-item {
353→  border-bottom: 1px solid color-mix(in srgb, var(--text) 10%, transparent 90%);
354→}
355→
356→.feature-faq-item:first-of-type {
357→  border-top: 1px solid color-mix(in srgb, var(--text) 10%, transparent 90%);
358→}
359→
360→.feature-faq-item summary {
361→  padding: 18px 0;
362→  cursor: pointer;
363→  font-family: var(--font-display);
364→  font-weight: 600;
365→  font-size: 1rem;
366→  color: var(--text);
367→  list-style: none;
368→  display: flex;
369→  align-items: center;
370→  justify-content: space-between;
371→  gap: 16px;
372→}
373→
374→.feature-faq-item summary::-webkit-details-marker {
375→  display: none;
376→}
377→
378→.feature-faq-item summary::after {
379→  content: '+';
380→  font-size: 1.4rem;
381→  font-weight: 300;
382→  color: var(--accent);
383→  flex-shrink: 0;
384→  transition: transform 0.2s ease;
385→}
386→
387→.feature-faq-item[open] summary::after {
388→  content: '\2212';
389→}
390→
391→.feature-faq-item .faq-answer {
392→  padding: 0 0 18px;
393→}
394→
395→.feature-faq-item .faq-answer p {
396→  margin-bottom: 8px;
397→}
398→
399→.feature-faq-item .faq-answer p:last-child {
400→  margin-bottom: 0;
401→}
402→
403→/* ── CTA Section ── */
404→.feature-cta {
405→  margin: 56px 0 0;
406→  padding: 48px 40px;
407→  background: radial-gradient(ellipse at center, color-mix(in srgb, var(--accent) 10%, transparent 90%) 0%, transparent 70%);
408→  border: 1px solid color-mix(in srgb, var(--accent) 25%, transparent 75%);
409→  text-align: center;
410→  clip-path: polygon(20px 0, 100% 0, 100% calc(100% - 20px), calc(100% - 20px) 100%, 0 100%, 0 20px);
411→}
412→
413→.feature-cta h2 {
414→  font-size: 1.6rem;
415→  margin: 0 0 12px;
416→  text-transform: uppercase;
417→  letter-spacing: 0.02em;
418→  border-bottom: none;
419→  padding-bottom: 0;
420→}
421→
422→.feature-cta p {
423→  max-width: 500px;
424→  margin: 0 auto 24px;
425→  text-align: center;
426→}
427→
428→.feature-cta .btn {
429→  font-size: 1rem;
430→}
431→
432→/* ── Related Features Grid ── */
433→.related-features {
434→  margin: 56px 0 0;
435→}
436→
437→.related-features h2 {
438→  font-size: 1.2rem;
439→  margin-bottom: 20px;
440→  text-align: center;
441→  text-transform: uppercase;
442→  letter-spacing: 0.05em;
443→  border-bottom: none;
444→  padding-bottom: 0;
445→}
446→
447→.related-grid {
448→  display: grid;
449→  grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
450→  gap: 16px;
451→}
452→
453→.related-card {
454→  display: flex;
455→  align-items: center;
456→  gap: 14px;
457→  padding: 16px 20px;
458→  background: color-mix(in srgb, var(--surface) 50%, transparent 50%);
459→  border: 1px solid color-mix(in srgb, var(--text) 8%, transparent 92%);
460→  clip-path: polygon(8px 0, 100% 0, 100% calc(100% - 8px), calc(100% - 8px) 100%, 0 100%, 0 8px);
461→  color: var(--text);
462→  transition: border-color 0.2s, transform 0.2s, background 0.2s;
463→}
464→
465→.related-card:hover {
466→  border-color: var(--accent);
467→  transform: translateY(-2px);
468→  background: color-mix(in srgb, var(--accent) 6%, transparent 94%);
469→}
470→
471→.related-card-icon {
472→  font-size: 1.5rem;
473→  flex-shrink: 0;
474→}
475→
476→.related-card-text {
477→  display: flex;
478→  flex-direction: column;
479→  gap: 2px;
480→}
481→
482→.related-card-title {
483→  font-family: var(--font-display);
484→  font-weight: 600;
485→  font-size: 0.88rem;
486→  letter-spacing: 0.01em;
487→}
488→
489→.related-card-desc {
490→  font-size: 0.78rem;
491→  color: var(--text-secondary);
492→  line-height: 1.4;
493→}
494→
