358→        const ctx = canvas.getContext("2d");
359→        if (!ctx) return;
360→
361→        const runtime = {
362→          disposed: false,
363→          heroVisible: true,
364→          width: 0,
365→          height: 0,
366→          bgDots: [],
367→          accentColor: "#CCC3F8",
368→          accentTrailColor: "rgba(204, 195, 248, 0.4)",
369→          drawingPath: false,
370→          drawingResetTimerId: 0,
371→          drawRafId: 0,
372→          minuteTimerId: 0,
373→          sequenceIndex: 0,
374→          nodeCenters: [],
375→          patternLoopTicket: 0,
376→          patternLoopRunning: false,
377→          timeoutIds: new Set(),
378→          sequences: [[0, 5, 6, 7, 12, 17, 16, 15, 20, 21, 22, 23, 24]]
