387→          }, ms);
388→          runtime.timeoutIds.add(id);
389→          return id;
390→        };
391→
392→        const clearScheduledTimeout = (id) => {
393→          if (!id) return;
394→          window.clearTimeout(id);
395→          runtime.timeoutIds.delete(id);
396→        };
397→
398→        const clearAllTimeouts = () => {
399→          runtime.timeoutIds.forEach((id) => window.clearTimeout(id));
400→          runtime.timeoutIds.clear();
401→          runtime.drawingResetTimerId = 0;
402→          runtime.minuteTimerId = 0;
403→        };
404→
405→        const updateCanvasTheme = () => {
406→          const styles = getComputedStyle(document.documentElement);
407→          runtime.accentColor = styles.getPropertyValue("--accent-dark").trim() || "#CCC3F8";
408→          runtime.accentTrailColor = toRgba(runtime.accentColor, 0.35);
409→        };
410→
411→        const initDots = () => {
412→          runtime.bgDots = [];
413→          const spacing = 110;
414→          const rows = Math.ceil(runtime.height / spacing);
415→          const cols = Math.ceil(runtime.width / spacing);
416→          for (let i = 0; i < cols; i += 1) {
417→            for (let j = 0; j < rows; j += 1) {
418→              runtime.bgDots.push({
419→                x: i * spacing + (Math.random() * 24),
420→                y: j * spacing + (Math.random() * 24)
421→              });
422→            }
423→          }
424→        };
