443→        const drawRandomPattern = () => {
444→          if (runtime.drawingPath || runtime.bgDots.length === 0 || runtime.disposed || document.hidden || !runtime.heroVisible) return;
445→          runtime.drawingPath = true;
446→
447→          const startIndex = Math.floor(Math.random() * runtime.bgDots.length);
448→          const pathLength = 4 + Math.floor(Math.random() * 3);
449→          const start = runtime.bgDots[startIndex];
450→          let current = start;
451→
452→          ctx.strokeStyle = runtime.accentTrailColor;
453→          ctx.lineWidth = 2;
454→          ctx.beginPath();
455→          ctx.moveTo(start.x, start.y);
456→
457→          for (let i = 0; i < pathLength; i += 1) {
458→            const next = runtime.bgDots[Math.floor(Math.random() * runtime.bgDots.length)];
459→            const dist = Math.hypot(next.x - current.x, next.y - current.y);
460→            if (dist < 200 && dist > 36) {
461→              ctx.lineTo(next.x, next.y);
462→              current = next;
463→            }
464→          }
