488→          if (Math.random() > 0.95) drawRandomPattern();
489→          runtime.drawRafId = window.requestAnimationFrame(draw);
490→        };
491→
492→        const startDrawLoop = () => {
493→          if (runtime.disposed || document.hidden || !runtime.heroVisible || runtime.drawRafId) return;
494→          runtime.drawRafId = window.requestAnimationFrame(draw);
495→        };
496→
497→        const stopDrawLoop = () => {
498→          if (!runtime.drawRafId) return;
499→          window.cancelAnimationFrame(runtime.drawRafId);
500→          runtime.drawRafId = 0;
501→        };
