503→        const updateMockupTime = () => {
504→          const now = new Date();
505→          const hour = ((now.getHours() + 11) % 12) + 1;
506→          const minute = String(now.getMinutes()).padStart(2, "0");
507→          mockupTimeEl.textContent = `${hour}:${minute}`;
508→        };
509→
510→        const scheduleMinuteTick = () => {
511→          if (runtime.disposed || document.hidden || !runtime.heroVisible) return;
512→          updateMockupTime();
513→          const now = new Date();
514→          const msUntilNextMinute = ((60 - now.getSeconds()) * 1000) - now.getMilliseconds();
515→          clearScheduledTimeout(runtime.minuteTimerId);
516→          runtime.minuteTimerId = scheduleTimeout(() => {
517→            runtime.minuteTimerId = 0;
518→            scheduleMinuteTick();
519→          }, Math.max(200, msUntilNextMinute));
520→        };
521→
522→        const computeNodeCenters = () => {
523→          const baseRect = lockZone.getBoundingClientRect();
524→          svgEl.setAttribute("viewBox", `0 0 ${baseRect.width} ${baseRect.height}`);
525→          svgEl.setAttribute("width", `${baseRect.width}`);
526→          svgEl.setAttribute("height", `${baseRect.height}`);
527→          runtime.nodeCenters = dots.map((dot) => {
528→            const rect = dot.getBoundingClientRect();
529→            return {
530→              x: rect.left - baseRect.left + (rect.width / 2),
531→              y: rect.top - baseRect.top + (rect.height / 2)
532→            };
533→          });
534→        };
535→
536→        const setPath = (points) => {
537→          if (points.length === 0) {
538→            pathEl.setAttribute("d", "");
539→            cursorEl.style.opacity = "0";
540→            return;
541→          }
542→          const commands = points.map((point, idx) => `${idx === 0 ? "M" : "L"} ${point.x.toFixed(2)} ${point.y.toFixed(2)}`);
543→          pathEl.setAttribute("d", commands.join(" "));
544→          const tail = points[points.length - 1];
545→          cursorEl.setAttribute("cx", tail.x.toFixed(2));
546→          cursorEl.setAttribute("cy", tail.y.toFixed(2));
547→          cursorEl.style.opacity = "1";
548→        };
549→
550→        const sleep = (ms, ticket) => new Promise((resolve) => {
551→          scheduleTimeout(() => resolve(!runtime.disposed && !document.hidden && runtime.heroVisible && ticket === runtime.patternLoopTicket), ms);
552→        });
553→
554→        const animateSegment = (stablePoints, fromPoint, toPoint, durationMs, ticket) => new Promise((resolve) => {
555→          const startedAt = performance.now();
556→          const tick = (now) => {
557→            if (runtime.disposed || document.hidden || !runtime.heroVisible || ticket !== runtime.patternLoopTicket) {
558→              resolve(false);
559→              return;
560→            }
561→            const progress = Math.min(1, (now - startedAt) / durationMs);
562→            const x = fromPoint.x + ((toPoint.x - fromPoint.x) * progress);
563→            const y = fromPoint.y + ((toPoint.y - fromPoint.y) * progress);
564→            setPath([...stablePoints, { x, y }]);
565→            if (progress < 1) {
566→              window.requestAnimationFrame(tick);
567→            } else {
568→              resolve(true);
569→            }
570→          };
571→          window.requestAnimationFrame(tick);
572→        });
