574→        const runSequence = async (sequence, ticket) => {
575→          dots.forEach((dot) => dot.classList.remove("active"));
576→          setPath([]);
577→          const points = [];
578→          computeNodeCenters();
579→          if (!(await sleep(250, ticket))) return false;
580→          if (sequence.length === 0) return true;
581→
582→          const firstNode = sequence[0];
583→          dots[firstNode].classList.add("active");
584→          points.push(runtime.nodeCenters[firstNode]);
585→          setPath(points);
586→
587→          for (let i = 1; i < sequence.length; i += 1) {
588→            const prevPoint = runtime.nodeCenters[sequence[i - 1]];
589→            const nextPoint = runtime.nodeCenters[sequence[i]];
590→            const stablePoints = points.slice(0);
591→            const segmentOk = await animateSegment(stablePoints, prevPoint, nextPoint, 170, ticket);
