602→          phoneScreen.classList.add("mockup-vault");
603→          if (!(await sleep(5000, ticket))) return false;
604→          phoneScreen.classList.remove("mockup-vault");
605→
606→          dots.forEach((dot) => dot.classList.remove("active"));
607→          setPath([]);
608→          return sleep(430, ticket);
609→        };
610→
611→        const animatePatternLoop = async (ticket) => {
612→          if (!(await sleep(700, ticket))) return;
613→          while (!runtime.disposed && !document.hidden && runtime.heroVisible && ticket === runtime.patternLoopTicket) {
614→            const sequence = runtime.sequences[runtime.sequenceIndex];
615→            const ok = await runSequence(sequence, ticket);
616→            if (!ok) break;
617→            runtime.sequenceIndex = (runtime.sequenceIndex + 1) % runtime.sequences.length;
618→          }
619→          if (ticket === runtime.patternLoopTicket) {
620→            runtime.patternLoopRunning = false;
621→          }
622→        };
623→
624→        const startPatternLoop = () => {
625→          if (runtime.disposed || document.hidden || !runtime.heroVisible || runtime.patternLoopRunning) return;
626→          runtime.patternLoopRunning = true;
627→          runtime.patternLoopTicket += 1;
628→          animatePatternLoop(runtime.patternLoopTicket).catch(() => {
629→            runtime.patternLoopRunning = false;
630→          });
631→        };
632→
633→        const stopPatternLoop = () => {
634→          runtime.patternLoopTicket += 1;
635→          runtime.patternLoopRunning = false;
636→          dots.forEach((dot) => dot.classList.remove("active"));
637→          setPath([]);
638→          phoneScreen.classList.remove("mockup-unlocking");
639→          phoneScreen.classList.remove("mockup-vault");
640→        };
641→
642→        const pause = () => {
643→          stopDrawLoop();
644→          stopPatternLoop();
645→          clearScheduledTimeout(runtime.minuteTimerId);
646→          runtime.minuteTimerId = 0;
647→          clearScheduledTimeout(runtime.drawingResetTimerId);
648→          runtime.drawingResetTimerId = 0;
649→          runtime.drawingPath = false;
650→        };
651→
652→        const resume = () => {
653→          if (runtime.disposed || document.hidden || !runtime.heroVisible) return;
654→          resize();
655→          updateCanvasTheme();
656→          startDrawLoop();
657→          scheduleMinuteTick();
658→          startPatternLoop();
659→        };
660→
661→        const dispose = () => {
662→          if (runtime.disposed) return;
663→          runtime.disposed = true;
664→          pause();
665→          clearAllTimeouts();
666→        };
667→
668→        const onVisibility = () => {
669→          if (document.hidden || !runtime.heroVisible) {
670→            pause();
671→          } else {
672→            resume();
673→          }
674→        };
675→
676→        const onThemeChange = () => {
677→          updateCanvasTheme();
678→        };
679→
680→        const heroSection = document.querySelector(".hero");
681→        let heroObserver = null;
682→
683→        if (heroSection && typeof IntersectionObserver !== "undefined") {
684→          heroObserver = new IntersectionObserver((entries) => {
685→            const entry = entries[0];
686→            runtime.heroVisible = Boolean(entry && entry.isIntersecting);
687→            if (runtime.heroVisible && !document.hidden) {
688→              resume();
689→            } else {
690→              pause();
691→            }
692→          }, { threshold: 0.05 });
693→          heroObserver.observe(heroSection);
694→        } else {
695→          runtime.heroVisible = true;
696→        }
697→
698→        document.addEventListener("visibilitychange", onVisibility);
699→        window.addEventListener("pageshow", resume);
700→        window.addEventListener("vaultaire-themechange", onThemeChange);
701→        window.addEventListener("resize", scheduleResize);
702→
703→        this._pushCleanup(() => {
704→          document.removeEventListener("visibilitychange", onVisibility);
705→          window.removeEventListener("pageshow", resume);
706→          window.removeEventListener("vaultaire-themechange", onThemeChange);
707→          window.removeEventListener("resize", scheduleResize);
