/* ── Home Page Styles ── */
/* Shared header/nav/footer/buttons/mobile-nav are in site-shared.css */

.page-home {
  --bg-light: #2A2D49;
  --surface-light: #3A3D55;
  --text-light: #D6D8EC;
  --phone-frame-bg: #06070E;
  --phone-screen-bg: #20233C;
  --phone-screen-border: rgba(204, 195, 248, 0.18);
  --phone-text-primary: #E8E8F0;
  --phone-text-secondary: rgba(232, 232, 240, 0.78);
  --phone-dot-inactive: rgba(232, 232, 240, 0.39);
  --phone-dot-border: rgba(232, 232, 240, 0.23);
  --phone-dot-active: #CCC3F8;
  --phone-line-color: rgba(204, 195, 248, 0.70);
  --phone-shield-fill: rgba(232, 232, 240, 0.76);
  --phone-shield-cut: #20233C;
  --phone-action-primary: rgba(232, 232, 240, 0.78);
  --phone-action-secondary: rgba(232, 232, 240, 0.55);
  --phone-unlock-pixel: rgba(66, 108, 195, 0.82);
  --phone-unlock-pixel-dim: rgba(66, 108, 195, 0.30);
  --ease-cinematic: cubic-bezier(0.19, 1, 0.22, 1);
  overflow-x: hidden;
}

:root[data-theme='light'] .page-home {
  --bg-light: #E3E4F4;
  --surface-light: #FFFFFF;
  --text-light: #4D4F67;
  --phone-frame-bg: #B8BCD2;
  --phone-screen-bg: #D1D1E9;
  --phone-screen-border: rgba(31, 13, 119, 0.22);
  --phone-text-primary: #2B2C34;
  --phone-text-secondary: rgba(43, 44, 52, 0.70);
  --phone-dot-inactive: rgba(43, 44, 52, 0.35);
  --phone-dot-border: rgba(43, 44, 52, 0.21);
  --phone-dot-active: #1F0D77;
  --phone-line-color: rgba(31, 13, 119, 0.70);
  --phone-shield-fill: rgba(43, 44, 52, 0.3);
  --phone-shield-cut: #D1D1E9;
  --phone-action-primary: rgba(43, 44, 52, 0.70);
  --phone-action-secondary: rgba(43, 44, 52, 0.50);
  --phone-unlock-pixel: rgba(31, 13, 119, 0.82);
  --phone-unlock-pixel-dim: rgba(31, 13, 119, 0.30);
}

.page-home .container {
  position: relative;
  z-index: 2;
}

.page-home .btn-secondary {
  background: transparent;
  border: 2px solid var(--text);
  color: var(--text);
}

.page-home .btn-secondary:hover {
  background: var(--surface);
  border-color: var(--accent);
}

/* ── Hero ── */
.hero {
  position: relative;
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding-top: max(108px, calc(80px + env(safe-area-inset-top)));
  overflow: hidden;
}

.hero-bg-x {
  position: absolute;
  top: 0; left: 0; width: 100%; height: 100%;
  z-index: 0;
  pointer-events: none;
}

.slash-1 {
  position: absolute;
  width: 200%;
  height: 2px;
  top: 0;
  left: -50%;
  transform: rotate(35deg);
  transform-origin: center;
  background: linear-gradient(90deg, transparent, var(--highlight), transparent);
  opacity: 0.8;
}

.slash-2 {
  position: absolute;
  width: 200%;
  height: 2px;
  top: 0;
  left: -50%;
  transform: rotate(-35deg);
  transform-origin: center;
  background: linear-gradient(90deg, transparent, var(--highlight), transparent);
  opacity: 0.8;
}

#pattern-canvas {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  opacity: 0.3;
}

.hero-content {
  position: relative;
  z-index: 2;
  width: 100%;
}

.hero-layout {
  display: grid;
  grid-template-columns: 1.2fr 1fr;
  align-items: center;
  gap: 40px;
  width: 100%;
}

.hero-title-stack {
  font-family: var(--font-display);
  font-size: clamp(3.5rem, 7vw, 7rem);
  line-height: 0.85;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: -0.03em;
  display: flex;
  flex-direction: column;
}

.stack-layer {
  display: block;
  transition: transform 0.5s var(--ease-cinematic);
}

.stack-1 {
  color: transparent;
  -webkit-text-stroke: 1px var(--text-secondary);
  opacity: 0.5;
  transform: translateX(-20px);
}

.stack-2 {
  color: var(--accent);
  opacity: 0.8;
}

.stack-3 {
  color: var(--text);
  position: relative;
  z-index: 10;
}

.hero-sub {
  margin-top: 2rem;
  font-size: 1.25rem;
  color: var(--text);
  opacity: 0.8;
  max-width: 600px;
  border-left: 2px solid var(--highlight);
  padding-left: 20px;
}

.hero-actions {
  display: flex;
  gap: 20px;
  flex-wrap: wrap;
  margin-top: 48px;
}

/* ── Phone Mockup ── */
.mockup-container {
  position: relative;
  --mockup-h: min(700px, calc(100vh - 190px));
  width: min(336px, calc(var(--mockup-h) * 0.48), 100%);
  aspect-ratio: 336 / 700;
  height: var(--mockup-h);
  max-height: 700px;
  max-width: 100%;
  margin-left: auto;
}

.phone-frame {
  width: 100%;
  height: 100%;
  background: var(--phone-frame-bg);
  border-radius: 46px;
  padding: 10px;
  box-shadow: 0 38px 90px rgba(0, 0, 0, 0.46), 0 0 0 1px rgba(255, 255, 255, 0.06);
  position: relative;
  transition: transform 220ms ease;
}

.phone-frame:hover {
  transform: translateY(-2px);
}

.phone-screen {
  width: 100%;
  height: 100%;
  background: var(--phone-screen-bg);
  border-radius: 36px;
  overflow: hidden;
  position: relative;
  border: 1px solid var(--phone-screen-border);
  padding: 12px 12px 18px;
}

.phone-status {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 0.88rem;
  font-weight: 600;
  margin: 2px 8px 8px;
  color: var(--phone-text-primary);
  opacity: 0.9;
  position: relative;
  min-height: 28px;
  z-index: 5;
}

.phone-dynamic-island {
  position: absolute;
  left: 50%;
  top: 1px;
  transform: translateX(-50%);
  width: min(92px, 32%);
  height: 24px;
  border-radius: 999px;
  background: #05060A;
}

.status-right {
  display: inline-flex;
  align-items: center;
  gap: 5px;
  flex-shrink: 0;
}

.status-icon-svg {
  width: 14px;
  height: 14px;
  flex-shrink: 0;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  color: currentColor;
  opacity: 0.95;
}

.status-icon-svg svg {
  width: 100%;
  height: 100%;
  display: block;
}

.status-battery {
  width: 20px;
  height: 10px;
  flex-shrink: 0;
  border-radius: 2px;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 62%, transparent 38%);
  position: relative;
}

.status-battery::before {
  content: '';
  position: absolute;
  right: -3px;
  top: 2px;
  width: 2px;
  height: 4px;
  border-radius: 1px;
  background: color-mix(in srgb, var(--phone-text-primary) 62%, transparent 38%);
}

.status-battery::after {
  content: '';
  position: absolute;
  top: 1px;
  left: 1px;
  width: 12px;
  height: 6px;
  border-radius: 1px;
  background: color-mix(in srgb, var(--phone-dot-active) 65%, var(--phone-screen-bg) 35%);
}

.mockup-stage {
  position: absolute;
  inset: 46px 12px 18px;
  border-radius: 28px;
  transition: opacity 220ms ease;
}

.mockup-lock-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  opacity: 1;
  pointer-events: auto;
}

.mockup-unlock-state {
  opacity: 0;
  pointer-events: none;
  display: grid;
  place-items: center;
}

.mockup-vault-state {
  opacity: 0;
  pointer-events: none;
  display: flex;
  flex-direction: column;
  gap: 10px;
  padding: 2px 2px 0;
  min-height: 0;
}

.phone-screen.mockup-unlocking .mockup-lock-state {
  opacity: 0;
  pointer-events: none;
}

.phone-screen.mockup-unlocking .mockup-unlock-state {
  opacity: 1;
}

.phone-screen.mockup-vault .mockup-lock-state,
.phone-screen.mockup-vault .mockup-unlock-state {
  opacity: 0;
  pointer-events: none;
}

.phone-screen.mockup-vault .mockup-vault-state {
  opacity: 1;
  pointer-events: auto;
}

.lock-header {
  margin-top: 70px;
  display: grid;
  gap: 10px;
  place-items: center;
}

.lock-shield {
  width: 44px;
  height: 44px;
  border-radius: 12px;
  background: color-mix(in srgb, var(--phone-text-primary) 10%, transparent 90%);
  display: grid;
  place-items: center;
  color: color-mix(in srgb, var(--phone-text-primary) 78%, transparent 22%);
  font-size: 1rem;
}

.lock-title {
  font-family: var(--font-body);
  font-size: 1.25rem;
  font-weight: 600;
  text-transform: none;
  letter-spacing: -0.01em;
  color: var(--phone-text-primary);
  margin: 0;
}

.pattern-lock-zone {
  width: 226px;
  height: 226px;
  position: relative;
  margin-top: 50px;
  margin-bottom: 8px;
}

.pattern-grid {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 34px;
  position: relative;
  z-index: 5;
}

.grid-dot {
  width: 18px;
  height: 18px;
  border-radius: 50%;
  background: var(--phone-dot-inactive);
  border: 1px solid var(--phone-dot-border);
  transition: background-color 180ms ease, box-shadow 180ms ease;
}

.grid-dot.active {
  background: var(--phone-dot-active);
  border-color: transparent;
  box-shadow: 0 0 0 2px color-mix(in srgb, var(--phone-dot-active) 30%, transparent 70%);
}

.pattern-svg {
  position: absolute;
  inset: 0;
  pointer-events: none;
  z-index: 4;
}

.pattern-line {
  stroke: var(--phone-line-color);
  stroke-width: 4;
  stroke-linecap: round;
  stroke-linejoin: round;
  fill: none;
  filter: drop-shadow(0 0 6px color-mix(in srgb, var(--phone-dot-active) 52%, transparent 48%));
}

.pattern-cursor {
  fill: var(--phone-dot-active);
  filter: drop-shadow(0 0 3px color-mix(in srgb, var(--phone-dot-active) 62%, transparent 38%));
  opacity: 0;
  transition: opacity 120ms ease;
}

.lock-actions {
  margin-top: auto;
  margin-bottom: 20px;
  display: grid;
  gap: 12px;
  place-items: center;
}

.lock-action {
  color: var(--phone-text-secondary);
  font-size: 0.95rem;
  font-weight: 500;
  opacity: 0.85;
}

.lock-action-primary {
  opacity: 1;
  color: var(--phone-action-primary);
}

.lock-action-secondary {
  opacity: 1;
  color: var(--phone-action-secondary);
}

.unlock-wrap {
  display: grid;
  gap: 14px;
  place-items: center;
  transform: translateY(-8px);
}

.unlock-pixel {
  width: 14px;
  height: 14px;
  border-radius: 2px;
  background: var(--phone-unlock-pixel-dim);
  animation: unlockPulse 1s infinite ease-in-out;
}

.unlock-text {
  color: var(--phone-text-secondary);
  font-size: 1.02rem;
  font-weight: 500;
}

.vault-app-header {
  width: 100%;
  display: grid;
  grid-template-columns: 34px 1fr 34px;
  align-items: center;
  gap: 8px;
  margin-top: 2px;
}

.vault-app-btn {
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 22%, transparent 78%);
  background: color-mix(in srgb, var(--phone-text-primary) 8%, transparent 92%);
  color: var(--phone-text-primary);
  display: grid;
  place-items: center;
  font-size: 0.84rem;
}

.vault-app-title {
  margin: 0;
  color: var(--phone-text-primary);
  font-size: 0.92rem;
  font-weight: 600;
  text-align: center;
}

.vault-search-row {
  width: 100%;
  display: grid;
  grid-template-columns: 1fr 30px 30px;
  gap: 6px;
  align-items: center;
  margin-top: 2px;
}

.vault-search {
  height: 32px;
  border-radius: 9px;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 14%, transparent 86%);
  background: color-mix(in srgb, var(--phone-text-primary) 6%, transparent 94%);
  color: var(--phone-text-secondary);
  font-size: 0.82rem;
  display: flex;
  align-items: center;
  padding: 0 10px;
}

.vault-search-icon {
  margin-right: 6px;
  opacity: 0.7;
  font-size: 0.72rem;
}

.vault-chip {
  height: 30px;
  border-radius: 8px;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 18%, transparent 82%);
  background: color-mix(in srgb, var(--phone-text-primary) 8%, transparent 92%);
  color: var(--phone-text-secondary);
  display: grid;
  place-items: center;
  font-size: 0.78rem;
}

.vault-section-label {
  color: var(--phone-text-secondary);
  font-size: 0.82rem;
  font-weight: 600;
  margin-top: 1px;
  padding-left: 2px;
  margin-bottom: 2px;
}

.vault-grid {
  width: 100%;
  flex: 1;
  min-height: 0;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: repeat(6, minmax(0, 1fr));
  gap: 2px;
  border-radius: 6px;
  overflow: hidden;
  align-content: stretch;
}

.vault-cell {
  background: color-mix(in srgb, var(--phone-text-primary) 6%, transparent 94%);
  position: relative;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 8%, transparent 92%);
  overflow: hidden;
}

.vault-thumb {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  filter: saturate(1.08) contrast(1.02);
}

.vault-cell::after {
  content: '';
  position: absolute;
  inset: 0;
  background: linear-gradient(180deg, transparent 58%, rgba(8, 10, 20, 0.35) 100%);
  pointer-events: none;
}

.vault-cell-time {
  position: absolute;
  left: 3px;
  bottom: 3px;
  font-size: 0.54rem;
  color: rgba(255, 255, 255, 0.9);
  background: rgba(20, 22, 36, 0.75);
  border-radius: 3px;
  padding: 0 3px;
  z-index: 2;
}

.vault-fab {
  position: absolute;
  right: 10px;
  bottom: 12px;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: none;
  background: color-mix(in srgb, var(--phone-dot-active) 84%, white 16%);
  color: color-mix(in srgb, var(--phone-screen-bg) 82%, black 18%);
  padding: 0;
  display: grid;
  place-items: center;
  box-shadow: 0 6px 14px color-mix(in srgb, var(--phone-dot-active) 32%, transparent 68%);
}

.vault-fab-plus {
  position: relative;
  width: 16px;
  height: 16px;
  display: block;
}

.vault-fab-plus::before,
.vault-fab-plus::after {
  content: '';
  position: absolute;
  left: 50%;
  top: 50%;
  width: 16px;
  height: 3px;
  border-radius: 3px;
  background: currentColor;
  transform: translate(-50%, -50%);
}

.vault-fab-plus::after {
  transform: translate(-50%, -50%) rotate(90deg);
}

/* ── Trust Strip ── */
.trust-strip {
  background: var(--highlight);
  padding: 24px 0;
  position: relative;
  z-index: 3;
  transform: skewY(-2deg);
  margin-top: 0;
  border-top: 1px solid var(--surface-light);
  border-bottom: 1px solid var(--surface-light);
}

.trust-content {
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  transform: skewY(2deg);
  gap: 20px;
}

.trust-item {
  font-family: var(--font-display);
  font-weight: 700;
  text-transform: uppercase;
  color: #FFFFFF;
  font-size: 1.1rem;
  display: flex;
  align-items: center;
  gap: 10px;
}

:root[data-theme='light'] .trust-item {
  color: #1A1B2E;
}

.trust-item::before {
  content: '\00d7';
  color: var(--bg);
  font-size: 1.5rem;
  font-weight: 900;
}

:root[data-theme='light'] .trust-item::before {
  color: #FFFFFF;
}

/* ── Features ── */
.features {
  padding: 170px 0 120px;
  background: var(--bg-light);
  color: var(--text-light);
  position: relative;
  margin-top: -50px;
  clip-path: polygon(0 50px, 100% 0, 100% calc(100% - 50px), 0 100%);
}

.section-header {
  margin-bottom: 80px;
  text-align: center;
}

.section-header h2 {
  font-size: 3rem;
  color: var(--text);
  margin-bottom: 16px;
}

.section-header-subtitle {
  color: var(--text-light);
  max-width: 600px;
  margin: 0 auto;
}

.feature-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
}

.feature-card {
  background: var(--surface-light);
  padding: 40px;
  position: relative;
  clip-path: polygon(
    20px 0, 100% 0,
    100% calc(100% - 20px),
    calc(100% - 20px) 100%,
    0 100%, 0 20px
  );
  transition: transform 0.3s ease;
  box-shadow: 0 20px 40px rgba(32, 35, 60, 0.1);
}

.feature-card:hover {
  transform: translateY(-10px);
}

.feature-card::after {
  content: '';
  position: absolute;
  top: 0; right: 0;
  width: 40px;
  height: 40px;
  background: linear-gradient(to bottom left, var(--highlight) 50%, transparent 50%);
}

.feature-icon {
  font-size: 2rem;
  margin-bottom: 24px;
  color: var(--accent);
}

.feature-card h3 {
  font-size: 1.5rem;
  margin-bottom: 16px;
  color: var(--text);
}

.feature-card p {
  color: var(--text-light);
  font-size: 1rem;
  line-height: 1.7;
}

/* ── Timeline ── */
.timeline-section {
  padding: 120px 0;
  background: var(--bg);
}

.timeline {
  position: relative;
  max-width: 800px;
  margin: 0 auto;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: rgba(255,255,255,0.1);
  transform: translateX(-50%);
}

.step {
  position: relative;
  margin-bottom: 80px;
  width: 50%;
}

.step:nth-child(odd) {
  left: 0;
  padding-right: 60px;
  text-align: right;
}

.step:nth-child(even) {
  left: 50%;
  padding-left: 60px;
}

.step-marker {
  position: absolute;
  top: 0;
  width: 20px;
  height: 20px;
  background: var(--bg);
  border: 2px solid var(--highlight);
  transform: rotate(45deg);
  z-index: 2;
}

.step:nth-child(odd) .step-marker {
  right: -11px;
}

.step:nth-child(even) .step-marker {
  left: -11px;
}

.step-number {
  font-family: var(--font-display);
  font-size: 4rem;
  color: rgba(255,255,255,0.05);
  font-weight: 800;
  position: absolute;
  top: -20px;
  line-height: 1;
}

.step:nth-child(odd) .step-number { right: 40px; }
.step:nth-child(even) .step-number { left: 40px; }

.step h3 {
  font-size: 1.8rem;
  margin-bottom: 12px;
  color: var(--text);
  position: relative;
}

.step p {
  color: var(--accent);
}

/* ── Security ── */
.security-section {
  background: var(--surface);
  padding: 100px 0;
  border-top: 1px solid rgba(255,255,255,0.05);
}

.tech-specs {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  align-items: center;
}

.tech-diagram {
  background: var(--bg);
  border: 1px solid var(--accent);
  padding: 40px;
  position: relative;
}

.tech-diagram::before, .tech-diagram::after {
  content: '';
  position: absolute;
  width: 20px;
  height: 20px;
  border-color: var(--highlight);
  border-style: solid;
  transition: all 0.5s;
}

.tech-diagram::before { top: -1px; left: -1px; border-width: 2px 0 0 2px; }
.tech-diagram::after { bottom: -1px; right: -1px; border-width: 0 2px 2px 0; }

.code-block {
  font-family: monospace;
  color: var(--accent);
  line-height: 1.8;
  font-size: 0.9rem;
}

.code-key { color: var(--highlight); }
.code-val { color: var(--text); }
.code-comment { color: var(--text-secondary); }

.tech-content h2 {
  font-size: 2.5rem;
  margin-bottom: 24px;
}

.tech-lead {
  color: var(--accent);
  font-size: 1.1rem;
}

.tech-content ul {
  margin-top: 30px;
}

.tech-content li {
  margin-bottom: 16px;
  padding-left: 24px;
  position: relative;
  color: var(--text);
}

.tech-content li::before {
  content: '/';
  color: var(--highlight);
  position: absolute;
  left: 0;
  font-weight: bold;
}

/* ── FAQ ── */
.faq-section {
  padding: 120px 0;
  background: var(--bg);
}

.faq-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 24px;
  width: min(100%, 800px);
  max-width: 800px;
  margin: 0 auto;
}

.faq-item {
  border: 1px solid color-mix(in srgb, var(--text) 12%, transparent 88%);
  background: color-mix(in srgb, var(--surface) 58%, transparent 42%);
  transition: 0.3s;
}

.faq-item[open] {
  border-color: var(--highlight);
  background: var(--surface);
}

.faq-summary {
  padding: 24px;
  cursor: pointer;
  font-family: var(--font-display);
  font-size: 1.2rem;
  font-weight: 500;
  display: flex;
  justify-content: space-between;
  align-items: center;
  list-style: none;
}

.faq-summary::-webkit-details-marker { display: none; }

.faq-summary::after {
  content: '+';
  font-size: 1.5rem;
  color: var(--highlight);
  transition: transform 0.3s;
}

.faq-item[open] .faq-summary::after {
  transform: rotate(45deg);
}

.faq-content {
  padding: 0 24px 24px 24px;
  color: var(--text);
  opacity: 0.9;
}

/* ── Final CTA ── */
.final-cta {
  padding: 140px 0;
  text-align: center;
  background: radial-gradient(circle at center, var(--accent) 0%, var(--bg) 70%);
  position: relative;
  overflow: hidden;
}

.final-cta h2 {
  font-size: 4rem;
  margin-bottom: 30px;
  line-height: 1;
}

/* ── Grid Overlay ── */
.grid-overlay {
  position: absolute;
  width: 100%; height: 100%;
  background-image: radial-gradient(var(--text) 1px, transparent 1px);
  background-size: 40px 40px;
  opacity: 0.05;
  z-index: 1;
  pointer-events: none;
}

/* ── Responsive ── */
@media (max-width: 900px) {
  .hero-layout {
    grid-template-columns: 1fr;
    text-align: center;
  }

  .hero-sub { border-left: none; padding-left: 0; margin-inline: auto; }
  .hero-actions { justify-content: center; }

  .mockup-container {
    --mockup-h: clamp(320px, calc(100vh - 260px), 700px);
    margin: 24px auto 28px;
    height: var(--mockup-h);
    width: min(100%, calc(var(--mockup-h) * 0.48));
    max-width: 336px;
  }

  .hero-title-stack { align-items: center; }
  .stack-1 { transform: translateX(0); }
}

@media (max-width: 768px) {
  .hero {
    min-height: auto;
    padding-top: max(112px, calc(74px + env(safe-area-inset-top) + 28px));
    padding-bottom: 40px;
  }

  .hero-layout {
    gap: 24px;
  }

  .hero-title-stack { font-size: clamp(2.5rem, 8vw, 4rem); }

  .hero-sub {
    margin-top: 1.35rem;
    font-size: 1.05rem;
    line-height: 1.5;
    max-width: 36ch;
  }

  .hero-actions {
    margin-top: 28px;
    width: 100%;
    gap: 12px;
  }

  .hero-actions .btn {
    width: 100%;
  }

  .timeline::before { left: 20px; }
  .step { width: 100%; padding-left: 60px !important; text-align: left !important; left: 0 !important; }
  .step:nth-child(odd) .step-marker, .step:nth-child(even) .step-marker { left: 9px; }
  .step-number { left: 60px !important; }
  .tech-specs { grid-template-columns: 1fr; }
  .trust-content { flex-direction: column; }

  .trust-strip {
    margin-top: 0;
  }

  .features {
    margin-top: -36px;
    padding-top: 136px;
    clip-path: polygon(0 36px, 100% 0, 100% calc(100% - 40px), 0 100%);
  }

  .mockup-container {
    width: min(100%, 340px);
    height: auto;
    margin: 20px auto 32px;
  }

  .pattern-lock-zone {
    width: 218px;
    height: 218px;
    margin-top: 42px;
  }

  .lock-title {
    font-size: 1.15rem;
  }
}

@media (max-height: 820px) and (min-width: 901px) {
  .hero {
    padding-bottom: 56px;
  }

  .trust-strip {
    margin-top: 0;
  }
}

@media (max-height: 700px) and (max-width: 900px) {
  .hero {
    padding-bottom: 56px;
  }

  .trust-strip {
    margin-top: 0;
  }
}

@keyframes unlockPulse {
  0% {
    opacity: 0.42;
    transform: scale(0.86);
    background: var(--phone-unlock-pixel-dim);
  }
  50% {
    opacity: 1;
    transform: scale(1);
    background: var(--phone-unlock-pixel);
  }
  100% {
    opacity: 0.42;
    transform: scale(0.86);
    background: var(--phone-unlock-pixel-dim);
  }
}

toolUseResult.content:
/* ── Home Page Styles ── */
/* Shared header/nav/footer/buttons/mobile-nav are in site-shared.css */

.page-home {
  --bg-light: #2A2D49;
  --surface-light: #3A3D55;
  --text-light: #D6D8EC;
  --phone-frame-bg: #06070E;
  --phone-screen-bg: #20233C;
  --phone-screen-border: rgba(204, 195, 248, 0.18);
  --phone-text-primary: #E8E8F0;
  --phone-text-secondary: rgba(232, 232, 240, 0.78);
  --phone-dot-inactive: rgba(232, 232, 240, 0.39);
  --phone-dot-border: rgba(232, 232, 240, 0.23);
  --phone-dot-active: #CCC3F8;
  --phone-line-color: rgba(204, 195, 248, 0.70);
  --phone-shield-fill: rgba(232, 232, 240, 0.76);
  --phone-shield-cut: #20233C;
  --phone-action-primary: rgba(232, 232, 240, 0.78);
  --phone-action-secondary: rgba(232, 232, 240, 0.55);
  --phone-unlock-pixel: rgba(66, 108, 195, 0.82);
  --phone-unlock-pixel-dim: rgba(66, 108, 195, 0.30);
  --ease-cinematic: cubic-bezier(0.19, 1, 0.22, 1);
  overflow-x: hidden;
}

:root[data-theme='light'] .page-home {
  --bg-light: #E3E4F4;
  --surface-light: #FFFFFF;
  --text-light: #4D4F67;
  --phone-frame-bg: #B8BCD2;
  --phone-screen-bg: #D1D1E9;
  --phone-screen-border: rgba(31, 13, 119, 0.22);
  --phone-text-primary: #2B2C34;
  --phone-text-secondary: rgba(43, 44, 52, 0.70);
  --phone-dot-inactive: rgba(43, 44, 52, 0.35);
  --phone-dot-border: rgba(43, 44, 52, 0.21);
  --phone-dot-active: #1F0D77;
  --phone-line-color: rgba(31, 13, 119, 0.70);
  --phone-shield-fill: rgba(43, 44, 52, 0.3);
  --phone-shield-cut: #D1D1E9;
  --phone-action-primary: rgba(43, 44, 52, 0.70);
  --phone-action-secondary: rgba(43, 44, 52, 0.50);
  --phone-unlock-pixel: rgba(31, 13, 119, 0.82);
  --phone-unlock-pixel-dim: rgba(31, 13, 119, 0.30);
}

.page-home .container {
  position: relative;
  z-index: 2;
}

.page-home .btn-secondary {
  background: transparent;
  border: 2px solid var(--text);
  color: var(--text);
}

.page-home .btn-secondary:hover {
  background: var(--surface);
  border-color: var(--accent);
}

/* ── Hero ── */
.hero {
  position: relative;
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding-top: max(108px, calc(80px + env(safe-area-inset-top)));
  overflow: hidden;
}

.hero-bg-x {
  position: absolute;
  top: 0; left: 0; width: 100%; height: 100%;
  z-index: 0;
  pointer-events: none;
}

.slash-1 {
  position: absolute;
  width: 200%;
  height: 2px;
  top: 0;
  left: -50%;
  transform: rotate(35deg);
  transform-origin: center;
  background: linear-gradient(90deg, transparent, var(--highlight), transparent);
  opacity: 0.8;
}

.slash-2 {
  position: absolute;
  width: 200%;
  height: 2px;
  top: 0;
  left: -50%;
  transform: rotate(-35deg);
  transform-origin: center;
  background: linear-gradient(90deg, transparent, var(--highlight), transparent);
  opacity: 0.8;
}

#pattern-canvas {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  opacity: 0.3;
}

.hero-content {
  position: relative;
  z-index: 2;
  width: 100%;
}

.hero-layout {
  display: grid;
  grid-template-columns: 1.2fr 1fr;
  align-items: center;
  gap: 40px;
  width: 100%;
}

.hero-title-stack {
  font-family: var(--font-display);
  font-size: clamp(3.5rem, 7vw, 7rem);
  line-height: 0.85;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: -0.03em;
  display: flex;
  flex-direction: column;
}

.stack-layer {
  display: block;
  transition: transform 0.5s var(--ease-cinematic);
}

.stack-1 {
  color: transparent;
  -webkit-text-stroke: 1px var(--text-secondary);
  opacity: 0.5;
  transform: translateX(-20px);
}

.stack-2 {
  color: var(--accent);
  opacity: 0.8;
}

.stack-3 {
  color: var(--text);
  position: relative;
  z-index: 10;
}

.hero-sub {
  margin-top: 2rem;
  font-size: 1.25rem;
  color: var(--text);
  opacity: 0.8;
  max-width: 600px;
  border-left: 2px solid var(--highlight);
  padding-left: 20px;
}

.hero-actions {
  display: flex;
  gap: 20px;
  flex-wrap: wrap;
  margin-top: 48px;
}

/* ── Phone Mockup ── */
.mockup-container {
  position: relative;
  --mockup-h: min(700px, calc(100vh - 190px));
  width: min(336px, calc(var(--mockup-h) * 0.48), 100%);
  aspect-ratio: 336 / 700;
  height: var(--mockup-h);
  max-height: 700px;
  max-width: 100%;
  margin-left: auto;
}

.phone-frame {
  width: 100%;
  height: 100%;
  background: var(--phone-frame-bg);
  border-radius: 46px;
  padding: 10px;
  box-shadow: 0 38px 90px rgba(0, 0, 0, 0.46), 0 0 0 1px rgba(255, 255, 255, 0.06);
  position: relative;
  transition: transform 220ms ease;
}

.phone-frame:hover {
  transform: translateY(-2px);
}

.phone-screen {
  width: 100%;
  height: 100%;
  background: var(--phone-screen-bg);
  border-radius: 36px;
  overflow: hidden;
  position: relative;
  border: 1px solid var(--phone-screen-border);
  padding: 12px 12px 18px;
}

.phone-status {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 0.88rem;
  font-weight: 600;
  margin: 2px 8px 8px;
  color: var(--phone-text-primary);
  opacity: 0.9;
  position: relative;
  min-height: 28px;
  z-index: 5;
}

.phone-dynamic-island {
  position: absolute;
  left: 50%;
  top: 1px;
  transform: translateX(-50%);
  width: min(92px, 32%);
  height: 24px;
  border-radius: 999px;
  background: #05060A;
}

.status-right {
  display: inline-flex;
  align-items: center;
  gap: 5px;
  flex-shrink: 0;
}

.status-icon-svg {
  width: 14px;
  height: 14px;
  flex-shrink: 0;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  color: currentColor;
  opacity: 0.95;
}

.status-icon-svg svg {
  width: 100%;
  height: 100%;
  display: block;
}

.status-battery {
  width: 20px;
  height: 10px;
  flex-shrink: 0;
  border-radius: 2px;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 62%, transparent 38%);
  position: relative;
}

.status-battery::before {
  content: '';
  position: absolute;
  right: -3px;
  top: 2px;
  width: 2px;
  height: 4px;
  border-radius: 1px;
  background: color-mix(in srgb, var(--phone-text-primary) 62%, transparent 38%);
}

.status-battery::after {
  content: '';
  position: absolute;
  top: 1px;
  left: 1px;
  width: 12px;
  height: 6px;
  border-radius: 1px;
  background: color-mix(in srgb, var(--phone-dot-active) 65%, var(--phone-screen-bg) 35%);
}

.mockup-stage {
  position: absolute;
  inset: 46px 12px 18px;
  border-radius: 28px;
  transition: opacity 220ms ease;
}

.mockup-lock-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  opacity: 1;
  pointer-events: auto;
}

.mockup-unlock-state {
  opacity: 0;
  pointer-events: none;
  display: grid;
  place-items: center;
}

.mockup-vault-state {
  opacity: 0;
  pointer-events: none;
  display: flex;
  flex-direction: column;
  gap: 10px;
  padding: 2px 2px 0;
  min-height: 0;
}

.phone-screen.mockup-unlocking .mockup-lock-state {
  opacity: 0;
  pointer-events: none;
}

.phone-screen.mockup-unlocking .mockup-unlock-state {
  opacity: 1;
}

.phone-screen.mockup-vault .mockup-lock-state,
.phone-screen.mockup-vault .mockup-unlock-state {
  opacity: 0;
  pointer-events: none;
}

.phone-screen.mockup-vault .mockup-vault-state {
  opacity: 1;
  pointer-events: auto;
}

.lock-header {
  margin-top: 70px;
  display: grid;
  gap: 10px;
  place-items: center;
}

.lock-shield {
  width: 44px;
  height: 44px;
  border-radius: 12px;
  background: color-mix(in srgb, var(--phone-text-primary) 10%, transparent 90%);
  display: grid;
  place-items: center;
  color: color-mix(in srgb, var(--phone-text-primary) 78%, transparent 22%);
  font-size: 1rem;
}

.lock-title {
  font-family: var(--font-body);
  font-size: 1.25rem;
  font-weight: 600;
  text-transform: none;
  letter-spacing: -0.01em;
  color: var(--phone-text-primary);
  margin: 0;
}

.pattern-lock-zone {
  width: 226px;
  height: 226px;
  position: relative;
  margin-top: 50px;
  margin-bottom: 8px;
}

.pattern-grid {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 34px;
  position: relative;
  z-index: 5;
}

.grid-dot {
  width: 18px;
  height: 18px;
  border-radius: 50%;
  background: var(--phone-dot-inactive);
  border: 1px solid var(--phone-dot-border);
  transition: background-color 180ms ease, box-shadow 180ms ease;
}

.grid-dot.active {
  background: var(--phone-dot-active);
  border-color: transparent;
  box-shadow: 0 0 0 2px color-mix(in srgb, var(--phone-dot-active) 30%, transparent 70%);
}

.pattern-svg {
  position: absolute;
  inset: 0;
  pointer-events: none;
  z-index: 4;
}

.pattern-line {
  stroke: var(--phone-line-color);
  stroke-width: 4;
  stroke-linecap: round;
  stroke-linejoin: round;
  fill: none;
  filter: drop-shadow(0 0 6px color-mix(in srgb, var(--phone-dot-active) 52%, transparent 48%));
}

.pattern-cursor {
  fill: var(--phone-dot-active);
  filter: drop-shadow(0 0 3px color-mix(in srgb, var(--phone-dot-active) 62%, transparent 38%));
  opacity: 0;
  transition: opacity 120ms ease;
}

.lock-actions {
  margin-top: auto;
  margin-bottom: 20px;
  display: grid;
  gap: 12px;
  place-items: center;
}

.lock-action {
  color: var(--phone-text-secondary);
  font-size: 0.95rem;
  font-weight: 500;
  opacity: 0.85;
}

.lock-action-primary {
  opacity: 1;
  color: var(--phone-action-primary);
}

.lock-action-secondary {
  opacity: 1;
  color: var(--phone-action-secondary);
}

.unlock-wrap {
  display: grid;
  gap: 14px;
  place-items: center;
  transform: translateY(-8px);
}

.unlock-pixel {
  width: 14px;
  height: 14px;
  border-radius: 2px;
  background: var(--phone-unlock-pixel-dim);
  animation: unlockPulse 1s infinite ease-in-out;
}

.unlock-text {
  color: var(--phone-text-secondary);
  font-size: 1.02rem;
  font-weight: 500;
}

.vault-app-header {
  width: 100%;
  display: grid;
  grid-template-columns: 34px 1fr 34px;
  align-items: center;
  gap: 8px;
  margin-top: 2px;
}

.vault-app-btn {
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 22%, transparent 78%);
  background: color-mix(in srgb, var(--phone-text-primary) 8%, transparent 92%);
  color: var(--phone-text-primary);
  display: grid;
  place-items: center;
  font-size: 0.84rem;
}

.vault-app-title {
  margin: 0;
  color: var(--phone-text-primary);
  font-size: 0.92rem;
  font-weight: 600;
  text-align: center;
}

.vault-search-row {
  width: 100%;
  display: grid;
  grid-template-columns: 1fr 30px 30px;
  gap: 6px;
  align-items: center;
  margin-top: 2px;
}

.vault-search {
  height: 32px;
  border-radius: 9px;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 14%, transparent 86%);
  background: color-mix(in srgb, var(--phone-text-primary) 6%, transparent 94%);
  color: var(--phone-text-secondary);
  font-size: 0.82rem;
  display: flex;
  align-items: center;
  padding: 0 10px;
}

.vault-search-icon {
  margin-right: 6px;
  opacity: 0.7;
  font-size: 0.72rem;
}

.vault-chip {
  height: 30px;
  border-radius: 8px;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 18%, transparent 82%);
  background: color-mix(in srgb, var(--phone-text-primary) 8%, transparent 92%);
  color: var(--phone-text-secondary);
  display: grid;
  place-items: center;
  font-size: 0.78rem;
}

.vault-section-label {
  color: var(--phone-text-secondary);
  font-size: 0.82rem;
  font-weight: 600;
  margin-top: 1px;
  padding-left: 2px;
  margin-bottom: 2px;
}

.vault-grid {
  width: 100%;
  flex: 1;
  min-height: 0;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: repeat(6, minmax(0, 1fr));
  gap: 2px;
  border-radius: 6px;
  overflow: hidden;
  align-content: stretch;
}

.vault-cell {
  background: color-mix(in srgb, var(--phone-text-primary) 6%, transparent 94%);
  position: relative;
  border: 1px solid color-mix(in srgb, var(--phone-text-primary) 8%, transparent 92%);
  overflow: hidden;
}

.vault-thumb {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  filter: saturate(1.08) contrast(1.02);
}

.vault-cell::after {
  content: '';
  position: absolute;
  inset: 0;
  background: linear-gradient(180deg, transparent 58%, rgba(8, 10, 20, 0.35) 100%);
  pointer-events: none;
}

.vault-cell-time {
  position: absolute;
  left: 3px;
  bottom: 3px;
  font-size: 0.54rem;
  color: rgba(255, 255, 255, 0.9);
  background: rgba(20, 22, 36, 0.75);
  border-radius: 3px;
  padding: 0 3px;
  z-index: 2;
}

.vault-fab {
  position: absolute;
  right: 10px;
  bottom: 12px;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: none;
  background: color-mix(in srgb, var(--phone-dot-active) 84%, white 16%);
  color: color-mix(in srgb, var(--phone-screen-bg) 82%, black 18%);
  padding: 0;
  display: grid;
  place-items: center;
  box-shadow: 0 6px 14px color-mix(in srgb, var(--phone-dot-active) 32%, transparent 68%);
}

.vault-fab-plus {
  position: relative;
  width: 16px;
  height: 16px;
  display: block;
}

.vault-fab-plus::before,
.vault-fab-plus::after {
  content: '';
  position: absolute;
  left: 50%;
  top: 50%;
  width: 16px;
  height: 3px;
  border-radius: 3px;
  background: currentColor;
  transform: translate(-50%, -50%);
}

.vault-fab-plus::after {
  transform: translate(-50%, -50%) rotate(90deg);
}

/* ── Trust Strip ── */
.trust-strip {
  background: var(--highlight);
  padding: 24px 0;
  position: relative;
  z-index: 3;
  transform: skewY(-2deg);
  margin-top: 0;
  border-top: 1px solid var(--surface-light);
  border-bottom: 1px solid var(--surface-light);
}

.trust-content {
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  transform: skewY(2deg);
  gap: 20px;
}

.trust-item {
  font-family: var(--font-display);
  font-weight: 700;
  text-transform: uppercase;
  color: #FFFFFF;
  font-size: 1.1rem;
  display: flex;
  align-items: center;
  gap: 10px;
}

:root[data-theme='light'] .trust-item {
  color: #1A1B2E;
}

.trust-item::before {
  content: '\00d7';
  color: var(--bg);
  font-size: 1.5rem;
  font-weight: 900;
}

:root[data-theme='light'] .trust-item::before {
  color: #FFFFFF;
}

/* ── Features ── */
.features {
  padding: 170px 0 120px;
  background: var(--bg-light);
  color: var(--text-light);
  position: relative;
  margin-top: -50px;
  clip-path: polygon(0 50px, 100% 0, 100% calc(100% - 50px), 0 100%);
}

.section-header {
  margin-bottom: 80px;
  text-align: center;
}

.section-header h2 {
  font-size: 3rem;
  color: var(--text);
  margin-bottom: 16px;
}

.section-header-subtitle {
  color: var(--text-light);
  max-width: 600px;
  margin: 0 auto;
}

.feature-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
}

.feature-card {
  background: var(--surface-light);
  padding: 40px;
  position: relative;
  clip-path: polygon(
    20px 0, 100% 0,
    100% calc(100% - 20px),
    calc(100% - 20px) 100%,
    0 100%, 0 20px
  );
  transition: transform 0.3s ease;
  box-shadow: 0 20px 40px rgba(32, 35, 60, 0.1);
}

.feature-card:hover {
  transform: translateY(-10px);
}

.feature-card::after {
  content: '';
  position: absolute;
  top: 0; right: 0;
  width: 40px;
  height: 40px;
  background: linear-gradient(to bottom left, var(--highlight) 50%, transparent 50%);
}

.feature-icon {
  font-size: 2rem;
  margin-bottom: 24px;
  color: var(--accent);
}

.feature-card h3 {
  font-size: 1.5rem;
  margin-bottom: 16px;
  color: var(--text);
}

.feature-card p {
  color: var(--text-light);
  font-size: 1rem;
  line-height: 1.7;
}

/* ── Timeline ── */
.timeline-section {
  padding: 120px 0;
  background: var(--bg);
}

.timeline {
  position: relative;
  max-width: 800px;
  margin: 0 auto;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: rgba(255,255,255,0.1);
  transform: translateX(-50%);
}

.step {
  position: relative;
  margin-bottom: 80px;
  width: 50%;
}

.step:nth-child(odd) {
  left: 0;
  padding-right: 60px;
  text-align: right;
}

.step:nth-child(even) {
  left: 50%;
  padding-left: 60px;
}

.step-marker {
  position: absolute;
  top: 0;
  width: 20px;
  height: 20px;
  background: var(--bg);
  border: 2px solid var(--highlight);
  transform: rotate(45deg);
  z-index: 2;
}

.step:nth-child(odd) .step-marker {
  right: -11px;
}

.step:nth-child(even) .step-marker {
  left: -11px;
}

.step-number {
  font-family: var(--font-display);
  font-size: 4rem;
  color: rgba(255,255,255,0.05);
  font-weight: 800;
  position: absolute;
  top: -20px;
  line-height: 1;
}

.step:nth-child(odd) .step-number { right: 40px; }
.step:nth-child(even) .step-number { left: 40px; }

.step h3 {
  font-size: 1.8rem;
  margin-bottom: 12px;
  color: var(--text);
  position: relative;
}

.step p {
  color: var(--accent);
}

/* ── Security ── */
.security-section {
  background: var(--surface);
  padding: 100px 0;
  border-top: 1px solid rgba(255,255,255,0.05);
}

.tech-specs {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  align-items: center;
}

.tech-diagram {
  background: var(--bg);
  border: 1px solid var(--accent);
  padding: 40px;
  position: relative;
}

.tech-diagram::before, .tech-diagram::after {
  content: '';
  position: absolute;
  width: 20px;
  height: 20px;
  border-color: var(--highlight);
  border-style: solid;
  transition: all 0.5s;
}

.tech-diagram::before { top: -1px; left: -1px; border-width: 2px 0 0 2px; }
.tech-diagram::after { bottom: -1px; right: -1px; border-width: 0 2px 2px 0; }

.code-block {
  font-family: monospace;
  color: var(--accent);
  line-height: 1.8;
  font-size: 0.9rem;
}

.code-key { color: var(--highlight); }
.code-val { color: var(--text); }
.code-comment { color: var(--text-secondary); }

.tech-content h2 {
  font-size: 2.5rem;
  margin-bottom: 24px;
}

.tech-lead {
  color: var(--accent);
  font-size: 1.1rem;
}

.tech-content ul {
  margin-top: 30px;
}

.tech-content li {
  margin-bottom: 16px;
  padding-left: 24px;
  position: relative;
  color: var(--text);
}

.tech-content li::before {
  content: '/';
  color: var(--highlight);
  position: absolute;
  left: 0;
  font-weight: bold;
}

/* ── FAQ ── */
.faq-section {
  padding: 120px 0;
  background: var(--bg);
}

.faq-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 24px;
  width: min(100%, 800px);
  max-width: 800px;
  margin: 0 auto;
}

.faq-item {
  border: 1px solid color-mix(in srgb, var(--text) 12%, transparent 88%);
  background: color-mix(in srgb, var(--surface) 58%, transparent 42%);
  transition: 0.3s;
}

.faq-item[open] {
  border-color: var(--highlight);
  background: var(--surface);
}

.faq-summary {
  padding: 24px;
  cursor: pointer;
  font-family: var(--font-display);
  font-size: 1.2rem;
  font-weight: 500;
  display: flex;
  justify-content: space-between;
  align-items: center;
  list-style: none;
}

.faq-summary::-webkit-details-marker { display: none; }

.faq-summary::after {
  content: '+';
  font-size: 1.5rem;
  color: var(--highlight);
  transition: transform 0.3s;
}

.faq-item[open] .faq-summary::after {
  transform: rotate(45deg);
}

.faq-content {
  padding: 0 24px 24px 24px;
  color: var(--text);
  opacity: 0.9;
}

/* ── Final CTA ── */
.final-cta {
  padding: 140px 0;
  text-align: center;
  background: radial-gradient(circle at center, var(--accent) 0%, var(--bg) 70%);
  position: relative;
  overflow: hidden;
}

.final-cta h2 {
  font-size: 4rem;
  margin-bottom: 30px;
  line-height: 1;
}

/* ── Grid Overlay ── */
.grid-overlay {
  position: absolute;
  width: 100%; height: 100%;
  background-image: radial-gradient(var(--text) 1px, transparent 1px);
  background-size: 40px 40px;
  opacity: 0.05;
  z-index: 1;
  pointer-events: none;
}

/* ── Responsive ── */
@media (max-width: 900px) {
  .hero-layout {
    grid-template-columns: 1fr;
    text-align: center;
  }

  .hero-sub { border-left: none; padding-left: 0; margin-inline: auto; }
  .hero-actions { justify-content: center; }

  .mockup-container {
    --mockup-h: clamp(320px, calc(100vh - 260px), 700px);
    margin: 24px auto 28px;
    height: var(--mockup-h);
    width: min(100%, calc(var(--mockup-h) * 0.48));
    max-width: 336px;
  }

  .hero-title-stack { align-items: center; }
  .stack-1 { transform: translateX(0); }
}

@media (max-width: 768px) {
  .hero {
    min-height: auto;
    padding-top: max(112px, calc(74px + env(safe-area-inset-top) + 28px));
    padding-bottom: 40px;
  }

  .hero-layout {
    gap: 24px;
  }

  .hero-title-stack { font-size: clamp(2.5rem, 8vw, 4rem); }

  .hero-sub {
    margin-top: 1.35rem;
    font-size: 1.05rem;
    line-height: 1.5;
    max-width: 36ch;
  }

  .hero-actions {
    margin-top: 28px;
    width: 100%;
    gap: 12px;
  }

  .hero-actions .btn {
    width: 100%;
  }

  .timeline::before { left: 20px; }
  .step { width: 100%; padding-left: 60px !important; text-align: left !important; left: 0 !important; }
  .step:nth-child(odd) .step-marker, .step:nth-child(even) .step-marker { left: 9px; }
  .step-number { left: 60px !important; }
  .tech-specs { grid-template-columns: 1fr; }
  .trust-content { flex-direction: column; }

  .trust-strip {
    margin-top: 0;
  }

  .features {
    margin-top: -36px;
    padding-top: 136px;
    clip-path: polygon(0 36px, 100% 0, 100% calc(100% - 40px), 0 100%);
  }

  .mockup-container {
    width: min(100%, 340px);
    height: auto;
    margin: 20px auto 32px;
  }

  .pattern-lock-zone {
    width: 218px;
    height: 218px;
    margin-top: 42px;
  }

  .lock-title {
    font-size: 1.15rem;
  }
}

@media (max-height: 820px) and (min-width: 901px) {
  .hero {
    padding-bottom: 56px;
  }

  .trust-strip {
    margin-top: 0;
  }
}

@media (max-height: 700px) and (max-width: 900px) {
  .hero {
    padding-bottom: 56px;
  }

  .trust-strip {
    margin-top: 0;
  }
}

@keyframes unlockPulse {
  0% {
    opacity: 0.42;
    transform: scale(0.86);
    background: var(--phone-unlock-pixel-dim);
  }
  50% {
    opacity: 1;
    transform: scale(1);
    background: var(--phone-unlock-pixel);
  }
  100% {
    opacity: 0.42;
    transform: scale(0.86);
    background: var(--phone-unlock-pixel-dim);
  }
}

