    --app-accent-light: #1F0D77;
    --app-accent-dark: #CCC3F8;
    --app-highlight-light: #E45858;
    --app-highlight-dark: #FF6F6F;

    --bg-dark: #20233C;
    --bg-light: #2A2D49;
    --surface-dark: #323444;
    --surface-light: #3A3D55;
    --text-dark: #E8E8F0;
    --text-light: #D6D8EC;
    --accent-dark: #CCC3F8;
    --accent-light: #CCC3F8;
