1737→    <script>
1738→        (function () {
1739→            const root = document.documentElement;
1740→            const themeToggle = document.getElementById('theme-toggle');
1741→            const themeIcon = document.getElementById('theme-icon');
1742→            const themeLabel = document.getElementById('theme-label');
1743→            const preferred = window.matchMedia('(prefers-color-scheme: dark)');
1744→            const savedTheme = localStorage.getItem('vaultaire-theme');
1745→            const hasUserOverride = savedTheme === 'dark' || savedTheme === 'light';
1746→
1747→            const toRgba = (hex, alpha) => {
1748→                const normalized = hex.replace('#', '').trim();
1749→                const fullHex = normalized.length === 3
1750→                    ? normalized.split('').map(ch => ch + ch).join('')
1751→                    : normalized;
1752→
1753→                if (!/^[0-9a-fA-F]{6}$/.test(fullHex)) {
1754→                    return `rgba(204, 195, 248, ${alpha})`;
1755→                }
1756→
1757→                const r = parseInt(fullHex.slice(0, 2), 16);
1758→                const g = parseInt(fullHex.slice(2, 4), 16);
1759→                const b = parseInt(fullHex.slice(4, 6), 16);
1760→                return `rgba(${r}, ${g}, ${b}, ${alpha})`;
1761→            };
1762→
1763→            function setTheme(theme, persist) {
1764→                root.dataset.theme = theme;
1765→                const isDark = theme === 'dark';
1766→                themeIcon.textContent = isDark ? '☀︎' : '☽';
1767→                themeLabel.textContent = isDark ? 'Light' : 'Dark';
1768→                themeToggle.setAttribute('aria-pressed', String(!isDark));
1769→                themeToggle.setAttribute('aria-label', `Switch to ${isDark ? 'light' : 'dark'} mode`);
1770→                if (persist) {
1771→                    localStorage.setItem('vaultaire-theme', theme);
1772→                }
1773→                updateCanvasTheme();
1774→            }
1775→
1776→            const canvas = document.getElementById('pattern-canvas');
1777→            const ctx = canvas.getContext('2d');
1778→            let width = 0;
1779→            let height = 0;
1780→            let bgDots = [];
1781→            let accentColor = '#CCC3F8';
1782→            let accentTrailColor = 'rgba(204, 195, 248, 0.4)';
1783→
1784→            function updateCanvasTheme() {
1785→                const styles = getComputedStyle(root);
1786→                accentColor = styles.getPropertyValue('--accent-dark').trim() || '#CCC3F8';
1787→                accentTrailColor = toRgba(accentColor, 0.35);
1788→            }
1789→
1790→            setTheme(hasUserOverride ? savedTheme : (preferred.matches ? 'dark' : 'light'), false);
1791→
1792→            themeToggle.addEventListener('click', () => {
1793→                const next = root.dataset.theme === 'dark' ? 'light' : 'dark';
1794→                setTheme(next, true);
1795→            });
1796→
1797→            preferred.addEventListener('change', (event) => {
1798→                if (!localStorage.getItem('vaultaire-theme')) {
1799→                    setTheme(event.matches ? 'dark' : 'light', false);
1800→                }
1801→            });
1802→
1803→            function resize() {
1804→                width = canvas.width = canvas.parentElement.offsetWidth;
1805→                height = canvas.height = canvas.parentElement.offsetHeight;
1806→                initDots();
1807→            }
1808→
1809→            function initDots() {
1810→                bgDots = [];
1811→                const spacing = 110;
1812→                const rows = Math.ceil(height / spacing);
1813→                const cols = Math.ceil(width / spacing);
1814→
1815→                for (let i = 0; i < cols; i += 1) {
1816→                    for (let j = 0; j < rows; j += 1) {
1817→                        bgDots.push({
1818→                            x: i * spacing + (Math.random() * 24),
1819→                            y: j * spacing + (Math.random() * 24)
1820→                        });
1821→                    }
1822→                }
1823→            }
1824→
1825→            let drawingPath = false;
1826→            function drawRandomPattern() {
1827→                if (drawingPath || bgDots.length === 0) return;
1828→                drawingPath = true;
1829→
1830→                const startIndex = Math.floor(Math.random() * bgDots.length);
1831→                const pathLength = 4 + Math.floor(Math.random() * 3);
1832→                const start = bgDots[startIndex];
1833→                let current = start;
1834→
1835→                ctx.strokeStyle = accentTrailColor;
1836→                ctx.lineWidth = 2;
1837→                ctx.beginPath();
1838→                ctx.moveTo(start.x, start.y);
1839→
1840→                for (let i = 0; i < pathLength; i += 1) {
1841→                    const next = bgDots[Math.floor(Math.random() * bgDots.length)];
1842→                    const dist = Math.hypot(next.x - current.x, next.y - current.y);
1843→                    if (dist < 200 && dist > 36) {
1844→                        ctx.lineTo(next.x, next.y);
1845→                        current = next;
1846→                    }
1847→                }
1848→                ctx.stroke();
1849→
1850→                setTimeout(() => {
1851→                    drawingPath = false;
1852→                }, 540);
1853→            }
1854→
1855→            function draw() {
1856→                ctx.clearRect(0, 0, width, height);
1857→                ctx.fillStyle = accentColor;
1858→                bgDots.forEach((dot) => {
1859→                    ctx.beginPath();
1860→                    ctx.arc(dot.x, dot.y, 1.4, 0, Math.PI * 2);
1861→                    ctx.fill();
1862→                });
1863→
1864→                if (Math.random() > 0.95) {
1865→                    drawRandomPattern();
1866→                }
1867→
1868→                requestAnimationFrame(draw);
1869→            }
1870→
1871→            window.addEventListener('resize', resize);
1872→            resize();
1873→            draw();
1874→
1875→            const dots = Array.from(document.querySelectorAll('.grid-dot'));
1876→            const pathEl = document.getElementById('drawing-path');
1877→            const cursorEl = document.getElementById('drawing-cursor');
1878→            const svgEl = document.getElementById('mockup-svg');
1879→            const lockZone = document.getElementById('pattern-lock-zone');
1880→            const phoneScreen = document.querySelector('.phone-screen');
1881→            const mockupTimeEl = document.getElementById('mockup-time');
1882→
1883→            const sequences = [
1884→                [0, 5, 6, 7, 12, 17, 16, 15, 20, 21, 22, 23, 24]
1885→            ];
1886→            let sequenceIndex = 0;
1887→            let nodeCenters = [];
1888→
1889→            const sleep = (ms) => new Promise((resolve) => setTimeout(resolve, ms));
1890→
1891→            function updateMockupTime() {
1892→                const now = new Date();
1893→                const hour = ((now.getHours() + 11) % 12) + 1;
1894→                const minute = String(now.getMinutes()).padStart(2, '0');
1895→                mockupTimeEl.textContent = `${hour}:${minute}`;
1896→            }
1897→
1898→            function scheduleMockupMinuteTick() {
1899→                updateMockupTime();
1900→                const now = new Date();
1901→                const msUntilNextMinute = ((60 - now.getSeconds()) * 1000) - now.getMilliseconds();
1902→                setTimeout(scheduleMockupMinuteTick, Math.max(200, msUntilNextMinute));
1903→            }
1904→
1905→            scheduleMockupMinuteTick();
1906→
1907→            function computeNodeCenters() {
1908→                const baseRect = lockZone.getBoundingClientRect();
1909→                svgEl.setAttribute('viewBox', `0 0 ${baseRect.width} ${baseRect.height}`);
1910→                svgEl.setAttribute('width', `${baseRect.width}`);
1911→                svgEl.setAttribute('height', `${baseRect.height}`);
1912→                nodeCenters = dots.map((dot) => {
1913→                    const rect = dot.getBoundingClientRect();
1914→                    return {
1915→                        x: rect.left - baseRect.left + (rect.width / 2),
1916→                        y: rect.top - baseRect.top + (rect.height / 2)
1917→                    };
1918→                });
1919→            }
1920→
1921→            function setPath(points) {
1922→                if (points.length === 0) {
1923→                    pathEl.setAttribute('d', '');
1924→                    cursorEl.style.opacity = '0';
1925→                    return;
1926→                }
1927→                const commands = points.map((point, idx) => {
1928→                    const cmd = idx === 0 ? 'M' : 'L';
1929→                    return `${cmd} ${point.x.toFixed(2)} ${point.y.toFixed(2)}`;
1930→                });
1931→                pathEl.setAttribute('d', commands.join(' '));
1932→                const tail = points[points.length - 1];
1933→                cursorEl.setAttribute('cx', tail.x.toFixed(2));
1934→                cursorEl.setAttribute('cy', tail.y.toFixed(2));
1935→                cursorEl.style.opacity = '1';
1936→            }
1937→
1938→            function animateSegment(stablePoints, fromPoint, toPoint, durationMs) {
1939→                return new Promise((resolve) => {
1940→                    const startedAt = performance.now();
1941→
1942→                    function tick(now) {
1943→                        const progress = Math.min(1, (now - startedAt) / durationMs);
1944→                        const x = fromPoint.x + ((toPoint.x - fromPoint.x) * progress);
1945→                        const y = fromPoint.y + ((toPoint.y - fromPoint.y) * progress);
1946→                        setPath([...stablePoints, { x, y }]);
1947→
1948→                        if (progress < 1) {
1949→                            requestAnimationFrame(tick);
1950→                        } else {
1951→                            resolve();
1952→                        }
1953→                    }
1954→
1955→                    requestAnimationFrame(tick);
1956→                });
1957→            }
1958→
1959→            async function runSequence(sequence) {
1960→                dots.forEach((dot) => dot.classList.remove('active'));
1961→                setPath([]);
1962→                const points = [];
1963→                computeNodeCenters();
1964→                await sleep(250);
1965→
1966→                if (sequence.length === 0) return;
1967→
1968→                const firstNode = sequence[0];
1969→                dots[firstNode].classList.add('active');
1970→                points.push(nodeCenters[firstNode]);
1971→                setPath(points);
1972→
1973→                for (let i = 1; i < sequence.length; i += 1) {
1974→                    const prevPoint = nodeCenters[sequence[i - 1]];
1975→                    const nextPoint = nodeCenters[sequence[i]];
1976→                    const stablePoints = points.slice(0);
1977→                    await animateSegment(stablePoints, prevPoint, nextPoint, 170);
1978→
1979→                    dots[sequence[i]].classList.add('active');
1980→                    points.push(nextPoint);
1981→                    setPath(points);
1982→                }
1983→
1984→                await sleep(220);
1985→                phoneScreen.classList.add('mockup-unlocking');
1986→                await sleep(980);
1987→                phoneScreen.classList.remove('mockup-unlocking');
1988→                phoneScreen.classList.add('mockup-vault');
1989→                await sleep(5000);
1990→                phoneScreen.classList.remove('mockup-vault');
1991→
1992→                dots.forEach((dot) => dot.classList.remove('active'));
1993→                setPath([]);
1994→                await sleep(430);
1995→            }
1996→
1997→            (async function animatePatternLoop() {
1998→                await sleep(700);
1999→                while (true) {
2000→                    const sequence = sequences[sequenceIndex];
2001→                    await runSequence(sequence);
2002→                    sequenceIndex = (sequenceIndex + 1) % sequences.length;
2003→                }
2004→            })();
2005→        })();
2006→    </script>
