 123→
 124→        * {
 125→            box-sizing: border-box;
 126→            margin: 0;
 127→            padding: 0;
 128→        }
 129→
 130→        html {
 131→            scroll-behavior: smooth;
 132→        }
 133→
 134→        body {
 135→            background-color: var(--bg-dark);
 136→            color: var(--text-dark);
 137→            font-family: var(--font-body);
 138→            overflow-x: hidden;
 139→            line-height: 1.6;
 140→            -webkit-font-smoothing: antialiased;
 141→            transition: background-color 240ms ease, color 240ms ease;
 142→        }
 143→
 144→        h1, h2, h3, h4, h5, h6 {
 145→            font-family: var(--font-display);
 146→            text-transform: uppercase;
 147→            letter-spacing: -0.02em;
 148→        }
 149→
 150→        a { text-decoration: none; color: inherit; transition: 0.3s ease; }
 151→        ul { list-style: none; }
 152→
 153→        .container {
 154→            max-width: 1200px;
 155→            margin: 0 auto;
 156→            padding: 0 24px;
 157→            position: relative;
 158→            z-index: 2;
 159→        }
 160→
 161→        .text-gradient {
 162→            background: linear-gradient(135deg, var(--text-dark) 0%, var(--accent-dark) 100%);
 163→            -webkit-background-clip: text;
 164→            -webkit-text-fill-color: transparent;
 165→        }
 166→
 167→        .highlight-text {
 168→            color: var(--highlight-dark);
 169→        }
 170→
 171→        /* ── Buttons ── */
 172→        .btn {
 173→            display: inline-flex;
 174→            align-items: center;
 175→            justify-content: center;
 176→            padding: 18px 36px;
 177→            font-family: var(--font-display);
 178→            font-weight: 700;
 179→            text-transform: uppercase;
 180→            letter-spacing: 0.05em;
 181→            position: relative;
 182→            clip-path: polygon(10px 0, 100% 0, 100% calc(100% - 10px), calc(100% - 10px) 100%, 0 100%, 0 10px);
 183→            transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
 184→            border: none;
 185→            cursor: pointer;
 186→        }
 187→
 188→        .btn-primary {
 189→            background-color: var(--accent-dark);
 190→            color: var(--accent-contrast);
 191→        }
 192→
 193→        .btn-primary::before {
 194→            content: '';
 195→            position: absolute;
 196→            top: 0; left: 0; width: 100%; height: 100%;
 197→            background: rgba(255,255,255,0.2);
 198→            transform: translateX(-100%);
 199→            transition: transform 0.4s;
 200→            z-index: 1;
 201→        }
 202→
 203→        .btn-primary:hover::before {
 204→            transform: translateX(0);
 205→        }
 206→
 207→        .btn-primary:hover {
 208→            background-color: var(--accent-dark);
 209→            transform: translateY(-2px);
 210→            box-shadow: 0 10px 30px rgba(31, 13, 119, 0.3);
 211→        }
 212→
 213→        .btn-secondary {
 214→            background: transparent;
 215→            border: 2px solid var(--text-dark);
 216→            color: var(--text-dark);
 217→        }
 218→
 219→        .btn-secondary:hover {
 220→            background: var(--surface-dark);
 221→            border-color: var(--accent-dark);
 222→        }
 223→
 224→        /* ── Header / Nav ── */
 225→        header {
 226→            position: fixed;
 227→            top: 0;
 228→            left: 0;
 229→            width: 100%;
 230→            z-index: 1000;
 231→            background: var(--header-bg);
 232→            backdrop-filter: blur(12px);
 233→            border-bottom: 1px solid var(--header-border);
 234→            transition: background-color 240ms ease, border-color 240ms ease;
 235→        }
 236→
 237→        .nav-inner {
 238→            display: flex;
 239→            justify-content: space-between;
 240→            align-items: center;
 241→            height: 80px;
 242→        }
 243→
 244→        .logo {
 245→            font-family: var(--font-display);
 246→            font-weight: 700;
 247→            font-size: 1.25rem;
 248→            letter-spacing: 0.01em;
 249→            color: var(--text-dark);
 250→            display: flex;
 251→            align-items: center;
 252→            gap: 10px;
 253→        }
 254→
 255→        .logo .logo-mark {
 256→            width: 28px;
 257→            height: 28px;
 258→            border-radius: 8px;
 259→            display: block;
 260→            box-shadow: 0 6px 14px color-mix(in srgb, var(--bg-dark) 68%, transparent 32%);
 261→        }
 262→
 263→        .logo .logo-mark--light {
 264→            display: none;
 265→        }
 266→
 267→        :root[data-theme='light'] .logo .logo-mark--dark {
 268→            display: none;
 269→        }
 270→
 271→        :root[data-theme='light'] .logo .logo-mark--light {
 272→            display: block;
 273→        }
 274→
 275→        .logo span {
 276→            color: var(--text-dark);
 277→        }
 278→
 279→        .nav-links {
 280→            display: flex;
 281→            gap: 32px;
 282→        }
 283→
 284→        .nav-links a {
 285→            font-size: 0.9rem;
 286→            text-transform: uppercase;
 287→            letter-spacing: 0.1em;
 288→            opacity: 0.8;
 289→            position: relative;
 290→        }
 291→
 292→        .nav-links a::after {
 293→            content: '';
 294→            position: absolute;
 295→            bottom: -5px;
 296→            left: 0;
 297→            width: 0;
 298→            height: 2px;
 299→            background: var(--accent-dark);
 300→            transition: width 0.3s;
 301→        }
 302→
 303→        .nav-links a:hover::after {
 304→            width: 100%;
 305→        }
 306→
 307→        .nav-cta {
 308→            display: flex;
 309→            align-items: center;
 310→            gap: 12px;
 311→        }
 312→
 313→        .theme-toggle {
 314→            border: 1px solid var(--header-border);
 315→            background: var(--theme-button-bg);
 316→            color: var(--theme-button-text);
 317→            height: 44px;
 318→            min-width: 44px;
 319→            padding: 0 14px;
 320→            border-radius: 999px;
 321→            font: 600 0.82rem var(--font-body);
 322→            display: inline-flex;
 323→            align-items: center;
 324→            gap: 8px;
 325→            cursor: pointer;
 326→            transition: transform 180ms ease, background-color 180ms ease, border-color 180ms ease;
 327→        }
 328→
 329→        .theme-toggle:hover {
 330→            transform: translateY(-1px);
 331→        }
 332→
 333→        .theme-toggle:focus-visible {
 334→            outline: 2px solid var(--accent-dark);
 335→            outline-offset: 2px;
 336→        }
 337→
 338→        .theme-icon {
 339→            font-size: 1rem;
 340→            line-height: 1;
 341→        }
 342→
 343→        /* ── Hero ── */
 344→        .hero {
 345→            position: relative;
 346→            min-height: 100vh;
 347→            display: flex;
 348→            align-items: center;
 349→            padding-top: 80px;
 350→            overflow: hidden;
 351→        }
 352→
 353→        .hero-bg-x {
 354→            position: absolute;
 355→            top: 0; left: 0; width: 100%; height: 100%;
 356→            z-index: 0;
 357→            pointer-events: none;
 358→        }
 359→
 360→        .slash-1 {
 361→            position: absolute;
 362→            width: 200%;
 363→            height: 2px;
 364→            top: 0;
 365→            left: -50%;
 366→            transform: rotate(35deg);
 367→            transform-origin: center;
 368→            background: linear-gradient(90deg, transparent, var(--highlight-dark), transparent);
 369→            opacity: 0.8;
 370→        }
 371→        .slash-2 {
 372→            position: absolute;
 373→            width: 200%;
 374→            height: 2px;
 375→            top: 0;
 376→            left: -50%;
 377→            transform: rotate(-35deg);
 378→            transform-origin: center;
 379→            background: linear-gradient(90deg, transparent, var(--highlight-dark), transparent);
 380→            opacity: 0.8;
 381→        }
 382→
 383→        #pattern-canvas {
 384→            position: absolute;
 385→            top: 0;
 386→            left: 0;
 387→            width: 100%;
 388→            height: 100%;
 389→            z-index: 0;
 390→            opacity: 0.3;
 391→        }
 392→
 393→        .hero-content {
 394→            position: relative;
 395→            z-index: 2;
 396→            width: 100%;
 397→        }
 398→
 399→        /* Hero: Two-column grid — stacked title (724b) + phone mockup (9bff) */
 400→        .hero-layout {
 401→            display: grid;
 402→            grid-template-columns: 1.2fr 1fr;
 403→            align-items: center;
 404→            gap: 40px;
 405→            width: 100%;
 406→        }
 407→
 408→        /* Stacked title from design-724b */
 409→        .hero-title-stack {
 410→            font-family: var(--font-display);
 411→            font-size: clamp(3.5rem, 7vw, 7rem);
 412→            line-height: 0.85;
 413→            font-weight: 700;
 414→            text-transform: uppercase;
 415→            letter-spacing: -0.03em;
 416→            display: flex;
 417→            flex-direction: column;
 418→        }
 419→
 420→        .stack-layer {
 421→            display: block;
 422→            transition: transform 0.5s var(--ease-cinematic);
 423→        }
 424→
 425→        .stack-1 {
 426→            color: transparent;
 427→            -webkit-text-stroke: 1px var(--text-secondary);
 428→            opacity: 0.5;
 429→            transform: translateX(-20px);
 430→        }
 431→
 432→        .stack-2 {
 433→            color: var(--accent-dark);
 434→            opacity: 0.8;
 435→        }
 436→
 437→        .stack-3 {
 438→            color: var(--text-dark);
 439→            position: relative;
 440→            z-index: 10;
 441→        }
 442→
 443→        .hero-sub {
 444→            margin-top: 2rem;
 445→            font-size: 1.25rem;
 446→            color: var(--text-dark);
 447→            opacity: 0.8;
 448→            max-width: 600px;
 449→            border-left: 2px solid var(--highlight-dark);
 450→            padding-left: 20px;
 451→        }
 452→
 453→        .hero-actions {
 454→            display: flex;
 455→            gap: 20px;
 456→            flex-wrap: wrap;
 457→            margin-top: 48px;
 458→        }
 459→
 460→        /* ── Phone Mockup ── */
 461→        .mockup-container {
 462→            position: relative;
 463→            width: min(336px, 100%);
 464→            height: 700px;
 465→            margin-left: auto;
 466→        }
 467→
 468→        .phone-frame {
 469→            width: 100%;
 470→            height: 100%;
 471→            background: var(--phone-frame-bg);
 472→            border-radius: 46px;
 473→            padding: 10px;
 474→            box-shadow: 0 38px 90px rgba(0, 0, 0, 0.46), 0 0 0 1px rgba(255, 255, 255, 0.06);
 475→            position: relative;
 476→            transition: transform 220ms ease;
 477→        }
 478→
 479→        .phone-frame:hover {
 480→            transform: translateY(-2px);
 481→        }
 482→
 483→        .phone-screen {
 484→            width: 100%;
 485→            height: 100%;
 486→            background: var(--phone-screen-bg);
 487→            border-radius: 36px;
 488→            overflow: hidden;
 489→            position: relative;
 490→            border: 1px solid var(--phone-screen-border);
 491→            padding: 12px 12px 18px;
 492→        }
 493→
 494→        .phone-status {
 495→            display: flex;
 496→            justify-content: space-between;
 497→            align-items: center;
 498→            font-size: 0.88rem;
 499→            font-weight: 600;
 500→            margin: 2px 8px 8px;
 501→            color: var(--phone-text-primary);
 502→            opacity: 0.9;
 503→            position: relative;
 504→            min-height: 28px;
 505→            z-index: 5;
 506→        }
 507→
 508→        .phone-dynamic-island {
 509→            position: absolute;
 510→            left: 50%;
 511→            top: 1px;
 512→            transform: translateX(-50%);
 513→            width: 122px;
 514→            height: 24px;
 515→            border-radius: 999px;
 516→            background: #05060A;
 517→        }
 518→
 519→        .status-right {
 520→            display: inline-flex;
 521→            align-items: center;
 522→            gap: 8px;
 523→        }
 524→
 525→        .status-icon-svg {
 526→            width: 14px;
 527→            height: 14px;
 528→            display: inline-flex;
 529→            align-items: center;
 530→            justify-content: center;
 531→            color: currentColor;
 532→            opacity: 0.95;
 533→        }
 534→
 535→        .status-icon-svg svg {
 536→            width: 100%;
 537→            height: 100%;
 538→            display: block;
 539→        }
 540→
 541→        .status-battery {
 542→            width: 20px;
 543→            height: 10px;
 544→            border-radius: 2px;
 545→            border: 1px solid color-mix(in srgb, var(--phone-text-primary) 62%, transparent 38%);
 546→            position: relative;
 547→        }
 548→
 549→        .status-battery::before {
 550→            content: '';
 551→            position: absolute;
 552→            right: -3px;
 553→            top: 2px;
 554→            width: 2px;
 555→            height: 4px;
 556→            border-radius: 1px;
 557→            background: color-mix(in srgb, var(--phone-text-primary) 62%, transparent 38%);
 558→        }
 559→
 560→        .status-battery::after {
 561→            content: '';
 562→            position: absolute;
 563→            top: 1px;
 564→            left: 1px;
 565→            width: 12px;
 566→            height: 6px;
 567→            border-radius: 1px;
 568→            background: color-mix(in srgb, var(--phone-dot-active) 65%, var(--phone-screen-bg) 35%);
 569→        }
 570→
 571→        .mockup-stage {
 572→            position: absolute;
 573→            inset: 46px 12px 18px;
 574→            border-radius: 28px;
 575→            transition: opacity 220ms ease;
 576→        }
 577→
 578→        .mockup-lock-state {
 579→            display: flex;
 580→            flex-direction: column;
 581→            align-items: center;
 582→            text-align: center;
 583→            opacity: 1;
 584→            pointer-events: auto;
 585→        }
 586→
 587→        .mockup-unlock-state {
 588→            opacity: 0;
 589→            pointer-events: none;
 590→            display: grid;
 591→            place-items: center;
 592→        }
 593→
 594→        .mockup-vault-state {
 595→            opacity: 0;
 596→            pointer-events: none;
 597→            display: flex;
 598→            flex-direction: column;
 599→            gap: 10px;
 600→            padding: 2px 2px 0;
 601→            min-height: 0;
 602→        }
 603→
 604→        .phone-screen.mockup-unlocking .mockup-lock-state {
 605→            opacity: 0;
 606→            pointer-events: none;
 607→        }
 608→
 609→        .phone-screen.mockup-unlocking .mockup-unlock-state {
 610→            opacity: 1;
 611→        }
 612→
 613→        .phone-screen.mockup-vault .mockup-lock-state,
 614→        .phone-screen.mockup-vault .mockup-unlock-state {
 615→            opacity: 0;
 616→            pointer-events: none;
 617→        }
 618→
 619→        .phone-screen.mockup-vault .mockup-vault-state {
 620→            opacity: 1;
 621→            pointer-events: auto;
 622→        }
 623→
 624→        .lock-header {
 625→            margin-top: 70px;
 626→            display: grid;
 627→            gap: 10px;
 628→            place-items: center;
 629→        }
 630→
 631→        .lock-shield {
 632→            width: 44px;
 633→            height: 44px;
 634→            border-radius: 12px;
 635→            background: color-mix(in srgb, var(--phone-text-primary) 10%, transparent 90%);
 636→            display: grid;
 637→            place-items: center;
 638→            color: color-mix(in srgb, var(--phone-text-primary) 78%, transparent 22%);
 639→            font-size: 1rem;
 640→        }
 641→
 642→        .lock-title {
 643→            font-family: var(--font-body);
 644→            font-size: 1.25rem;
 645→            font-weight: 600;
 646→            text-transform: none;
 647→            letter-spacing: -0.01em;
 648→            color: var(--phone-text-primary);
 649→            margin: 0;
 650→        }
 651→
 652→        .pattern-lock-zone {
 653→            width: 226px;
 654→            height: 226px;
 655→            position: relative;
 656→            margin-top: 50px;
 657→            margin-bottom: 8px;
 658→        }
 659→
 660→        .pattern-grid {
 661→            display: grid;
 662→            grid-template-columns: repeat(5, 1fr);
 663→            gap: 34px;
 664→            position: relative;
 665→            z-index: 5;
 666→        }
 667→
