116→        }
117→
118→        let generation = initializationGeneration &+ 1
119→        initializationGeneration = generation
120→        currentOwnerFingerprint = KeyDerivation.keyFingerprint(from: key.rawBytes)
121→
122→        do {
123→            async let accountStatusTask = CloudKitSharingManager.shared.checkiCloudStatus()
