125→            let localSnapshot = try await Self.loadLocalSnapshot(vaultKey: key)
126→            guard generation == initializationGeneration else { return }
127→
128→            estimatedUploadSize = localSnapshot.estimatedUploadSize
129→            activeShares = localSnapshot.activeShares
130→
131→            await refreshUploadJobs(reloadShares: false)
132→            guard generation == initializationGeneration else { return }
133→            updateModeForCurrentData()
134→
135→            Task {
