686→            activeShares = []
687→            return
688→        }
689→        // Sort by creation date, newest first
690→        activeShares = shares.sorted { $0.createdAt > $1.createdAt }
691→        updateModeForCurrentData()
692→    }
693→    
694→    /// Formats a date as relative time (e.g., "5 minutes ago", "1 day ago")
695→    private func relativeTimeString(from date: Date) -> String {
696→        let now = Date()
697→        let diff = now.timeIntervalSince(date)
698→        
699→        // Less than a minute
700→        if diff < 60 {
701→            return "just now"
702→        }
703→        // Less than an hour - show minutes
704→        else if diff < 3600 {
705→            let minutes = Int(diff / 60)
706→            return minutes == 1 ? "1 minute ago" : "\(minutes) minutes ago"
707→        }
708→        // Less than a day - show hours
709→        else if diff < 86400 {
710→            let hours = Int(diff / 3600)
711→            return hours == 1 ? "1 hour ago" : "\(hours) hours ago"
712→        }
713→        // Less than 30 days - show days
714→        else if diff < 2592000 {
715→            let days = Int(diff / 86400)
716→            return days == 1 ? "1 day ago" : "\(days) days ago"
717→        }
718→        // More than 30 days - show the date
719→        else {
720→            let formatter = DateFormatter()
721→            formatter.dateStyle = .medium
722→            formatter.timeStyle = .none
723→            return formatter.string(from: date)
724→        }
725→    }
726→
727→    private func revokeShare(_ share: VaultStorage.ShareRecord) async {
728→        guard let key = appState.currentVaultKey else { return }
729→
730→        // Update local state first for instant UI response
731→        activeShares.removeAll { $0.id == share.id }
732→        
733→        // Check if this was the last share - if so, terminate any ongoing sync
734→        let remainingShares = activeShares
735→        let remainingJobs = uploadJobs.filter { $0.shareVaultId != share.id }
736→        
737→        if remainingShares.isEmpty && remainingJobs.isEmpty {
738→            // No more users - terminate any ongoing uploads for this specific share
739→            for job in uploadJobs where job.shareVaultId == share.id && job.canTerminate {
740→                ShareUploadManager.shared.terminateUpload(
741→                    jobId: job.id,
742→                    vaultKey: key,
743→                    cleanupRemote: true
744→                )
745→            }
746→            uploadJobs.removeAll { $0.shareVaultId == share.id }
747→            mode = .newShare
748→        }
749→        // If there are remaining shares, sync will continue for those users
750→
751→        do {
752→            // Persist to index
753→            var index = try await VaultStorage.shared.loadIndex(with: key)
754→            index.activeShares?.removeAll { $0.id == share.id }
755→            try await VaultStorage.shared.saveIndex(index, with: key)
756→        } catch {
757→            shareVaultLogger.error("Failed to update index after revoke: \(error.localizedDescription, privacy: .public)")
758→        }
759→
760→        // Delete from CloudKit in background (fire-and-forget)
761→        Task {
762→            try? await CloudKitSharingManager.shared.revokeShare(shareVaultId: share.id)
763→        }
764→    }
765→
766→    private func stopAllSharing() async {
767→        guard let key = appState.currentVaultKey else { return }
768→
769→        isStopping = true
770→
771→        do {
772→            // Cancel all upload jobs for this vault first
773→            for job in uploadJobs where job.canTerminate {
774→                ShareUploadManager.shared.terminateUpload(
775→                    jobId: job.id,
776→                    vaultKey: key,
777→                    cleanupRemote: true
778→                )
779→            }
780→
781→            // Immediately clear local shares so UI feels responsive
782→            var index = try await VaultStorage.shared.loadIndex(with: key)
783→            let sharesToDelete = index.activeShares ?? []
784→            index.activeShares = nil
785→            try await VaultStorage.shared.saveIndex(index, with: key)
786→
787→            activeShares = []
788→            uploadJobs = []
789→            mode = .newShare
790→            isStopping = false
791→
792→            // Delete from CloudKit in background
793→            for share in sharesToDelete {
794→                try? await CloudKitSharingManager.shared.deleteSharedVault(shareVaultId: share.id)
795→            }
796→        } catch {
797→            isStopping = false
798→            mode = .error("Failed to stop sharing: \(error.localizedDescription)")
799→        }
800→    }
801→
802→    private func terminateUpload(_ job: ShareUploadManager.UploadJob) {
803→        ShareUploadManager.shared.terminateUpload(
804→            jobId: job.id,
805→            vaultKey: appState.currentVaultKey,
806→            cleanupRemote: true
807→        )
808→
809→        uploadJobs.removeAll { $0.id == job.id }
810→        activeShares.removeAll { $0.id == job.shareVaultId }
811→        if uploadJobs.isEmpty && activeShares.isEmpty {
812→            mode = .newShare
813→        }
814→    }
815→
816→    // MARK: - Idle timer
817→
818→    private func applyIdleTimerPolicy() {
819→        let shouldDisable = Self.shouldDisableIdleTimer(
820→            isShareScreenVisible: isShareScreenVisible,
821→            uploadJobs: uploadJobs
822→        )
823→
824→        if shouldDisable && !didDisableIdleTimerForUploads {
825→            IdleTimerManager.shared.disable()
826→            didDisableIdleTimerForUploads = true
827→        } else if !shouldDisable && didDisableIdleTimerForUploads {
828→            IdleTimerManager.shared.enable()
829→            didDisableIdleTimerForUploads = false
830→        }
831→    }
832→
833→    private func releaseIdleTimerPolicyIfNeeded() {
834→        if didDisableIdleTimerForUploads {
835→            IdleTimerManager.shared.enable()
836→            didDisableIdleTimerForUploads = false
837→        }
838→    }
839→
840→    // MARK: - Helpers
841→
842→    private static let byteCountFormatter: ByteCountFormatter = {
843→        let formatter = ByteCountFormatter()
844→        formatter.countStyle = .file
845→        return formatter
846→    }()
847→
848→    private struct LocalSnapshot: Sendable {
849→        let estimatedUploadSize: Int
850→        let activeShares: [VaultStorage.ShareRecord]
851→    }
852→
853→    private static func loadLocalSnapshot(vaultKey: VaultKey) async throws -> LocalSnapshot {
854→        try await Task.detached(priority: .userInitiated) {
855→            let index = try await VaultStorage.shared.loadIndex(with: vaultKey)
856→            let estimatedSize = index.files.filter { !$0.isDeleted }.reduce(0) { $0 + $1.size }
857→            let shares = index.activeShares ?? []
858→            // Sort by creation date, newest first (descending order)
859→            let sortedShares = shares.sorted { $0.createdAt > $1.createdAt }
860→            return LocalSnapshot(
861→                estimatedUploadSize: estimatedSize,
862→                activeShares: sortedShares
863→            )
864→        }.value
865→    }
866→
867→    private static func loadActiveShares(vaultKey: VaultKey) async throws -> [VaultStorage.ShareRecord] {
868→        try await Task.detached(priority: .utility) {
869→            let index = try await VaultStorage.shared.loadIndex(with: vaultKey)
870→            let shares = index.activeShares ?? []
871→            // Sort by creation date, newest first
872→            return shares.sorted { $0.createdAt > $1.createdAt }
873→        }.value
874→    }
875→
876→    private func reconcileConsumedShares(
877→        vaultKey: VaultKey,
878→        initialShares: [VaultStorage.ShareRecord],
879→        generation: Int
880→    ) async {
881→        guard !initialShares.isEmpty else { return }
882→
883→        let consumedMap: [String: Bool]
884→        do {
885→            consumedMap = try await CloudKitSharingManager.shared.consumedStatusByShareVaultIds(initialShares.map(\.id))
886→        } catch {
887→            shareVaultLogger.warning("Failed to check consumed status: \(error.localizedDescription, privacy: .private)")
888→            return
889→        }
890→        let consumedIds = Set(consumedMap.compactMap { $0.value ? $0.key : nil })
891→        guard !consumedIds.isEmpty else { return }
892→
893→        do {
894→            let updatedShares = try await Task.detached(priority: .utility) { () -> [VaultStorage.ShareRecord] in
895→                var index = try await VaultStorage.shared.loadIndex(with: vaultKey)
896→                var shares = index.activeShares ?? []
897→                shares.removeAll { consumedIds.contains($0.id) }
898→                // Sort by creation date, newest first (descending order)
899→                shares.sort { $0.createdAt > $1.createdAt }
900→                index.activeShares = shares.isEmpty ? nil : shares
901→                try await VaultStorage.shared.saveIndex(index, with: vaultKey)
902→                return shares
903→            }.value
904→
905→            await MainActor.run {
906→                guard generation == initializationGeneration else { return }
907→                activeShares = updatedShares
908→                updateModeForCurrentData()
909→            }
910→        } catch {
911→            shareVaultLogger.error("Failed to reconcile consumed shares: \(error.localizedDescription, privacy: .public)")
912→        }
913→    }
914→
915→    static func shouldDisplayUploadJob(_ job: ShareUploadManager.UploadJob) -> Bool {
916→        if case .complete = job.status { return false }
917→        if case .cancelled = job.status { return false }
918→        return true
919→    }
920→
921→    static func shouldDisableIdleTimer(
922→        isShareScreenVisible: Bool,
923→        uploadJobs: [ShareUploadManager.UploadJob]
924→    ) -> Bool {
925→        guard isShareScreenVisible else { return false }
926→        return uploadJobs.contains(where: { $0.status.isRunning })
927→            || ShareSyncManager.shared.syncStatus == .syncing
928→    }
929→
930→    private func iCloudStatusMessage(_ status: CKAccountStatus) -> String {
931→        switch status {
932→        case .available: return "iCloud is available"
933→        case .noAccount: return "Please sign in to iCloud in Settings"
934→        case .restricted: return "iCloud access is restricted"
935→        case .couldNotDetermine: return "Could not determine iCloud status"
936→        case .temporarilyUnavailable: return "iCloud is temporarily unavailable. Please check your iCloud settings and try again."
937→        @unknown default: return "iCloud is not available"
938→        }
939→    }
940→}
941→
942→private struct SharePhraseSheetItem: Identifiable {
943→    let phrase: String
944→    var id: String { phrase }
945→}
946→
947→#Preview {
948→    ShareVaultView()
949→        .environment(AppState())
950→}
951→

