    private func phraseSheet(phrase: String) -> some View {
        NavigationStack {
            ScrollView {
                VStack(spacing: 20) {
                    PhraseDisplayCard(phrase: phrase)
                    PhraseActionButtons(phrase: phrase)
                    shareLinkButtons(for: phrase)

