1B6F904DF6C54504915C2F83 /* Upload Debug Symbols to Sentry */ = {
	isa = PBXShellScriptBuildPhase;
	alwaysOutOfDate = 1;
	buildActionMask = 2147483647;
	files = (
	);
	inputPaths = (
		"${DWARF_DSYM_FOLDER_PATH}/${DWARF_DSYM_FILE_NAME}/Contents/Resources/DWARF/${TARGET_NAME}",
	);
	name = "Upload Debug Symbols to Sentry";
	outputPaths = (
	);
	runOnlyForDeploymentPostprocessing = 0;
	shellPath = /bin/sh;
	shellScript = "# This script is responsible for uploading debug symbols and source context for Sentry.\nif which sentry-cli >/dev/null; then\n  export SENTRY_ORG=wraxle\n  export SENTRY_PROJECT=vault\n  ERROR=$(sentry-cli debug-files upload --include-sources \"$DWARF_DSYM_FOLDER_PATH\" 2>&1 >/dev/null)\n  if [ ! $? -eq 0 ]; then\n    echo \"warning: sentry-cli - $ERROR\"\n  fi\nelse\n  echo \"warning: sentry-cli not installed, download from https://github.com/getsentry/sentry-cli/releases\"\nfi\n";
};

