EB000000DF6C54504915C2F8 /* Upload Debug Symbols to Embrace */ = {
	isa = PBXShellScriptBuildPhase;
	alwaysOutOfDate = 1;
	buildActionMask = 2147483647;
	files = (
	);
	inputPaths = (
		"${DWARF_DSYM_FOLDER_PATH}/${DWARF_DSYM_FILE_NAME}/Contents/Resources/DWARF/${TARGET_NAME}",
	);
	name = "Upload Debug Symbols to Embrace";
	outputPaths = (
	);
	runOnlyForDeploymentPostprocessing = 0;
	shellPath = /bin/sh;
	shellScript = "# Upload dSYMs to Embrace for crash symbolication.\nEMBRACE_ID=ehz4q\REDACTED\n\n# Use the Embrace symbol upload tool bundled with the SDK\nSYMBOL_UPLOAD=\"${BUILD_DIR%Build/*}SourcePackages/artifacts/embrace-apple-sdk/embrace_symbol_upload.zip/embrace_symbol_upload\"\n\nif [ -f \"$SYMBOL_UPLOAD\" ]; then\n  \"$SYMBOL_UPLOAD\" --app \"$EMBRACE_ID\" --token \"$EMBRACE_TOKEN\" \"$DWARF_DSYM_FOLDER_PATH\"\nelse\n  echo \"warning: Embrace symbol upload tool not found at $SYMBOL_UPLOAD\"\nfi\n";
};

