  1→import Foundation
  2→import StoreKit
  3→import os.log
  4→
  5→enum StoreKitError: Error {
  6→    case unknown
  7→}
  8→
  9→@MainActor
 10→@Observable
 11→final class SubscriptionManager {
 12→    static let shared = SubscriptionManager()
 13→
 14→    private static let logger = Logger(subsystem: "app.vaultaire.ios", category: "Subscription")
 15→
 16→    private(set) var isPremium = false
 17→    private(set) var products: [Product] = []
 18→    private(set) var purchasedProductIDs: Set<String> = []
 19→
 20→    static let productIDs = [
 21→        "monthly_pro",
 22→        "yearly_pro",
 23→        "lifetime",
 24→    ]
 25→
 26→    static let subscriptionGroupID = "vaultaire_pro"
 27→
 28→    static let maxFreeVaults = 5
 29→    static let maxFreeSharedVaults = 10
 30→    static let maxFreeFilesPerVault = 100
 31→
 32→    private static let premiumOverrideKey = "premiumTestingOverride"
 33→
 34→    /// Whether the app is running in a sandbox environment (TestFlight or Xcode)
 35→    static var isSandbox: Bool {
 36→        Bundle.main.appStoreReceiptURL?.lastPathComponent == "sandboxReceipt"
 37→    }
 38→
 39→    /// Testing override for premium status (persisted in UserDefaults)
 40→    var hasPremiumOverride: Bool {
 41→        get { UserDefaults.standard.bool(forKey: Self.premiumOverrideKey) }
 42→        set {
 43→            UserDefaults.standard.set(newValue, forKey: Self.premiumOverrideKey)
 44→            isPremium = newValue
 45→            Self.isPremiumSnapshot = newValue
 46→            UserDefaults(suiteName: VaultCoreConstants.appGroupIdentifier)?
 47→                .set(newValue, forKey: VaultCoreConstants.isPremiumKey)
 48→        }
 49→    }
 50→
 51→    private var transactionListener: Task<Void, Never>?
 52→
 53→    private init() {
 54→        // Start listening for transactions immediately
 55→        transactionListener = listenForTransactions()
 56→
 57→        #if DEBUG
 58→        if ProcessInfo.processInfo.arguments.contains("-MAESTRO_PREMIUM_OVERRIDE")
 59→            || ProcessInfo.processInfo.arguments.contains("MAESTRO_PREMIUM_OVERRIDE") {
 60→            UserDefaults.standard.set(true, forKey: Self.premiumOverrideKey)
 61→        }
 62→        #endif
 63→
 64→        // Apply testing override on launch
 65→        if UserDefaults.standard.bool(forKey: Self.premiumOverrideKey) {
 66→            isPremium = true
 67→            Self.isPremiumSnapshot = true
 68→        }
 69→
 70→        Task {
 71→            await loadProducts()
 72→            await updatePurchasedProducts()
 73→        }
 74→    }
 75→
 76→    nonisolated deinit {
 77→        // Task is automatically cancelled when reference is dropped
 78→    }
 79→
 80→    // MARK: - Products
 81→
 82→    func loadProducts() async {
 83→        do {
 84→            let loaded = try await Product.products(for: Self.productIDs)
 85→            products = loaded.sorted { a, b in
 86→                Self.productIDs.firstIndex(of: a.id) ?? 0 < Self.productIDs.firstIndex(of: b.id) ?? 0
 87→            }
 88→        } catch {
 89→            Self.logger.error("Failed to load products: \(error.localizedDescription, privacy: .public)")
 90→        }
 91→    }
 92→
 93→    var hasProducts: Bool { !products.isEmpty }
 94→
 95→    func product(for id: String) -> Product? {
 96→        products.first { $0.id == id }
 97→    }
 98→
 99→    // MARK: - Transactions
100→
101→    private func listenForTransactions() -> Task<Void, Never> {
102→        Task.detached { [weak self] in
103→            for await result in Transaction.updates {
104→                await self?.handleVerified(result)
105→            }
106→        }
107→    }
108→
109→    private func handleVerified(_ result: VerificationResult<Transaction>) async {
110→        guard let transaction = try? result.payloadValue else {
111→            Self.logger.error("Transaction verification failed")
112→            return
113→        }
114→
115→        if transaction.revocationDate != nil {
116→            purchasedProductIDs.remove(transaction.productID)
117→        } else {
118→            purchasedProductIDs.insert(transaction.productID)
119→        }
120→
121→        await transaction.finish()
122→        refreshPremiumStatus()
123→    }
124→
125→    // MARK: - Purchase
126→
127→    func purchase(_ product: Product) async throws -> Bool {
128→        let span = EmbraceManager.shared.startTransaction(name: "subscription.purchase", operation: "subscription.purchase")
129→        span.setTag(value: product.id, key: "product_id")
130→        span.setTag(value: Self.isSandbox ? "sandbox" : "production", key: "environment")
131→
132→        let result = try await product.purchase()
133→
134→        switch result {
135→        case .success(let verification):
136→            guard let transaction = try? verification.payloadValue else {
137→                EmbraceManager.shared.captureError(StoreKitError.unknown, context: [
138→                    "action": "purchase_verification_failed",
139→                    "product_id": product.id
140→                ])
141→                span.finish(status: .invalidArgument)
142→                return false
143→            }
144→            purchasedProductIDs.insert(transaction.productID)
145→            await transaction.finish()
146→            refreshPremiumStatus()
147→            EmbraceManager.shared.addBreadcrumb(category: "subscription.purchased", data: [
148→                "product_id": transaction.productID,
149→                "transaction_id": String(transaction.id)
150→            ])
151→            span.finish(status: .ok)
152→            return true
153→
154→        case .userCancelled:
155→            EmbraceManager.shared.addBreadcrumb(category: "subscription.cancelled", data: ["product_id": product.id])
156→            span.finish(status: .aborted)
157→            return false
158→
159→        case .pending:
160→            EmbraceManager.shared.addBreadcrumb(category: "subscription.pending", data: ["product_id": product.id])
161→            span.finish(status: .ok)
162→            return false
163→
164→        @unknown default:
165→            EmbraceManager.shared.captureError(StoreKitError.unknown, context: [
166→                "action": "purchase_unknown_result",
167→                "product_id": product.id
168→            ])
169→            span.finish(status: .internalError)
170→            return false
171→        }
172→    }
173→
174→    // MARK: - Restore
175→
176→    func restorePurchases() async throws {
177→        try await AppStore.sync()
178→        await updatePurchasedProducts()
179→    }
180→
181→    // MARK: - Entitlements
182→
183→    func updatePurchasedProducts() async {
184→        var purchased: Set<String> = []
185→
186→        for await result in Transaction.currentEntitlements {
187→            if let transaction = try? result.payloadValue,
188→               transaction.revocationDate == nil {
189→                purchased.insert(transaction.productID)
190→            }
191→        }
192→
193→        purchasedProductIDs = purchased
194→        refreshPremiumStatus()
195→    }
196→
197→    private func refreshPremiumStatus() {
198→        let hasPurchase = !purchasedProductIDs.isEmpty
199→        let hasOverride = UserDefaults.standard.bool(forKey: Self.premiumOverrideKey)
200→        isPremium = hasPurchase || hasOverride
201→        Self.isPremiumSnapshot = isPremium
202→
203→        UserDefaults(suiteName: VaultCoreConstants.appGroupIdentifier)?
204→            .set(isPremium, forKey: VaultCoreConstants.isPremiumKey)
205→
206→        Self.logger.debug("isPremium=\(self.isPremium), products=\(self.purchasedProductIDs.joined(separator: ", "), privacy: .public)")
207→    }
208→
209→    // MARK: - Limit Checks
210→
211→    func canCreateVault(currentCount: Int) -> Bool {
212→        isPremium || currentCount < Self.maxFreeVaults
213→    }
214→
215→    func canJoinSharedVault(currentCount: Int) -> Bool {
216→        isPremium || currentCount < Self.maxFreeSharedVaults
217→    }
218→
219→    func canAddFile(currentFileCount: Int) -> Bool {
220→        isPremium || currentFileCount < Self.maxFreeFilesPerVault
221→    }
222→
223→    func canCreateSharedVault() -> Bool {
224→        isPremium
225→    }
226→
227→    func canCreateDuressVault() -> Bool {
228→        isPremium
229→    }
230→
231→    func canSyncWithICloud() -> Bool {
232→        isPremium
233→    }
234→
235→    func canExpandStorage() -> Bool {
236→        isPremium
237→    }
238→
239→    /// Thread-safe snapshot of premium status for non-MainActor callers (e.g. VaultStorage).
240→    nonisolated(unsafe) static var isPremiumSnapshot: Bool = false
241→}
