  4→    case unlimitedVaults
  5→    case unlimitedFiles
  6→    case sharedVault
  7→    case duressVault
  8→    case icloudSync
  9→    case expandedStorage
 10→
 11→    var displayName: String {
 12→        switch self {
 13→        case .unlimitedVaults: return "Unlimited Vaults"
 14→        case .unlimitedFiles: return "Unlimited Files"
 15→        case .sharedVault: return "Shared Vaults"
 16→        case .duressVault: return "Duress Vault"
 17→        case .icloudSync: return "iCloud Sync"
