  1→import SwiftUI
  2→import CloudKit
  3→import UIKit
  4→import CryptoKit
  5→
  6→struct JoinVaultView: View {
  7→    @Environment(AppState.self) private var appState
  8→    @Environment(\.dismiss) private var dismiss
  9→    @Environment(SubscriptionManager.self) private var subscriptionManager
 10→
 11→    @State private var phrase = ""
 12→    @State private var mode: ViewMode = .input
 13→    @State private var showingPaywall = false
 14→    @State private var iCloudStatus: CKAccountStatus?
 15→
 16→    // Pattern setup for shared vault
 17→    @State private var patternState = PatternState()
 18→    @State private var newPattern: [Int] = []
 19→    @State private var patternStep: PatternStep = .create
 20→    @State private var validationResult: PatternValidationResult?
 21→    @State private var errorMessage: String?
 22→    @State private var showingOverwriteConfirmation = false
 23→    @State private var pendingOverwriteKey: VaultKey?
 24→    @State private var existingVaultNameForOverwrite = "Vault"
 25→    @State private var existingFileCountForOverwrite = 0
 26→    @State private var isSettingUpVault = false
 27→    
 28→    // Pending shared vault import state
 29→    @State private var pendingImportState: ShareImportManager.PendingImportState?
 30→    @State private var isResumingImport = false
 31→
 32→    enum ViewMode: Equatable {
 33→        case input
 34→        case patternSetup
 35→        case error(String)
 36→    }
 37→
 38→    enum PatternStep {
 39→        case create
 40→        case confirm
 41→    }
 42→
 43→    var body: some View {
 44→        VStack(spacing: 0) {
 45→            // Header
 46→            HStack {
 47→                Button("Cancel") { dismiss() }
 48→                    .accessibilityIdentifier("join_cancel")
 49→                Spacer()
 50→                Text("Join Shared Vault")
 51→                    .font(.headline)
 52→                Spacer()
 53→                Button("Cancel") {
 54→                    // No-op: invisible spacer for symmetric layout
 55→                }.opacity(0)
 56→            }
 57→            .padding(.horizontal)
 58→            .padding(.vertical, 12)
 59→            Divider()
 60→
 61→            ScrollView {
 62→                VStack(spacing: 24) {
 63→                    if let status = iCloudStatus, status != .available && status != .temporarilyUnavailable {
 64→                        iCloudUnavailableView(status)
 65→                    } else {
 66→                        switch mode {
 67→                        case .input:
 68→                            inputView
 69→                        case .patternSetup:
 70→                            patternSetupView
 71→                        case .error(let message):
 72→                            errorView(message)
 73→                        }
 74→                    }
 75→                }
 76→                .padding()
 77→            }
 78→        }
 79→        .frame(maxWidth: .infinity, maxHeight: .infinity, alignment: .top)
 80→        .background(Color.vaultBackground.ignoresSafeArea())
 81→        .ignoresSafeArea(.keyboard)
 82→        .task {
 83→            let status = await CloudKitSharingManager.shared.checkiCloudStatus()
 84→            iCloudStatus = status
 85→        }
 86→        .onAppear {
 87→            checkForPendingImport()
 88→        }
 89→        .task(id: mode) {
 90→            #if DEBUG
 91→            if mode == .patternSetup,
 92→               appState.isMaestroTestMode,
 93→               ProcessInfo.processInfo.arguments.contains("-MAESTRO_FORCE_JOIN_OVERWRITE_ALERT"),
 94→               !showingOverwriteConfirmation {
 95→                existingVaultNameForOverwrite = "Vault TEST"
 96→                existingFileCountForOverwrite = 2
 97→                let seed = Data("MAESTRO_JOIN_OVERWRITE_TEST_KEY".utf8)
 98→                pendingOverwriteKey = VaultKey(Data(SHA256.hash(data: seed)))
 99→                showingOverwriteConfirmation = true
100→            }
101→            #endif
102→        }
103→        .alert("Replace Existing Vault?", isPresented: $showingOverwriteConfirmation) {
104→            Button("Cancel", role: .cancel) {
105→                pendingOverwriteKey = nil
106→            }
107→            Button("Replace Vault", role: .destructive) {
108→                guard let key = pendingOverwriteKey else { return }
109→                pendingOverwriteKey = nil
110→                Task { await setupSharedVault(forceOverwrite: true, precomputedPatternKey: key) }
111→            }
112→        } message: {
113→            Text(
114→                "\(existingVaultNameForOverwrite) already exists with \(existingFileCountForOverwrite) file\(existingFileCountForOverwrite == 1 ? "" : "s"). "
115→                + "Joining this shared vault will replace it. Files are not merged."
116→            )
117→        }
118→        .premiumPaywall(isPresented: $showingPaywall)
119→    }
120→
121→    // MARK: - Views
122→
123→    private var inputView: some View {
124→        VStack(spacing: 24) {
125→            // Show pending import banner if there's an interrupted download
126→            if let pending = pendingImportState {
127→                PendingSharedVaultImportBanner(
128→                    importedCount: pending.importedFileIds.count,
129→                    totalCount: pending.totalFiles,
130→                    onResume: resumePendingImport,
131→                    isResuming: $isResumingImport
