169→            .accessibilityIdentifier("join_vault_button")
170→            .vaultProminentButtonStyle()
171→            .disabled(phrase.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty)
172→
173→            // Info
174→            VStack(alignment: .leading, spacing: 8) {
175→                Label("How it works", systemImage: "info.circle")
176→                    .font(.headline)
177→                Text("The share phrase downloads and decrypts the shared vault. Each phrase can only be used once.")
178→                    .font(.subheadline).foregroundStyle(.vaultSecondaryText)
179→            }
180→            .padding()
181→            .frame(maxWidth: .infinity, alignment: .leading)
182→            .vaultGlassBackground(cornerRadius: 12)
183→        }
184→    }
185→
186→    private var patternSetupView: some View {
187→        VStack(spacing: 24) {
188→            VStack(spacing: 8) {
189→                Image(systemName: "lock.shield.fill")
190→                    .font(.system(size: 40))
191→                    .foregroundStyle(.tint)
192→
193→                Text(patternStep == .create ? "Set a Pattern" : "Confirm Pattern")
194→                    .font(.title2).fontWeight(.semibold)
195→
196→                Text(patternStep == .create
197→                    ? "Connect at least 6 dots on the 5×5 grid with 2+ direction changes"
