276→    private func iCloudUnavailableView(_ status: CKAccountStatus) -> some View {
277→        VStack(spacing: 16) {
278→            Image(systemName: "icloud.slash")
279→                .font(.system(size: 48)).foregroundStyle(.vaultSecondaryText)
