288→    // MARK: - Actions
289→
290→    private func joinVault() {
291→        Task {
292→            let trimmed = phrase.trimmingCharacters(in: .whitespacesAndNewlines)
293→            guard !trimmed.isEmpty else { return }
294→
295→            // Check phrase availability before proceeding to pattern setup
296→            let result = await CloudKitSharingManager.shared.checkPhraseAvailability(phrase: trimmed)
297→            if case .failure(let error) = result {
298→                mode = .error(error.localizedDescription)
299→                return
300→            }
301→
302→            // Phrase is valid, proceed to pattern setup
303→            mode = .patternSetup
304→        }
305→    }
306→
307→    private func handlePatternComplete(_ pattern: [Int]) {
308→        switch patternStep {
309→        case .create:
310→            let result = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
311→            validationResult = result
312→
313→            if result.isValid {
314→                UINotificationFeedbackGenerator().notificationOccurred(.success)
315→                errorMessage = nil
316→                newPattern = pattern
317→                patternStep = .confirm
318→                patternState.reset()
319→            } else {
320→                patternState.reset()
321→            }
322→
323→        case .confirm:
324→            if pattern == newPattern {
325→                guard !isSettingUpVault else { return }
326→                isSettingUpVault = true
327→                UINotificationFeedbackGenerator().notificationOccurred(.success)
328→                errorMessage = nil
329→                patternState.reset()
330→                Task { await setupSharedVault() }
331→            } else {
332→                UINotificationFeedbackGenerator().notificationOccurred(.error)
333→                errorMessage = "Patterns don't match. Try again."
334→                patternState.reset()
335→                Task {
336→                    try? await Task.sleep(nanoseconds: 2_500_000_000)
337→                    await MainActor.run { errorMessage = nil }
338→                }
339→            }
340→        }
341→    }
342→
343→    private func setupSharedVault(forceOverwrite: Bool = false, precomputedPatternKey: VaultKey? = nil) async {
344→        let trimmedPhrase = phrase.trimmingCharacters(in: .whitespacesAndNewlines)
345→
346→        do {
347→            let patternKey = try await resolvePatternKey(precomputedPatternKey: precomputedPatternKey)
348→
349→            if VaultStorage.shared.vaultHasFiles(for: patternKey), !forceOverwrite {
350→                isSettingUpVault = false  // Reset so overwrite confirmation can proceed
351→                prepareOverwriteConfirmation(for: patternKey)
352→                return
353→            }
354→
355→            if forceOverwrite {
356→                try await overwriteExistingVaultIfNeeded(patternKey: patternKey)
357→            }
358→
359→            // Set app state so user can navigate the app
360→            appState.currentVaultKey = patternKey
361→            appState.currentPattern = newPattern
362→            let letters = GridLetterManager.shared.vaultName(for: newPattern)
363→            appState.updateVaultName(letters.isEmpty ? "Vault" : "Vault \(letters)")
364→
365→            // Create an empty vault index so the vault can open immediately.
366→            // loadIndex auto-creates a proper v3 index with master key + blob when none exists.
367→            let emptyIndex = try VaultStorage.shared.loadIndex(with: patternKey)
368→            try VaultStorage.shared.saveIndex(emptyIndex, with: patternKey)
369→
370→            // Navigate to the vault immediately
371→            appState.isUnlocked = true
372→            dismiss()
373→
374→            // Download and import in background
375→            ShareImportManager.shared.startBackgroundDownloadAndImport(
376→                phrase: trimmedPhrase,
377→                patternKey: patternKey
378→            )
379→        } catch {
380→            mode = .error("Failed to set up vault: \(error.localizedDescription)")
381→        }
382→    }
383→
384→    private func resolvePatternKey(precomputedPatternKey: VaultKey?) async throws -> VaultKey {
385→        if let precomputedPatternKey {
386→            return precomputedPatternKey
387→        }
388→        let keyData = try await KeyDerivation.deriveKey(from: newPattern, gridSize: 5)
389→        return VaultKey(keyData)
390→    }
391→
392→    private func prepareOverwriteConfirmation(for patternKey: VaultKey) {
393→        let letters = GridLetterManager.shared.vaultName(for: newPattern)
394→        existingVaultNameForOverwrite = letters.isEmpty ? "Vault" : "Vault \(letters)"
395→
396→        if let index = try? VaultStorage.shared.loadIndex(with: patternKey) {
397→            existingFileCountForOverwrite = index.files.filter { !$0.isDeleted }.count
398→        } else {
399→            existingFileCountForOverwrite = 0
400→        }
401→
402→        pendingOverwriteKey = patternKey
403→        showingOverwriteConfirmation = true
404→    }
405→
406→    private func overwriteExistingVaultIfNeeded(patternKey: VaultKey) async throws {
407→        if VaultStorage.shared.vaultExists(for: patternKey) {
408→            if await DuressHandler.shared.isDuressKey(patternKey.rawBytes) {
409→                await DuressHandler.shared.clearDuressVault()
410→            }
411→            try VaultStorage.shared.deleteVaultIndex(for: patternKey)
412→            try? await RecoveryPhraseManager.shared.deleteRecoveryData(for: patternKey.rawBytes)
413→        }
414→    }
415→
416→    // MARK: - Helpers
417→
418→    private func iCloudStatusMessage(_ status: CKAccountStatus) -> String {
419→        switch status {
420→        case .available: return "iCloud is available"
421→        case .noAccount: return "Please sign in to iCloud in Settings"
422→        case .restricted: return "iCloud access is restricted"
423→        case .couldNotDetermine: return "Could not determine iCloud status"
424→        case .temporarilyUnavailable: return "iCloud is temporarily unavailable"
425→        @unknown default: return "iCloud is not available"
426→        }
427→    }
