429→    // MARK: - Pending Import Handling
430→    
431→    private func checkForPendingImport() {
432→        if let pending = ShareImportManager.shared.getPendingImportState() {
433→            pendingImportState = pending
434→        }
435→    }
436→    
437→    private func resumePendingImport() {
438→        guard let pending = pendingImportState else { return }
439→        
440→        isResumingImport = true
441→        
442→        // Need the vault key to resume - use the current key if available
443→        if let key = appState.currentVaultKey {
444→            ShareImportManager.shared.startBackgroundDownloadAndImport(
445→                phrase: pending.phrase,
446→                patternKey: key
447→            )
448→            // Dismiss after starting
449→            DispatchQueue.main.asyncAfter(deadline: .now() + 0.5) {
450→                dismiss()
451→            }
452→        } else {
453→            // No key available, can't resume
454→            isResumingImport = false
455→            errorMessage = "Cannot resume download - vault is locked. Please unlock your vault first."
456→        }
457→    }
458→}
