 97→    func downloadUpdate() async {
 98→        guard let key = appState.currentVaultKey,
 99→              let vaultId = viewModel.sharedVaultId else { return }
100→
101→        viewModel.isUpdating = true
102→        viewModel.sharedVaultUpdateProgress = (completed: 0, total: 100, message: "Downloading update...")
103→        IdleTimerManager.shared.disable()
104→
105→        var bgTaskCleaned = false
106→        var bgTaskId: UIBackgroundTaskIdentifier = .invalid
107→        bgTaskId = UIApplication.shared.beginBackgroundTask(withName: "SharedVaultUpdate") {
108→            // Expiration handler — clean up progress state so the overlay disappears
109→            bgTaskCleaned = true
110→            Task { @MainActor [weak viewModel] in
111→                viewModel?.sharedVaultUpdateProgress = nil
112→                viewModel?.isUpdating = false
113→                IdleTimerManager.shared.enable()
114→            }
115→            UIApplication.shared.endBackgroundTask(bgTaskId)
116→            bgTaskId = .invalid
117→        }
118→
119→        defer {
120→            viewModel.isUpdating = false
121→            viewModel.sharedVaultUpdateProgress = nil
122→            if !bgTaskCleaned {
123→                // Only re-enable here if the expiration handler hasn't already;
124→                // otherwise we'd double-decrement the ref-counted idle timer.
125→                IdleTimerManager.shared.enable()
126→                if bgTaskId != .invalid {
127→                    UIApplication.shared.endBackgroundTask(bgTaskId)
128→                }
129→            }
130→        }
131→
132→        do {
133→            let index = try VaultStorage.shared.loadIndex(with: key)
134→
135→            // Use the stored phrase-derived share key
136→            guard let shareKeyData = index.shareKeyData else {
137→                #if DEBUG
138→                print("❌ [VaultView] No share key stored in vault index")
139→                #endif
140→                return
141→            }
142→
143→            let shareKey = ShareKey(shareKeyData)
144→            let data = try await CloudKitSharingManager.shared.downloadUpdatedVault(
145→                shareVaultId: vaultId,
146→                shareKey: shareKey,
147→                onProgress: { [weak viewModel] completed, total in
148→                    Task { @MainActor in
149→                        // Map download progress to 0→70% range
150→                        let pct = total > 0 ? Int(Double(completed) / Double(total) * 70) : 0
151→                        viewModel?.sharedVaultUpdateProgress = (completed: pct, total: 100, message: "Downloading update...")
152→                    }
153→                }
154→            )
155→
156→            if SVDFSerializer.isSVDF(data) {
157→                try await importSVDFDelta(data: data, shareKey: shareKeyData, vaultKey: key, index: index)
158→            } else {
159→                try await importLegacyFull(data: data, shareKey: shareKeyData, vaultKey: key, index: index)
160→            }
161→
162→            // Store the new version to avoid false "new files available"
163→            if let newVersion = try? await CloudKitSharingManager.shared.checkForUpdates(
164→                shareVaultId: vaultId, currentVersion: 0
165→            ) {
166→                var updatedIndex = try VaultStorage.shared.loadIndex(with: key)
167→                updatedIndex.sharedVaultVersion = newVersion
168→                try VaultStorage.shared.saveIndex(updatedIndex, with: key)
169→            }
170→
171→            viewModel.updateAvailable = false
172→            viewModel.loadFiles()
173→        } catch {
174→            viewModel.toastMessage = .error("Update failed: \(error.localizedDescription)")
175→            #if DEBUG
176→            print("❌ [VaultView] Failed to download update: \(error)")
177→            #endif
178→        }
179→    }
