181→    /// SVDF v4/v5 delta import: parse manifest, diff file IDs vs local, import only new files.
182→    func importSVDFDelta(data: Data, shareKey: Data, vaultKey: VaultKey, index: VaultStorage.VaultIndex) async throws {
183→        let vaultKeyFingerprint = vaultKey.rawBytes.hashValue
184→        let header = try SVDFSerializer.parseHeader(from: data)
185→        let manifest = try SVDFSerializer.parseManifest(from: data, shareKey: shareKey)
186→        let remoteFileIds = Set(manifest.filter { !$0.deleted }.map { $0.id })
187→        let localFileIds = Set(index.files.filter { !$0.isDeleted }.map { $0.fileId.uuidString })
188→        
189→        #if DEBUG
190→        print("🔐 [importSVDFDelta] Vault key hash: \(vaultKeyFingerprint), Local files: \(localFileIds.count), Remote files: \(remoteFileIds.count)")
191→        #endif
192→
193→        // Delete files that were removed remotely
194→        let removedIds = localFileIds.subtracting(remoteFileIds)
195→        if !removedIds.isEmpty {
196→            // Safety check: don't delete if it would remove ALL local files
197→            // This indicates a problem with the remote manifest
198→            if removedIds.count == localFileIds.count && !remoteFileIds.isEmpty {
199→                #if DEBUG
200→                print("⚠️ [importSVDFDelta] SAFETY: Skipping deletion of all files - manifest mismatch detected")
201→                #endif
202→            } else {
203→                #if DEBUG
204→                print("🗑️ [importSVDFDelta] Deleting \(removedIds.count) files: \(removedIds)")
205→                #endif
206→                for removedId in removedIds {
207→                    if let uuid = UUID(uuidString: removedId) {
208→                        try? VaultStorage.shared.deleteFile(id: uuid, with: vaultKey)
209→                    }
210→                }
211→            }
212→        }
213→
214→        // Import only new files
215→        let newIds = remoteFileIds.subtracting(localFileIds)
216→        let newEntries = manifest.filter { newIds.contains($0.id) && !$0.deleted }
217→        #if DEBUG
218→        print("📥 [importSVDFDelta] Importing \(newEntries.count) new files")
219→        #endif
220→        for (idx, entry) in newEntries.enumerated() {
221→            let file = try SVDFSerializer.extractFileEntry(from: data, at: entry.offset, size: entry.size, version: header.version)
222→            let decrypted = try CryptoEngine.decrypt(file.encryptedContent, with: shareKey)
223→
224→            // Decrypt thumbnail from shared file if available
225→            var thumbnailData: Data? = nil
226→            if let encryptedThumb = file.encryptedThumbnail {
227→                thumbnailData = try? CryptoEngine.decrypt(encryptedThumb, with: shareKey)
228→            }
229→            // Generate thumbnail for images/videos if not provided
230→            if thumbnailData == nil {
231→                if file.mimeType.hasPrefix("image/") {
232→                    thumbnailData = FileUtilities.generateThumbnail(from: decrypted)
233→                } else if file.mimeType.hasPrefix("video/") {
234→                    thumbnailData = await generateVideoThumbnail(from: decrypted)
235→                }
236→            }
237→
238→            _ = try VaultStorage.shared.storeFile(
239→                data: decrypted,
240→                filename: file.filename,
241→                mimeType: file.mimeType,
242→                with: vaultKey,
243→                thumbnailData: thumbnailData,
244→                duration: file.duration,
245→                fileId: file.id  // <- Preserve original file ID from shared vault
246→            )
247→
248→            // Map import progress to 70→100% range
249→            let pct = newEntries.count > 0 ? 70 + Int(Double(idx + 1) / Double(newEntries.count) * 30) : 100
250→            viewModel.sharedVaultUpdateProgress = (completed: pct, total: 100, message: "Importing \(idx + 1) of \(newEntries.count)...")
251→        }
252→
253→        #if DEBUG
254→        print("📦 [VaultView] SVDF delta: \(newIds.count) new, \(removedIds.count) removed, \(localFileIds.intersection(remoteFileIds).count) unchanged")
255→        #endif
256→    }
257→    
258→    /// Generates a thumbnail from video data.
259→    private func generateVideoThumbnail(from data: Data) async -> Data? {
260→        let tempURL = FileManager.default.temporaryDirectory
261→            .appendingPathComponent(UUID().uuidString)
262→            .appendingPathExtension("mp4")
263→        
264→        do {
265→            try data.write(to: tempURL, options: [.atomic])
266→            defer { try? FileManager.default.removeItem(at: tempURL) }
267→            
268→            let asset = AVAsset(url: tempURL)
269→            let generator = AVAssetImageGenerator(asset: asset)
270→            generator.appliesPreferredTrackTransform = true
271→            generator.maximumSize = CGSize(width: 400, height: 400)
272→            
273→            let time = CMTime(seconds: 0.5, preferredTimescale: 600)
274→            if let cgImage = try? generator.copyCGImage(at: time, actualTime: nil) {
275→                let uiImage = UIImage(cgImage: cgImage)
276→                return uiImage.jpegData(compressionQuality: 0.7)
277→            }
278→        } catch {
279→            // Silently fail - thumbnail generation is best-effort
280→        }
281→        
282→        return nil
283→    }
