285→    /// Legacy v1-v3 full wipe-and-replace import.
286→    func importLegacyFull(data: Data, shareKey: Data, vaultKey: VaultKey, index: VaultStorage.VaultIndex) async throws {
287→        let sharedVault = try SharedVaultData.decode(from: data)
288→
289→        // Delete all existing files
290→        for existingFile in index.files where !existingFile.isDeleted {
291→            try? VaultStorage.shared.deleteFile(id: existingFile.fileId, with: vaultKey)
292→        }
293→
294→        // Import all files
295→        let totalFiles = sharedVault.files.count
296→        for (idx, file) in sharedVault.files.enumerated() {
297→            let decrypted = try CryptoEngine.decrypt(file.encryptedContent, with: shareKey)
298→
299→            // Decrypt thumbnail from shared file if available
300→            var thumbnailData: Data? = nil
301→            if let encryptedThumb = file.encryptedThumbnail {
302→                thumbnailData = try? CryptoEngine.decrypt(encryptedThumb, with: shareKey)
303→            }
304→            // Generate thumbnail for images/videos if not provided
305→            if thumbnailData == nil {
306→                if file.mimeType.hasPrefix("image/") {
307→                    thumbnailData = FileUtilities.generateThumbnail(from: decrypted)
308→                } else if file.mimeType.hasPrefix("video/") {
309→                    thumbnailData = await generateVideoThumbnail(from: decrypted)
310→                }
311→            }
312→
313→            _ = try VaultStorage.shared.storeFile(
314→                data: decrypted,
315→                filename: file.filename,
316→                mimeType: file.mimeType,
317→                with: vaultKey,
318→                thumbnailData: thumbnailData,
319→                duration: file.duration,
320→                fileId: file.id  // <- Preserve original file ID from shared vault
321→            )
322→
323→            // Map import progress to 70→100% range
324→            let pct = totalFiles > 0 ? 70 + Int(Double(idx + 1) / Double(totalFiles) * 30) : 100
325→            viewModel.sharedVaultUpdateProgress = (completed: pct, total: 100, message: "Importing \(idx + 1) of \(totalFiles)...")
326→        }
327→    }
328→}
