15→    /// Load a video from PHPicker to a temp URL without loading entire file into memory.
16→    /// The caller is responsible for cleaning up the returned URL.
17→    static func loadVideoURL(from provider: NSItemProvider) async throws -> URL {
18→        try await withCheckedThrowingContinuation { continuation in
19→            provider.loadFileRepresentation(forTypeIdentifier: UTType.movie.identifier) { url, error in
20→                if let error {
21→                    continuation.resume(throwing: error)
22→                    return
23→                }
24→                guard let url else {
25→                    continuation.resume(throwing: CocoaError(.fileReadUnknown))
26→                    return
27→                }
28→                // Copy to our temp directory — provider URL is ephemeral
29→                let destURL = FileManager.default.temporaryDirectory
30→                    .appendingPathComponent(UUID().uuidString)
31→                    .appendingPathExtension(url.pathExtension.isEmpty ? "mov" : url.pathExtension)
32→                do {
33→                    try FileManager.default.copyItem(at: url, to: destURL)
34→                    continuation.resume(returning: destURL)
35→                } catch {
36→                    continuation.resume(throwing: error)
37→                }
38→            }
39→        }
40→    }
41→
42→    /// Generate a thumbnail and capture duration from a video URL.
43→    static func generateVideoMetadata(from url: URL) async -> (thumbnail: Data?, duration: TimeInterval?) {
44→        let asset = AVAsset(url: url)
45→        let generator = AVAssetImageGenerator(asset: asset)
46→        generator.appliesPreferredTrackTransform = true
47→        generator.maximumSize = CGSize(width: 400, height: 400)
48→
49→        var thumbnail: Data?
50→        var duration: TimeInterval?
51→
52→        // Capture duration
53→        if let cmDuration = try? await asset.load(.duration) {
54→            let seconds = CMTimeGetSeconds(cmDuration)
55→            if seconds.isFinite && seconds > 0 {
56→                duration = seconds
57→            }
58→        }
59→
60→        // Generate thumbnail frame
61→        let time = CMTime(seconds: 0.5, preferredTimescale: 600)
62→        if let cgImage = try? generator.copyCGImage(at: time, actualTime: nil) {
63→            let uiImage = UIImage(cgImage: cgImage)
64→            thumbnail = uiImage.jpegData(compressionQuality: 0.7)
65→        }
66→
67→        return (thumbnail, duration)
68→    }
69→}
